    "contracts/utils/CountSplitter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/utils/Strings.sol";

library CountSplitter {
    using Strings for uint256;

    function split(uint256 amountIn, uint256 count) internal pure returns(string memory temp) {
        temp = (amountIn%10).toString();
        amountIn /= 10;
        uint256 initCount = 1;
        while(amountIn > 0) {
            if(initCount == count){
                temp = string.concat(",", temp);
                delete initCount;
            }
            temp = string.concat((amountIn%10).toString(), temp);
            amountIn /= 10;
            initCount++;
        }
    }
}"
