import "@chainlink/contracts/src/v0.8/interfaces/AggregatorInterface.sol";

abstract contract PriceFeed {
    AggregatorInterface immutable AGGREGATOR_BNB_USD;

    constructor(
        address aggregator
    ) {
        AGGREGATOR_BNB_USD = AggregatorInterface(aggregator);
    }

    function BNB_USD() public view returns(uint256) {
        return uint256(AGGREGATOR_BNB_USD.latestAnswer());
    }
