    "contracts/landStaking/ILandStaking.sol": {
      "content": "//SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "../oracles/prices/interfaces/IGQOracle.sol";

/// @title The interface for the LandStaking contract.
/// @notice This interface facilitates the LandStaking functionalities.
interface ILandStaking {

    // ============== STRUCTS ==============

    /// @notice Struct to store the Lands staking information.
    struct StakedLand {
        uint256 tokenId;
        uint256 endTime;
        bool staked;
    }

    /// @notice Struct to store the Pool rewards information.
    struct PoolReward {
        address resource;
        uint256 amount;
    }

    // ============== ERRORS ==============

    /// @notice Error when the contract is not initialized.
    error ContractNotInitialized();

    /// @notice Error when trying to set the land contract address to the address 0.
    error LandZeroAddress();

    /// @notice Error when trying to set the staking token contract address to the address 0.
    error StakingTokenZeroAddress();

    /// @notice Error when trying to set the fee receiver address to the address 0.
    error FeeReceiverZeroAddress();

    /// @notice Error when trying to set the GQ oracle address to the address 0.
    error OracleZeroAddress();

    /// @notice Error when trying to stake/unstake a Land without being the owner.
    error NotTheOwnerOfTheLand();

    /// @notice Error when trying to stake a Land when there is another of the same size already staked.
    error LandSizeAlreadyStaked(address sender, uint256 tokenId);

    /// @notice Error when trying to set a Land block duration/unstake a Land without having anyone of the specific size staked.
    error LandSizeNotStaked(string size);

    /// @notice Error when trying to unstake a Land when the block duration hasn't ended.
    error BlockDurationNotEnded(string size);

    /// @notice Error when trying to claim rewards from a pool that hasn't got the pool rewards defined.
    error PoolRewardsNotDefined(string landType);

    // ============== EVENTS ==============

    /// @notice Event emitted when the address of the land contract is changed.
    /// @param sender The address of the user that changes the address.
    /// @param newLandAddress The new address of the land contract.
    event LandAddressChanged (
        address indexed sender,
        address newLandAddress
    );

    /// @notice Event emitted when the staking token contract is changed.
    /// @param sender The address of the user that changes the address.
    /// @param newStakingTokenAddress The new address of the staking token contract.
    event StakingTokenChanged (
        address indexed sender,
        address newStakingTokenAddress
    );

    /// @notice Event emitted when the fee receiver address is changed.
    /// @param sender The address of the user that changes the address.
    /// @param newFeeReceiverAddress The new fee receiver address.
    event FeeReceiverChanged (
        address indexed sender,
        address newFeeReceiverAddress
    );

    /// @notice Event emitted when the GQ oracle address is changed.
    /// @param sender The address of the user that changes the address.
    /// @param newOracleAddress The new GQ oracle address.
    event OracleChanged (
        address indexed sender,
        address newOracleAddress
    );

    /// @notice Event emitted when a new land type is added.
    /// @param sender The address of the user that adds the new land type.
    /// @param landType The new land type.
    event LandTypeAdded (
        address indexed sender,
        string landType
    );

    /// @notice Event emitted when the staking fee of a land size pool is changed.
    /// @param sender The address of the user that changes the staking fee.
    /// @param size The size of the Land.    
    /// @param newStakingFee The new staking fee.
    event PoolStakingFeeChanged (
        address indexed sender,
        string indexed size,
        uint256 newStakingFee
    );

    /// @notice Event emitted when the crafting cost reduction of a land size pool is changed.
    /// @param sender The address of the user that changes the cost reduction.
    /// @param size The size of the Land.
    /// @param costReduction The crafting cost reduction.
    event PoolCraftingCostReductionChanged (
        address indexed sender,
        string indexed size,
        uint256 costReduction
    );

    /// @notice Event emitted when the crafting time reduction of a land size pool is changed.
    /// @param sender The address of the user that changes the time reduction.
    /// @param size The size of the Land.
    /// @param timeReduction The crafting time reduction.
    event PoolCraftingTimeReductionChanged (
        address indexed sender,
        string indexed size,
        uint256 timeReduction
    );

    /// @notice Event emitted when the rewards of a land size pool are changed.
    /// @param sender The address of the user that changes the rewards.
    /// @param size The size of the Land.
    event PoolRewardsChanged (
        address indexed sender,
        string indexed size
    );

    /// @notice Event emitted when a Land is staked.
    /// @param sender The address of the user that staked the Land.
    /// @param tokenId The token id of the Land.
    event LandStaked (
        address indexed sender,
        uint256 indexed tokenId
    );

    /// @notice Event emitted when a Land block duration is changed.
    /// @param sender The address of the user that changes the block duration of the Land.
    /// @param sender The address of the owner of the Land.
    /// @param size The size of the Land.
    /// @param blockDuration The block duration of the Land.
    event LandBlockDurationChanged (
        address indexed sender,
        address indexed user,
        string indexed size,
        uint256 blockDuration
    );

    /// @notice Event emitted when a Land is unstaked.
    /// @param sender The address of the user that unstaked the Land.
    /// @param tokenId The token id of the Land.
    event LandUnstaked (
        address indexed sender,
        uint256 indexed tokenId
    );

    /// @notice Event emitted when a user claims rewards from a pool.
    /// @param sender The address of the user that claims rewards.
    /// @param size The size of the land.
    event RewardsClaimed (
        address indexed sender,
        string indexed size
    );


    // ============== FUNCTIONS ==============

    /// @notice Pauses all main functionalities.
    /// @dev Only callable by an account with the PAUSER_ROLE.
    function pause() external;

    /// @notice Unpauses all main functionalities.
    /// @dev Only callable by an account with the PAUSER_ROLE.
    function unpause() external;

    /// @notice Function that changes the address of the land contract.
    /// @dev Only callable by an address with the MANAGER_ROLE.        
    /// @param _landAddress The new land contract address.
    function changeLandAddress(
        address _landAddress
    ) external;

    /// @notice Function that changes the staking token contract.
    /// @dev Only callable by an address with the MANAGER_ROLE.        
    /// @param _stakingTokenAddress The new staking token contract address.
    function changeStakingToken(
        address _stakingTokenAddress
    ) external;

    /// @notice Function that changes the fee receiver address.
    /// @dev Only callable by an address with the MANAGER_ROLE.        
    /// @param _feeReceiver The fee receiver address.
    function changeFeeReceiver(
        address _feeReceiver
    ) external;

    /// @notice Function that changes the GQ oracle address.
    /// @dev Only callable by an address with the MANAGER_ROLE.        
    /// @param _oracle The GQ oracle address.
    function changeOracle(
        IGQOracle _oracle
    ) external;

    /// @notice Function that changes the staking fee of a land size pool.
    /// @dev Only callable by an address with the MANAGER_ROLE.        
    /// @param size The size of the land.
    /// @param stakingFee The staking fee.
    function changePoolStakingFee(
        string calldata size,
        uint256 stakingFee
    ) external;

    /// @notice Function that returns a land size pool staking fee in GQ. 
    /// @param size The size of the land.
    function getPoolStakingFeeInGQ(
        string calldata size
    ) external view returns (uint256);

    /// @notice Function that changes the crafting reduction cost of a land size pool.
    /// @dev Only callable by an address with the MANAGER_ROLE.        
    /// @param size The size of the land.
    /// @param costReduction The new cost reduction.
    function changePoolCraftingCostReduction(
        string calldata size,
        uint256 costReduction
    ) external;

    /// @notice Function that returns a user's crafting cost reduction (if has a land staked).    
    /// @param user The user.
    function getUserCraftingCostReduction(
        address user
    ) external view returns(uint256);

    /// @notice Function that changes the crafting reduction time of a land size pool.
    /// @dev Only callable by an address with the MANAGER_ROLE.        
    /// @param size The size of the land.
    /// @param timeReduction The time reduction.
    function changePoolCraftingTimeReduction(
        string calldata size,
        uint256 timeReduction
    ) external;

    /// @notice Function that returns a user's crafting time reduction (if has a land staked).    
    /// @param user The user.
    function getUserCraftingTimeReduction(
        address user
    ) external view returns(uint256);

    /// @notice Function that changes the rewards of a land size pool.
    /// @dev Only callable by an address with the MANAGER_ROLE.            
    /// @param size The size of the land.
    /// @param _poolRewards The new pool rewards.
    function changePoolRewards(
        string calldata size,
        PoolReward[] calldata _poolRewards
    ) external;

    /// @notice Function that returns the rewards of a land size pool.      
    /// @param size The size of the land.
    /// @return Array of rewards of the land size pool.
    function getPoolRewards(
        string calldata size 
    ) external view returns (PoolReward[] memory); 

    /// @notice Function that adds a new land type.
    /// @dev Only callable by an address with the MANAGER_ROLE.        
    /// @param landType The new land type.
    function addLandType(
        string calldata landType
    ) external;

    /// @notice Function that stakes a land.
    /// @dev Only callable when the contract is not paused.      
    /// @param tokenId The land token id.
    function stakeLand(
        uint256 tokenId
    ) external payable;

    /// @notice Function that returns a user's staked land info.    
    /// @param user The user.
    /// @return size The size of the top land of the user.
    function getUserStaking(
        address user, 
        string calldata size
    ) external view returns (StakedLand memory);

    /// @notice Function that returns a user's top land's size.    
    /// @param user The user.
    /// @return size The size of the top land of the user.
    function getUserTopLandSize(
        address user
    ) external view returns(string memory size);

    /// @notice Function that sets a block duration for the top size Land of a user.
    /// @dev Only callable by an address with the BLOCKER_ROLE.     
    /// @param user The user.
    /// @param blockDuration The land block duration.
    function setTopLandBlockDuration(
        address user,
        uint256 blockDuration
     ) external;

    /// @notice Function that sets a block duration of a Land.
    /// @dev Only callable by an address with the BLOCKER_ROLE.     
    /// @param user The user.
    /// @param size The size of the land.
    /// @param blockDuration The land block duration.
    function setLandBlockDuration(
        address user,
        string calldata size,
        uint256 blockDuration
    ) external;

    /// @notice Function that unstakes a land.
    /// @dev Only callable when the contract is not paused.      
    /// @param size The size of the land.
    function unstakeLand(
        string calldata size
    ) external;

    /// @notice Function that claims the pending rewards from a pool for a user.
    /// @dev Only callable when the contract is not paused.      
    /// @param size The size of the land.
    function claimRewards(
        string calldata size
    ) external;

    /// @notice Function that returns the user's pending rewards for a specific pool.    
    /// @param user The user.
    /// @param size The size assigned to the pool.
    /// @return pendingRewards Array of pending PoolRewards to claim.
    function getUserPendingRewards(
        address user,
        string calldata size
    ) external view returns(PoolReward[] memory pendingRewards);
}"
    },
    "contracts/landStaking/LandStaking.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlEnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721ReceiverUpgradeable.sol";

import "./ILandStaking.sol";
import "../tokens/Land.sol";
import "../tokens/Resource.sol";

/// @title LandStaking 
/// @author Urriza
/// @notice This contract will be used to manage the staking of the Lands.
/// @dev Inherits from Initializable, AccessControlEnumerableUpgradeable and PausableUpgradeable for upgradeability,
/// access control and pausable features. It also implements the ILandStaking interface.
contract LandStaking is 
    Initializable,
    AccessControlEnumerableUpgradeable,
    PausableUpgradeable,
    IERC721ReceiverUpgradeable,
    ILandStaking
{
    using SafeERC20Upgradeable for IERC20Upgradeable;

    // ============== CONSTANTS ==============

    /// @notice Constant to store the multiplier applied to handle decimals.
    uint256 public constant MULTIPLIER = 10_000;

    /// @notice The role identifier for the manager who can change the configuration variables.
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    /// @notice The role identifier for the pauser.
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    /// @notice The role identifier for the accounts that are allowed to set the block duration of the lands.
    bytes32 public constant BLOCKER_ROLE = keccak256("BLOCKER_ROLE");

    // ============== VARIABLES ==============

    /// @notice Variable for knowing if contract has been initialized.
    bool public initialized;

    /// @notice Variable to store the land contract address.
    address public landAddress;

    /// @notice Variable to store the address of the token used for the staking fee.
    address public stakingToken;

    /// @notice Variable to store the fee receiver address.
    address public feeReceiver;

    /// @notice Variable to store the GQ oracle prices address.
    IGQOracle public oracle;

    /// @notice Array to store the land types.
    string[] public landTypes;

    /// @notice Mapping to store the user's land stakings.
    mapping(address => mapping(string => StakedLand)) public userStakings;

    /// @notice Mapping to store the staking fee per pool.
    mapping(string => uint256) public poolStakingFee;

    /// @notice Mapping to store the crafting cost reduction per pool.
    mapping(string => uint256) public poolCraftingCostReductions;

    /// @notice Mapping to store the crafting time reduction per pool.
    mapping(string => uint256) public poolCraftingTimeReductions;

    /// @notice Mapping to store the rewards per pool.
    mapping(string => PoolReward[]) public poolRewards;

    /// @notice Mapping to store the last block when the user's have claimed rewards per pool.
    mapping(address => mapping(string => uint256)) public userLastRewardsBlock;

    /// @notice Storage gap for possible new variables.
    uint256[39] __gapLandStaking;

    // ============== MODIFIERS ==============

    /// @notice Modifier that will return true only when contract is initialized.
    modifier isInitialized() {
        if (!initialized) revert ContractNotInitialized();
        _;
    }

    //============== CONSTRUCTOR ==============

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    // ============== INITIALIZE ==============

    function initialize(
        address _landAddress,
        address _stakingToken,
        address _feeReceiver,
        IGQOracle _oracle
    ) external initializer {
        if (_landAddress == address(0)) revert LandZeroAddress();
        landAddress = _landAddress;
        if (_stakingToken == address(0)) revert StakingTokenZeroAddress();
        stakingToken = _stakingToken;
        if (_feeReceiver == address(0)) revert FeeReceiverZeroAddress();
        feeReceiver = _feeReceiver;
        if (address(_oracle) == address(0)) revert OracleZeroAddress();
        oracle = _oracle;

        __AccessControlEnumerable_init();
        __Pausable_init();

        landTypes.push('Nano');
        landTypes.push('Micro');
        landTypes.push('Standard');
        landTypes.push('Macro');
        landTypes.push('Mega');

        initialized = true;

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    // ============== EXTERNAL FUNCTIONS ==============

    /// @inheritdoc IERC721ReceiverUpgradeable
    function onERC721Received(
        address,
        address,
        uint256,
        bytes calldata
    ) external pure returns (bytes4) {
        return IERC721ReceiverUpgradeable.onERC721Received.selector;
    }

    /// @inheritdoc ILandStaking
    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    /// @inheritdoc ILandStaking
    function unpause() external onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    /// @inheritdoc ILandStaking
    function changeLandAddress(
        address _landAddress
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (_landAddress == address(0)) revert LandZeroAddress();
        landAddress = _landAddress;

        emit LandAddressChanged(msg.sender, _landAddress);
    }

    /// @inheritdoc ILandStaking
    function changeStakingToken(
        address _stakingToken
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (_stakingToken == address(0)) revert StakingTokenZeroAddress();
        stakingToken = _stakingToken;

        emit StakingTokenChanged(msg.sender, _stakingToken);
    }

    /// @inheritdoc ILandStaking
    function changeFeeReceiver(
        address _feeReceiver
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (_feeReceiver == address(0)) revert FeeReceiverZeroAddress();
        feeReceiver = _feeReceiver;

        emit FeeReceiverChanged(msg.sender, _feeReceiver);
    }

    /// @inheritdoc ILandStaking
    function changeOracle(
        IGQOracle _oracle
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        if (address(_oracle) == address(0)) revert OracleZeroAddress();
        oracle = _oracle;

        emit OracleChanged(msg.sender, address(_oracle));
    }

    /// @inheritdoc ILandStaking
    function changePoolStakingFee(
        string calldata size,
        uint256 stakingFee
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        poolStakingFee[size] = stakingFee;

        emit PoolStakingFeeChanged(msg.sender, size, stakingFee);
    }

    /// @inheritdoc ILandStaking
    function getPoolStakingFeeInGQ(
        string calldata size
    ) external view returns (uint256) {
        return oracle.convertBUSDToGQ(poolStakingFee[size]);
    }

    /// @inheritdoc ILandStaking
    function changePoolCraftingCostReduction(
        string calldata size,
        uint256 costReduction
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        poolCraftingCostReductions[size] = costReduction;

        emit PoolCraftingCostReductionChanged(msg.sender, size, costReduction);
    }

    /// @inheritdoc ILandStaking
    function getUserCraftingCostReduction(
        address user
    ) external view returns(uint256) {
        string memory userTopLandSize = _getUserTopLandSize(user);

        return poolCraftingCostReductions[userTopLandSize];
    } 

    /// @inheritdoc ILandStaking
    function changePoolCraftingTimeReduction(
        string calldata size,
        uint256 timeReduction
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        poolCraftingTimeReductions[size] = timeReduction;

        emit PoolCraftingTimeReductionChanged(msg.sender, size, timeReduction);
    }

    /// @inheritdoc ILandStaking
    function getUserCraftingTimeReduction(
        address user
    ) external view returns(uint256) {
        string memory userTopLandSize = _getUserTopLandSize(user);

        return poolCraftingTimeReductions[userTopLandSize];
    } 

    /// @inheritdoc ILandStaking
    function changePoolRewards(
        string calldata size,
        PoolReward[] calldata _poolRewards
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        PoolReward[] storage poolRewardsStored = poolRewards[size];

        if(poolRewardsStored.length > 0){
            delete poolRewards[size];
        }

        uint256 numPoolRewards = _poolRewards.length;
        for (uint256 i; i < numPoolRewards;) {
            poolRewardsStored.push(_poolRewards[i]);
            unchecked {
                ++i;
            }
        }

        emit PoolRewardsChanged(msg.sender, size);
    }

    /// @inheritdoc ILandStaking
    function getPoolRewards(
        string calldata size 
    ) external view returns (PoolReward[] memory) {
        return poolRewards[size];
    }

    /// @inheritdoc ILandStaking
    function addLandType(
        string calldata landType
    ) external isInitialized onlyRole(MANAGER_ROLE) {
        landTypes.push(landType);

        emit LandTypeAdded(msg.sender, landType);
    }

    /// @inheritdoc ILandStaking
    function stakeLand(
        uint256 tokenId
    ) external payable isInitialized whenNotPaused {
        // Check if the caller is the owner of the Land
        if (Land(landAddress).ownerOf(tokenId) != msg.sender) revert NotTheOwnerOfTheLand();

        string memory size = Land(landAddress).getLandSize(tokenId);

        // Check if the caller has already a staked Land of the same size
        if (userStakings[msg.sender][size].staked) revert LandSizeAlreadyStaked(msg.sender, userStakings[msg.sender][size].tokenId);

        userStakings[msg.sender][size] = StakedLand({
            tokenId: tokenId,
            endTime: block.timestamp,
            staked: true
        });

        // Set the last rewards block of the user
        userLastRewardsBlock[msg.sender][size] = block.number;

        // Transfer the staking fee from the user to the fee receiver
        uint256 oracleStakingFee = oracle.convertBUSDToGQ(poolStakingFee[size]);

        IERC20Upgradeable(stakingToken).safeTransferFrom(
            msg.sender,
            feeReceiver,
            oracleStakingFee
        );

        Land(landAddress).safeTransferFrom(msg.sender, address(this), tokenId);
    }

    /// @inheritdoc ILandStaking
    function getUserStaking(
        address user,
        string calldata size
    ) external view returns(StakedLand memory) {
        return userStakings[user][size];
    }

    /// @inheritdoc ILandStaking
    function getUserTopLandSize(
        address user
    ) external view returns(string memory size) {
        for (uint256 i = landTypes.length - 1; i >= 0;) {
            if (userStakings[user][landTypes[i]].staked) {
                return landTypes[i];
            }

            unchecked {
                if (i == 0) break;
                --i;
            }
        }
    }

    /// @inheritdoc ILandStaking
    function setLandBlockDuration(
        address user,
        string calldata size,
        uint256 blockDuration
    ) external isInitialized onlyRole(BLOCKER_ROLE) {
        // Check if the user has a Land staked of that size
        if (!userStakings[user][size].staked) revert LandSizeNotStaked(size);

        _setLandBlockDuration(user, size, blockDuration);
    }

    /// @inheritdoc ILandStaking
    function setTopLandBlockDuration(
        address user,
        uint256 blockDuration
    ) external isInitialized onlyRole(BLOCKER_ROLE) {
        string memory size = _getUserTopLandSize(user);

        _setLandBlockDuration(user, size, blockDuration);
    }

    /// @inheritdoc ILandStaking
    function unstakeLand(
        string calldata size
    ) external isInitialized whenNotPaused {
        // Check if the user has a Land staked of that size
        if (!userStakings[msg.sender][size].staked) revert LandSizeNotStaked(size);

        // Check if the block duration has ended
        if (block.timestamp < userStakings[msg.sender][size].endTime) revert BlockDurationNotEnded(size);

        uint256 tokenId = userStakings[msg.sender][size].tokenId;

        // Update the userStakings
        delete userStakings[msg.sender][size];

        // Compute pending rewards
        _computePendingRewards(msg.sender, size);

        Land(landAddress).safeTransferFrom(address(this), msg.sender, tokenId);
    }

    /// @inheritdoc ILandStaking
    function claimRewards(
        string calldata size
    ) external isInitialized whenNotPaused {
        // Check if the land size has pool rewards defined
        uint256 poolRewardsNum = poolRewards[size].length;
        if (poolRewardsNum == 0) revert PoolRewardsNotDefined(size);

        // Check if the user has a Land staked of that size
        if (!userStakings[msg.sender][size].staked) revert LandSizeNotStaked(size);

        // Compute pending rewards
        _computePendingRewards(msg.sender, size);

        emit RewardsClaimed(msg.sender, size);
    }

    /// @inheritdoc ILandStaking
    function getUserPendingRewards(
        address user,
        string calldata size
    ) external view returns(PoolReward[] memory pendingRewards) {
        uint256 poolRewardsNum = poolRewards[size].length;
        pendingRewards = new PoolReward[](poolRewardsNum);

        uint256 numBlocks = block.number - userLastRewardsBlock[user][size];

        for (uint256 i; i < poolRewardsNum;) {
            pendingRewards[i] = PoolReward({
                resource: poolRewards[size][i].resource,
                amount: userStakings[user][size].staked ? poolRewards[size][i].amount * numBlocks : 0
            });

            unchecked {
                ++i;
            }
        }
    }

    /// @notice Function that returns a user's top land's size.
    function _getUserTopLandSize(
        address user
    ) internal view returns(string memory size) {
        for (uint256 i = landTypes.length - 1; i >= 0;) {
            if (userStakings[user][landTypes[i]].staked) {
                return landTypes[i];
            }

            unchecked {
                if (i == 0) break;
                --i;
            }
        }
    } 

    /// @notice Function that sets a block duration of a Land.
    function _setLandBlockDuration(
        address user,
        string memory size,
        uint256 blockDuration
    ) internal {
        userStakings[user][size].endTime = block.timestamp + blockDuration;

        emit LandBlockDurationChanged(msg.sender, user, size, block.timestamp + blockDuration);
    } 

    /// @notice Computes the pending rewards on a pool for a user.
    function _computePendingRewards( 
        address user,
        string calldata size
    ) internal {
        uint256 poolRewardsNum = poolRewards[size].length;
        uint256 numBlocks = block.number - userLastRewardsBlock[msg.sender][size];

        for (uint256 i; i < poolRewardsNum;) {
            Resource(poolRewards[size][i].resource).mint(user, poolRewards[size][i].amount * numBlocks);

            unchecked {
                ++i;
            }
        }

        // Update the last rewards block of the user
        userLastRewardsBlock[msg.sender][size] = block.number;
    }
}"
    },
    "contracts/oracles/prices/interfaces/IGQOracle.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

interface IGQOracle {

    /// @notice Gets the price of GQ per stable.
    /// @param amount Amount in stable to convert.
