    "contracts/tokens/Land.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721URIStorageUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";

/// @title Lands NFT Smart Contract
/// @author Ariceta

//============== LAND ==============

contract Land is
    Initializable,
    ERC721Upgradeable,
    ERC721URIStorageUpgradeable,
    ERC721BurnableUpgradeable,
    AccessControlUpgradeable
{
    using CountersUpgradeable for CountersUpgradeable.Counter;

    //============== STRUCTS ==============
    /// @notice Struct for storing the info for each Land
    struct LandInfo {
        string size;
        string ring;
        string sector;
        string district;
        string collectionName;
        uint8 collectionId;
    }

    //============== MAPPINGS ==============
    /// @notice lands tokenId => LandInfo
    mapping(uint256 => LandInfo) public landsInfoMap;

    /// @notice lands tokenId => firstOwner
    mapping(uint256 => address) public firstOwnerLandMap;

    /// @notice land size name => allowed or not
    mapping(string => bool) public allowedLandSizes;

    /// @notice land ring name => allowed or not
    mapping(string => bool) public allowedLandRings;

    /// @notice land sector name => allowed or not
    mapping(string => bool) public allowedLandSectors;

    /// @notice land district name => allowed or not
    mapping(string => bool) public allowedLandDistricts;

    //============== VARIABLES ==============
    bytes32 private constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 private constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    CountersUpgradeable.Counter private _tokenIdCounter;

    //============== EVENTS ==============
    /// @notice This event is emitted when minting a Land
    event LandMinted(
        uint256 indexed tokenId,
        string size,
        string ring,
        string sector,
        string district,
        string collectionName,
        uint8 collectionId
    );

    /// @notice This event is emitted when a Land is bought
    ///         for the first time in an Auction
    event FirstOwnerSet(uint256 indexed tokenId, address indexed firstOwner);

    event ChangedAllowedLandSizes(string indexed landSize, bool isAllowed);
    event ChangedAllowedLandRings(string indexed landRing, bool isAllowed);
    event ChangedAllowedLandSectors(string indexed landsECTOR, bool isAllowed);
    event ChangedAllowedLandDistricts(
        string indexed landDistrict,
        bool isAllowed
    );

    //============== CONSTRUCTOR ==============
    //constructor() {
    //    _disableInitializers();
    //}

    //============== INITIALIZE ==============
    function initialize() public initializer {
        __ERC721_init("Outer Ring Lands", "ORLNDS");
        __ERC721URIStorage_init();
        __ERC721Burnable_init();
        __AccessControl_init();
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);

        // TO-DELETE INCLUDED FOR LAND STAKING
        _grantRole(MINTER_ROLE, msg.sender);

        allowedLandSizes["Nano"] = true;
        allowedLandSizes["Micro"] = true;
        allowedLandSizes["Standard"] = true;
        allowedLandSizes["Macro"] = true;
        allowedLandSizes["Mega"] = true;

        allowedLandRings["Ring 0"] = true;
        allowedLandRings["Ring 1"] = true;
        allowedLandRings["Ring 2"] = true;
        allowedLandRings["Ring 3"] = true;

        allowedLandSectors["Alpha"] = true;
        allowedLandSectors["Beta"] = true;
        allowedLandSectors["Gamma"] = true;
        allowedLandSectors["Delta"] = true;

        allowedLandDistricts["District 1"] = true;
        allowedLandDistricts["District 2"] = true;
        allowedLandDistricts["District 3"] = true;
        allowedLandDistricts["District 4"] = true;
        allowedLandDistricts["District 5"] = true;
    }

    //============== EXTERNAL FUNCTIONS ==============
    /// @dev Mints a group of lands to the specified addresses with the provided parameters
    /// @param to array of addresses to which the new land should be minted
    /// @param tokenURI_ array of metadata URI for the new lands
    /// @param landInfo The info about the lands
    function batchMint(
        address[] calldata to,
        string[] calldata tokenURI_,
        LandInfo[] calldata landInfo
    ) external onlyRole(MINTER_ROLE) {
        uint256 lenght = to.length;
        require(
            lenght == tokenURI_.length &&
                lenght == landInfo.length &&
                tokenURI_.length == landInfo.length,
            "Incorrect sizes"
        );
        for (uint256 i; i < lenght; ) {
            safeMint(to[i], tokenURI_[i], landInfo[i]);
            unchecked {
                ++i;
            }
        }
    }

    /// @notice Set the first owner of the land once they are purchased in an auction
    /// @param tokenId Identifier of the land
    /// @param firstOwner The first buyer of the land
    function setFirstOwner(
        uint256 tokenId,
        address firstOwner
    ) external onlyRole(MANAGER_ROLE) {
        firstOwnerLandMap[tokenId] = firstOwner;
        emit FirstOwnerSet(tokenId, firstOwner);
    }

    /// @notice Set the token URI of a batch of land tokens after they are purchased in an auction
    /// @param tokenId An array of land token identifiers
    /// @param URIs An array of token URIs corresponding to the land tokens
    /// @dev The function requires that the caller has the MANAGER_ROLE, and both arrays must have the same length
    function setBatchTokenURI(
        uint256[] calldata tokenId,
        string[] calldata URIs
    ) external onlyRole(MANAGER_ROLE) {
        require(tokenId.length == URIs.length, "Different sizes of arrays");
        uint256 length = tokenId.length;
        for(uint256 i = 0; i < length;){
            _setTokenURI(tokenId[i], URIs[i]);
            unchecked {
                i++;
            }
        }
    }

    /// @notice Change allowed land sizes
    /// @param landSize Land size
    /// @param isAllowed Is allowed or not
    function changeAllowedLandSizes(
        string calldata landSize,
        bool isAllowed
    ) external onlyRole(MANAGER_ROLE) {
        allowedLandSizes[landSize] = isAllowed;
        emit ChangedAllowedLandSizes(landSize, isAllowed);
    }

    /// @notice Change allowed land rings
    /// @param landRing Land ring
    /// @param isAllowed Is allowed or not
    function changeAllowedLandRings(
        string calldata landRing,
        bool isAllowed
    ) external onlyRole(MANAGER_ROLE) {
        allowedLandRings[landRing] = isAllowed;
        emit ChangedAllowedLandRings(landRing, isAllowed);
    }

    /// @notice Change allowed land sectors
    /// @param landSector Land sector
    /// @param isAllowed Is allowed or not
    function changeAllowedLandSectors(
        string calldata landSector,
        bool isAllowed
    ) external onlyRole(MANAGER_ROLE) {
        allowedLandSectors[landSector] = isAllowed;
        emit ChangedAllowedLandSectors(landSector, isAllowed);
    }

    /// @notice Change allowed land districts
    /// @param landDistrict Land district
    /// @param isAllowed Is allowed or not
    function changeAllowedLandDistricts(
        string calldata landDistrict,
        bool isAllowed
    ) external onlyRole(MANAGER_ROLE) {
        allowedLandDistricts[landDistrict] = isAllowed;
        emit ChangedAllowedLandDistricts(landDistrict, isAllowed);
    }

    /// @notice gets the first owner of the land
    /// @param tokenId Identifier of the land
    function getFirstOwner(uint256 tokenId) external view returns (address) {
        return firstOwnerLandMap[tokenId];
    }

    /// @notice Gets the size of the land
    /// @param tokenId Identifier of the land
    function getLandSize(
        uint256 tokenId
    ) external view returns (string memory) {
        return (landsInfoMap[tokenId].size);
    }

    //============== PUBLIC FUNCTIONS ==============
    /// @notice Mints a Land
    /// @param to The address that will receive the NFT
    /// @param uri The URI of the NFT
    /// @param landInfo The size, ring, sector and district
    function safeMint(
        address to,
        string memory uri,
        LandInfo calldata landInfo
    ) public onlyRole(MINTER_ROLE) {
        require(allowedLandSizes[landInfo.size], "Not allowed land size");
        require(allowedLandRings[landInfo.ring], "Not allowed land ring");
        require(allowedLandSectors[landInfo.sector], "Not allowed land sector");
        require(
            allowedLandDistricts[landInfo.district],
            "Not allowed land disctric"
        );

        uint256 tokenId = _tokenIdCounter.current();
        landsInfoMap[tokenId].size = landInfo.size;
        landsInfoMap[tokenId].ring = landInfo.ring;
        landsInfoMap[tokenId].sector = landInfo.sector;
        landsInfoMap[tokenId].district = landInfo.district;
        landsInfoMap[tokenId].collectionName = landInfo.collectionName;
        landsInfoMap[tokenId].collectionId = landInfo.collectionId;
        _tokenIdCounter.increment();
        _safeMint(to, tokenId);
        _setTokenURI(tokenId, uri);
        emit LandMinted(
            tokenId,
            landInfo.size,
            landInfo.ring,
            landInfo.sector,
            landInfo.district,
            landInfo.collectionName,
            landInfo.collectionId
        );
    }

    /// @dev The following functions are overrides required by Solidity.
    function tokenURI(
        uint256 tokenId
    )
        public
        view
        override(ERC721Upgradeable, ERC721URIStorageUpgradeable)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        override(ERC721Upgradeable, AccessControlUpgradeable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    //============== INTERNAL FUNCTIONS ==============
    function _burn(
        uint256 tokenId
    ) internal override(ERC721Upgradeable, ERC721URIStorageUpgradeable) {
        super._burn(tokenId);
    }
}"
    },
