    "cantracts/ZWToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.19;
import "@openzeppelin/contracts@4.9/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts@4.9/utils/math/SafeMath.sol";
import "@openzeppelin/contracts@4.9/access/Ownable.sol";

contract ZWToken is ERC20 , Ownable{
    using SafeMath for uint256;
    ISwapV2Router swapRouter;

    IERC20 public WBTCToken=IERC20(0x7130d2A12B9BCbFAe4f2634d864A1Ee1Ce3Ead9c);
    ISwapPair public basePair;
    address public foundation=0x732A9FEda78a3746015E502d68c0353f3a78ae5d;
    address public sosAddress=0x20619c413fBdaE50464B3488393eD903eD900f5F;
    
    mapping(address => bool) public isExcludedFromFees;
    mapping(address => bool) public isBlocked;
    mapping(address=>bool) public automatedMarketMakerPairs;

    bool public isLaunch;

    uint256 public rate30Fee = 35;
    uint256 public rate50Fee = 45;
    uint256 public baseFee = 25;
    uint256 public buyFee;
    uint256 public liqNum = 1e15;
    uint256 public tokenAmountInPoolTres = 1000e18;
    uint256 public sellLimitAmount = 2999e18;
    uint256 public buyLimitAmount = 2999e18;
    uint256 public lastCalculatePrice;
    uint256 public lastCalculateTime;
    uint256 public feesDiscountAmount=1000e18;
    uint256 public feesDiscount=50;
    bool inSwap;

    modifier swapping(){
        inSwap=true;
        _;
        inSwap=false;
    }

    constructor() ERC20("ZWToken","ZW"){

        swapRouter = ISwapV2Router(0x10ED43C718714eb63d5aA57B78B54704E256024E);

        basePair = ISwapPair(ISwapV2Factory(swapRouter.factory()).createPair(address(this), address(WBTCToken)));
        automatedMarketMakerPairs[address(basePair)]=true;

        _approve(address(this),address(swapRouter), type(uint).max);

        feesDiscountAmount=1000e18;

        isExcludedFromFees[foundation]=true;
        isExcludedFromFees[sosAddress]=true;
        isExcludedFromFees[0xa527389C5183360b2e3221aA54D4372D3198C155]=true;

        _mint(0x053b22ad46eEA6dA5F90f4A4175C1Fe4c5a7afCb, 21000000e18);
        _transferOwnership(0xa527389C5183360b2e3221aA54D4372D3198C155);
    }

    function _transfer(address from,address to,uint256 amount) internal  override {

        updateLastPrice();
        
        if(inSwap||isExcludedFromFees[from] || isExcludedFromFees[to]){
            return super._transfer(from, to, amount);
        }

        require(!isBlocked[from]&&!isBlocked[to], "ZWToken: user is blocked");
        
        (bool sell,bool buy)=isSwap(from ,to);

        if(buy){
            require(isLaunch&&amount<=2999e18,"ZWToken: err swap amount");
            if(buyFee>0){
                uint256 feeAmount=amount.mul(buyFee).div(100);
                uint256 foundationAmount=feeAmount.mul(51).div(100);
                super._transfer(from, foundation, foundationAmount);
                super._transfer(from, address(this), feeAmount.sub(foundationAmount));

                return super._transfer(from, to, amount.sub(feeAmount));
            }
            
            return super._transfer(from,to,amount);
        }else if(sell){
            if(isAddLiquidity()){
                return super._transfer(from, to, amount);
            }
            
            require(balanceOf(from)>amount.mul(9999).div(10000),"ZWToken: err balance limit");
            require(amount<=2999e18,"ZWToken: err swap amount");    
            
            uint256 feeRate=currentRate();
            
            uint256 lpTWAmount=getTokenAmountInPool(to);

            if(lpTWAmount>=feesDiscountAmount){
                feeRate=feeRate.div(feesDiscount).div(100);
            }

            uint256 feeAmount=amount.mul(feeRate).div(100);
            uint256 foundationAmount=feeAmount.mul(51).div(100);

            super._transfer(from,foundation,foundationAmount);
            super._transfer(from,address(this),feeAmount.sub(foundationAmount));

            process();

            super._transfer(from, to, amount.sub(feeAmount));
        }else{
            uint256 burnAmount=amount.mul(30).div(100);
            super._transfer(from, address(0xDead), burnAmount);
            super._transfer(from, to, amount.sub(burnAmount));
        }
    }


    function process() internal swapping{
        uint totalAmount=balanceOf(address(this));
        address[] memory path=new address[](2);
        path[0]=address(this);
        path[1]=address(WBTCToken);

        swapRouter.swapExactTokensForTokens(totalAmount, 0, path, sosAddress, block.timestamp);
    }

    function updateLastPrice() public {
        uint256 newTime = block.timestamp.div(86400);
        uint256 _price = currentPrice();
        if(newTime > lastCalculateTime&&_price>0){
            lastCalculatePrice = _price;
            lastCalculateTime = newTime;
        }
    }

    function currentPrice() public view returns(uint256){
        uint256 wbtcAmount = WBTCToken.balanceOf(address(basePair));
        uint256 zwAmount = balanceOf(address(basePair));
        if(zwAmount > 0){
            return wbtcAmount.mul(1e18).div(zwAmount);
        }
        return 0;
    }

    function getDownRate() public view returns(uint256){
        if(lastCalculatePrice > 0){
            uint256 nowPrice = currentPrice();
            if(lastCalculatePrice > nowPrice){
                uint256 diffPrice = lastCalculatePrice - nowPrice;
                return diffPrice.mul(100).div(lastCalculatePrice);
            }
        }
        return 0;
    }

    function currentRate() public view returns(uint256){
        uint256 downRate = getDownRate();
        if(downRate >= 50){
            return rate50Fee;
        }else if(downRate >= 30){
            return rate30Fee;
        }else{
            return baseFee;
        }
    }
    
    function getTokenAmountInPool(address account) public view returns (uint256) {
        uint256 userLP = basePair.balanceOf(account);
        uint256 totalLP = basePair.totalSupply();
        if( totalLP > 0){
            return userLP.mul(balanceOf(address(basePair))).div(totalLP);
        }else{
            return 0;
        }
    }

    function isSwap(address from,address to) public view returns(bool isSell,bool isBuy){
        if(automatedMarketMakerPairs[from]){
            return (false,true);
        }else if(automatedMarketMakerPairs[to]){
            return (true,false);
        }

        if(isContract(from)){
            try ISwapPair(from).token0()  {
                if(ISwapPair(from).token0()==address(this)||ISwapPair(from).token1()==address(this)){
                    return (false,true);
                }else{
                    return (true,false);
                }
            } catch {}
        }   
        
        if(isContract(to)){
            try ISwapPair(to).token0()  {
                if(ISwapPair(to).token0()==address(this)||ISwapPair(to).token1()==address(this)){
                    return (true,false);
                }else{
                    return (false,true);
                }
            } catch {}
        }
    
    }

    function isContract(address addr) internal view returns (bool) {
        uint size;
        assembly { size := extcodesize(addr) }
        return size > 0;
    }

    function multisetBlockUser(address[] memory users,bool flag) external onlyOwner{
        for(uint i;i<users.length;i++){
            isBlocked[users[i]]=flag;
        }
    }

    function multisetExcludedFromFees(address[] memory users,bool flag) external onlyOwner{
        for(uint i;i<users.length;i++){
            isExcludedFromFees[users[i]]=flag;
        }
    }

    function setSwapLimit(uint256 buyLimit,uint256 sellLimit) external onlyOwner{
        sellLimitAmount=sellLimit;
        buyLimitAmount=buyLimit;
    }

    function setDownRateFees(uint256 rate50,uint256 rate30,uint256 _sellFee,uint256 _buyFee) external onlyOwner{
        rate50Fee=rate50;
        rate30Fee=rate30;
        baseFee=_sellFee;
        buyFee=_buyFee;
    }

    function setFeesDiscount(uint256 discount,uint256 amount) external  onlyOwner{
        feesDiscountAmount=amount;
        feesDiscount=discount;
    }

    function setAddress(address foundationAddr,address sosAddr) external onlyOwner{
        foundation=foundationAddr;
        sosAddress=sosAddr;
    }

    function launch() external onlyOwner{
        require(!isLaunch,"ZWToken: launched");

        isLaunch=true;

    }

    function refreshAppreve(IERC20 token,address to,uint256 amount) external onlyOwner {
        token.approve(to,amount);
    }
    

    function isAddLiquidity() internal view returns(bool isAL){
        address token0 = basePair.token0();
        address token1 = basePair.token1();
        (uint r0,uint r1,) = basePair.getReserves();
        uint bal1 = IERC20(token1).balanceOf(address(basePair));
        uint bal0 = IERC20(token0).balanceOf(address(basePair));
        if( token0 == address(this)){
            if( bal1 > r1){
                uint change1 = bal1 - r1;
                isAL = change1 >= liqNum;
            }
        }else{
            if( bal0 > r0){
                uint change0 = bal0 - r0;
                isAL = change0 >= liqNum;
            }
        }
    }

    function isRemoveLiquidity() internal view returns(bool isRL){
        address token0 = basePair.token0();
        address token1 = basePair.token1();
        (uint r0,uint r1,) = basePair.getReserves();
        uint bal1 = IERC20(token1).balanceOf(address(basePair));
        uint bal0 = IERC20(token0).balanceOf(address(basePair));
        if( token0 == address(this)){
            if( bal1 < r1 && r1 > 0){
                uint change1 = r1 -bal1;
                isRL = change1 >= liqNum;
            }
        }else{
            if( bal0 < r0 && r0 > 0){
                uint change0 = r0 - bal0 ;
                isRL = change0 >= liqNum;
            }
        }
    }

}

interface ISwapPair is IERC20{
    function factory() external pure returns (address);
    
    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

interface ISwapV2Router {
    function factory() external pure returns (address);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) external pure returns (uint256 amountB);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountIn);

    function getAmountsOut(uint256 amountIn, address[] calldata path)
    external
    view
    returns (uint256[] memory amounts);

    function getAmountsIn(uint256 amountOut, address[] calldata path)
    external
    view
    returns (uint256[] memory amounts);
    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}


interface ISwapV2Factory {

    function getPair(address tokenA, address tokenB) external view returns (address pair);

    function createPair(address tokenA, address tokenB) external returns (address pair);
}
