pragma solidity ^0.8.7;









abstract contract Manageable is Ownable {
    mapping(address => bool) private _managers;

    event ManagerRemoved(address indexed manager_);
    event ManagerAdded(address indexed manager_);

    constructor() {}

    function managers(address manager_) public view virtual returns (bool) {
        return _managers[manager_];
    }

    modifier onlyManager() {
        require(_managers[_msgSender()], "Manageable: caller is not the owner");
        _;
    }

    function removeManager(address manager_) public virtual onlyOwner {
        _managers[manager_] = false;
        emit ManagerRemoved(manager_);
    }

    function addManager(address manager_) public virtual onlyOwner {
        require(
            manager_ != address(0),
            "Manageable: new owner is the zero address"
        );
        _managers[manager_] = true;
        emit ManagerAdded(manager_);
    }
}

contract NFT is
    Ownable,
    Manageable,
    ERC165,
    IERC721,
    IERC721Metadata,
    IERC721Enumerable
{
    using Address for address;
    using Strings for uint256;

    struct Node {
        uint256 id;
        string name;
        uint256 tier;
        uint256 value;
        uint256 totalClaimed;
        uint256 mintTimestamp;
        uint256 claimTimestamp;
    }

    struct Player {
        uint256 totalDeposit;
        uint256 totalAirdrop;
        uint256 totalClaimed;
    }

    mapping(address => uint256) private _balances;
    mapping(address => mapping(uint256 => uint256)) private _ownedTokens;
    mapping(uint256 => uint256) private _ownedTokensIndex;
    mapping(uint256 => address) private _owners;
    mapping(address => Player) public players;
    mapping(uint256 => Node) public _nodes;
    mapping(uint256 => address) private _tokenApprovals;
    mapping(address => mapping(address => bool)) private _operatorApprovals;
    mapping(address => bool) public isBlacklisted;

    uint256[] private _allTokens;
    mapping(uint256 => uint256) private _allTokensIndex;

    string public _baseURI = "";
    string public _name = "";
    string private _symbol = "";
    string public baseExtension = ".json";

    uint256 public minted = 0;
    uint256 public maxSupply = 3000;

    bool public mintingEnabled = true;
    bool public transferEnabled = false;
    bool public allowBurn = false;

    constructor(
        string memory baseURI,
        string memory name_,
        string memory symbol_,
        address manager_
    ) {
        _baseURI = baseURI;
        _name = name_;
        _symbol = symbol_;
        addManager(manager_);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC165, IERC165)
        returns (bool)
    {
        return
            interfaceId == type(IERC721).interfaceId ||
            interfaceId == type(IERC721Metadata).interfaceId ||
            interfaceId == type(IERC721Enumerable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function userNodes(address user) public view returns (Node[] memory) {
        uint256 balance = balanceOf(user);
        Node[] memory hisNodes = new Node[](balance);
        for (uint256 i = 0; i < balance; i++) {
            hisNodes[i] = _nodes[tokenOfOwnerByIndex(user, i)];
        }
        return hisNodes;
    }

    function _exists(uint256 tokenId) internal view virtual returns (bool) {
        return _owners[tokenId] != address(0);
    }

    modifier tokenExists(uint256 tokenId) {
        require(_exists(tokenId), "ERC721: owner query for nonexistent token");
        _;
    }

    function balanceOf(address owner) public view override returns (uint256) {
        return _balances[owner];
    }

    function ownerOf(uint256 tokenId)
        public
        view
        override
        tokenExists(tokenId)
        returns (address)
    {
        return _owners[tokenId];
    }

    function name() public view override returns (string memory) {
        return _name;
    }

    function symbol() public view override returns (string memory) {
        return _symbol;
    }

    function totalSupply() public view override returns (uint256) {
        return _allTokens.length;
    }

    function tokenOfOwnerByIndex(address owner, uint256 index)
        public
        view
        virtual
        override
        returns (uint256)
    {
        require(
            index < balanceOf(owner),
            "ERC721Enumerable: owner index out of bounds"
        );
        return _ownedTokens[owner][index];
    }

    function tokenByIndex(uint256 index)
        public
        view
        virtual
        override
        returns (uint256)
    {
        require(
            index < totalSupply(),
            "ERC721Enumerable: global index out of bounds"
        );
        return _allTokens[index];
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override
        tokenExists(tokenId)
        returns (string memory)
    {
        string memory baseURI = _baseURI;
        return
            bytes(baseURI).length > 0
                ? string(
                    abi.encodePacked(baseURI, tokenId.toString(), baseExtension)
                )
                : "";
    }

    function approve(address to, uint256 tokenId) public override {
        address owner = ownerOf(tokenId);
        require(
            !isBlacklisted[owner] && !isBlacklisted[to],
            "ERC721: blacklisted"
        );
        require(to != owner, "ERC721: approval to current owner");

        require(
            _msgSender() == owner || isApprovedForAll(owner, _msgSender()),
            "ERC721: approve caller is not owner nor approved for all"
        );

        _approve(to, tokenId);
    }

    function getApproved(uint256 tokenId)
        public
        view
        override
        returns (address)
    {
        require(
            _exists(tokenId),
            "ERC721: approved query for nonexistent token"
        );

        return _tokenApprovals[tokenId];
    }

    function setApprovalForAll(address operator, bool approved)
        public
        override
    {
        _setApprovalForAll(_msgSender(), operator, approved);
    }

    function isApprovedForAll(address owner, address operator)
        public
        view
        override
        returns (bool)
    {
        return _operatorApprovals[owner][operator];
    }

    function transferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override {
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "ERC721: transfer caller is not owner nor approved"
        );

        _transfer(from, to, tokenId);
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId
    ) public override {
        safeTransferFrom(from, to, tokenId, "");
    }

    function safeTransferFrom(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) public override {
        require(
            _isApprovedOrOwner(_msgSender(), tokenId),
            "ERC721: transfer caller is not owner nor approved"
        );
        _safeTransfer(from, to, tokenId, _data);
    }

    function _safeTransfer(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) internal {
        _transfer(from, to, tokenId);
        require(
            _checkOnERC721Received(from, to, tokenId, _data),
            "ERC721: transfer to non ERC721Receiver implementer"
        );
    }

    function _isApprovedOrOwner(address spender, uint256 tokenId)
        internal
        view
        returns (bool)
    {
        require(
            _exists(tokenId),
            "ERC721: operator query for nonexistent token"
        );
        address owner = ownerOf(tokenId);
        return (spender == owner ||
            isApprovedForAll(owner, spender) ||
            getApproved(tokenId) == spender);
    }

    function _safeMint(address to, uint256 tokenId) internal {
        _safeMint(to, tokenId, "");
    }

    function _safeMint(
        address to,
        uint256 tokenId,
        bytes memory _data
    ) internal {
        _mint(to, tokenId);
        require(
            _checkOnERC721Received(address(0), to, tokenId, _data),
            "ERC721: transfer to non ERC721Receiver implementer"
        );
    }

    function _mint(address to, uint256 tokenId) internal {
        require(mintingEnabled, "ERC721: minting not enabled yet");
        require(!isBlacklisted[to], "ERC721: blacklisted");
        require(to != address(0), "ERC721: mint to the zero address");
        require(!_exists(tokenId), "ERC721: token already minted");
        minted++;
        require(minted <= maxSupply, "ERC721: max supplied reached");

        _beforeTokenTransfer(address(0), to, tokenId);

        _balances[to] += 1;
        _owners[tokenId] = to;

        emit Transfer(address(0), to, tokenId);
    }

    function _burn(uint256 tokenId) internal {
        address owner = ownerOf(tokenId);

        _beforeTokenTransfer(owner, address(0), tokenId);

        _approve(address(0), tokenId);

        _balances[owner] -= 1;
        delete _owners[tokenId];

        emit Transfer(owner, address(0), tokenId);
    }

    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) internal {
        require(transferEnabled, "ERC721: transfers are not enabled");
        require(
            !isBlacklisted[from] && !isBlacklisted[to],
            "ERC721: blacklisted"
        );
        require(
            ownerOf(tokenId) == from,
            "ERC721: transfer from incorrect owner"
        );
        require(to != address(0), "ERC721: transfer to the zero address");

        _beforeTokenTransfer(from, to, tokenId);

        _approve(address(0), tokenId);

        _balances[from] -= 1;
        _balances[to] += 1;
        _owners[tokenId] = to;

        emit Transfer(from, to, tokenId);
    }

    function _approve(address to, uint256 tokenId) internal {
        _tokenApprovals[tokenId] = to;
        emit Approval(ownerOf(tokenId), to, tokenId);
    }

    function _setApprovalForAll(
        address owner,
        address operator,
        bool approved
    ) internal {
        require(owner != operator, "ERC721: approve to caller");
        require(
            !isBlacklisted[owner] && !isBlacklisted[operator],
            "ERC721: blacklisted"
        );
        _operatorApprovals[owner][operator] = approved;
        emit ApprovalForAll(owner, operator, approved);
    }

    function _checkOnERC721Received(
        address from,
        address to,
        uint256 tokenId,
        bytes memory _data
    ) private returns (bool) {
        if (to.isContract()) {
            try
                IERC721Receiver(to).onERC721Received(
                    _msgSender(),
                    from,
                    tokenId,
                    _data
                )
            returns (bytes4 retval) {
                return retval == IERC721Receiver.onERC721Received.selector;
            } catch (bytes memory reason) {
                if (reason.length == 0) {
                    revert(
                        "ERC721: transfer to non ERC721Receiver implementer"
                    );
                } else {
                    assembly {
                        revert(add(32, reason), mload(reason))
                    }
                }
            }
        } else {
            return true;
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal {
        if (from == address(0)) {
            _addTokenToAllTokensEnumeration(tokenId);
        } else if (from != to) {
            _removeTokenFromOwnerEnumeration(from, tokenId);
        }
        if (to == address(0) && allowBurn) {
            _removeTokenFromAllTokensEnumeration(tokenId);
        } else if (to != from) {
            _addTokenToOwnerEnumeration(to, tokenId);
        }
    }

    function _addTokenToOwnerEnumeration(address to, uint256 tokenId) private {
        uint256 length = balanceOf(to);
        _ownedTokens[to][length] = tokenId;
        _ownedTokensIndex[tokenId] = length;
    }

    function _addTokenToAllTokensEnumeration(uint256 tokenId) private {
        _allTokensIndex[tokenId] = _allTokens.length;
        _allTokens.push(tokenId);
    }

    function _removeTokenFromOwnerEnumeration(address from, uint256 tokenId)
        private
    {
        uint256 lastTokenIndex = balanceOf(from) - 1;
        uint256 tokenIndex = _ownedTokensIndex[tokenId];

        if (tokenIndex != lastTokenIndex) {
            uint256 lastTokenId = _ownedTokens[from][lastTokenIndex];

            _ownedTokens[from][tokenIndex] = lastTokenId;
            _ownedTokensIndex[lastTokenId] = tokenIndex;
        }

        delete _ownedTokensIndex[tokenId];
        delete _ownedTokens[from][lastTokenIndex];
    }

    function _removeTokenFromAllTokensEnumeration(uint256 tokenId) private {
        uint256 lastTokenIndex = _allTokens.length - 1;
        uint256 tokenIndex = _allTokensIndex[tokenId];

        uint256 lastTokenId = _allTokens[lastTokenIndex];

        _allTokens[tokenIndex] = lastTokenId;
        _allTokensIndex[lastTokenId] = tokenIndex;

        delete _allTokensIndex[tokenId];
        _allTokens.pop();
    }

    function mint(
        address to,
        string memory nodeName,
        uint256 tier,
        uint256 value
    ) public onlyManager {
        uint256 id = totalSupply() + 1;
        _safeMint(to, id);
        _nodes[id] = Node({
            id: id,
            name: nodeName,
            tier: tier,
            value: value,
            totalClaimed: 0,
            mintTimestamp: block.timestamp,
            claimTimestamp: block.timestamp
        });
        players[to].totalDeposit += value;
    }

    function mint(
        address to,
        string memory nodeName,
        uint256 value,
        uint256 totalClaimed,
        uint8 tier,
        uint256 mintTimestamp,
        uint256 claimTimestamp
    ) public onlyOwner {
        uint256 id = totalSupply() + 1;
        _safeMint(to, id);
        _nodes[id] = Node({
            id: id,
            name: nodeName,
            tier: tier,
            value: value,
            totalClaimed: totalClaimed,
            mintTimestamp: mintTimestamp,
            claimTimestamp: claimTimestamp
        });
        players[to].totalDeposit += value;
    }

    function updateClaimTimestamp(uint256 id) public onlyManager {
        _nodes[id].claimTimestamp = block.timestamp;
    }

    function updateValue(uint256 id, uint256 rewards) public onlyManager {
        players[ownerOf(id)].totalDeposit += rewards;
        _nodes[id].value += rewards;
    }

    function updateName(uint256 id, string memory nodeName) public onlyManager {
        _nodes[id].name = nodeName;
    }

    function updateTotalClaimed(uint256 id, uint256 rewards)
        public
        onlyManager
    {
        players[ownerOf(id)].totalClaimed += rewards;
        _nodes[id].totalClaimed += rewards;
    }

    function addAirdrop(address to, uint256 quantity) public onlyManager {
        players[to].totalAirdrop += quantity;
    }

    function updateMintingEnabled(bool value) public onlyOwner {
        mintingEnabled = value;
    }

    function updateTransferEnabled(bool value) public onlyOwner {
        transferEnabled = value;
    }

    function updateMaxSupply(uint256 value) public onlyOwner {
        maxSupply = value;
    }

    function updateTiers(uint256[] memory ids, uint8 tier) public onlyOwner {
        for (uint256 i = 0; i < ids.length; i++) {
            _nodes[ids[i]].tier = tier;
        }
    }

    function updateBaseURI(string memory baseURI) public onlyOwner {
        _baseURI = baseURI;
    }
}
