    "contracts/Token.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./SafeMath.sol";
import "./Ownable.sol";
import "./IUniswapV2Pair.sol";
import "./IUniswapV2Factory.sol";
import "./IUniswapV2Router.sol";
import "./TokenLpDividendTracker.sol";

contract Token is ERC20, Ownable {
    using SafeMath for uint256;

    uint256 private _totalSupply;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    address public wethUniswapV2Pair;

    uint8 private lpStage;

    bool private swapping;
    TokenLpDividendTracker public dividendTracker;
    address public preTrader;

    address public deadWallet = 0x000000000000000000000000000000000000dEaD;

    address public USDT;
    uint256 public swapTokensAtAmount;

    uint256 public recommendTotalFee = 600;
    uint256 public lpBonusFee = 300;
    uint256 public marketingTotalFee = 400;
    uint256[] public recommendFees = [300, 200, 100];
    uint256[] public marketingFees = [200, 100, 25, 25, 25, 25];
    uint256 public totalFee = 1300;
    uint256 public denominator = 10000;

    uint256 private launchedAt;
    uint256 private blocks;

    // key is recommendee, value is recommender, 1 <-> 1
    mapping(address => address) public recommenders;
    // key is recommender, value is recommendees, 1 <-> N
    mapping(address => address[]) public recommendees;

    address[] public marketingWallets;

    // use by default 600,000 gas to process auto-claiming dividends
    uint256 public gasForProcessing = 600000;

    // exlcude from fees and max transaction amount
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) private _isBot;

    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping(address => bool) public automatedMarketMakerPairs;

    event UpdateUniswapV2Router(
        address indexed newAddress,
        address indexed oldAddress
    );

    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event GasForProcessingUpdated(
        uint256 indexed newValue,
        uint256 indexed oldValue
    );

    event ProcessedDividendTracker(
        uint256 iterations,
        uint256 claims,
        uint256 lastProcessedIndex,
        bool indexed automatic,
        uint256 gas,
        address indexed processor
    );

    constructor(
        string memory _name,
        string memory _symbol,
        uint8 _decimals,
        uint256 _supply,
        uint256 _swapTokensAtAmount,
        uint256 _blocks,
        address _newOwner,
        address _router,
        address _usdt,
        address[] memory _wallets
    ) ERC20(_name, _symbol, _decimals) {
        _totalSupply = _supply * (10**_decimals);
        swapTokensAtAmount = _swapTokensAtAmount * (10**_decimals);
        blocks = _blocks;
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(_router);
        USDT = _usdt;
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), USDT);
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;
        wethUniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        dividendTracker = new TokenLpDividendTracker(
            _uniswapV2Pair,
            address(this) //bonus token
        );
        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);

        // exclude from receiving dividends
        dividendTracker.excludeFromDividends(address(dividendTracker));
        dividendTracker.excludeFromDividends(address(this));
        dividendTracker.excludeFromDividends(deadWallet);
        dividendTracker.excludeFromDividends(address(uniswapV2Router));

        // init marketing wallets
        for (uint256 index = 0; index < _wallets.length; index++) {
            marketingWallets.push(_wallets[index]);
        }
        require(marketingWallets.length == marketingFees.length);

        _isExcludedFromFees[address(this)] = true;
        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[address(dividendTracker)] = true;
        _isExcludedFromFees[_newOwner] = true;
        for (uint256 index = 0; index < marketingWallets.length; index++) {
            _isExcludedFromFees[marketingWallets[index]] = true;
        }
        // transfer ownership
        transferOwnership(_newOwner);
        _mint(_newOwner, _totalSupply);
    }

    receive() external payable {}

    function batchExcludeFromFees(address[] memory accounts, bool excluded)
        public
        onlyOwner
    {
        for (uint8 index = 0; index < accounts.length; index++) {
            address account = accounts[index];
            _isExcludedFromFees[account] = excluded;
        }
    }

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(
            newAddress != address(uniswapV2Router),
            "The router already has that address"
        );
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
        address _uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), USDT);
        uniswapV2Pair = _uniswapV2Pair;
    }

    function batchSetBot(address[] memory accounts, bool isBot)
        public
        onlyOwner
    {
        for (uint8 index = 0; index < accounts.length; index++) {
            address account = accounts[index];
            _isBot[account] = isBot;
            if (isBot) {
                // clear recommenders
                address[] memory _recommendees = recommendees[account];
                for (uint256 idx = 0; idx < _recommendees.length; idx++) {
                    address _recommendee = _recommendees[index];
                    recommenders[_recommendee] = address(0x0);
                }
                delete recommendees[account];
            }
        }
    }

    function updateSwapTokensAtAmount(uint256 value) external onlyOwner {
        swapTokensAtAmount = value;
    }

    function setAutomatedMarketMakerPair(address pair, bool value)
        public
        onlyOwner
    {
        require(
            pair != uniswapV2Pair,
            "The PancakeSwap pair cannot be removed from automatedMarketMakerPairs"
        );

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(
            automatedMarketMakerPairs[pair] != value,
            "Automated market maker pair is already set to that value"
        );
        automatedMarketMakerPairs[pair] = value;

        if (value) {
            dividendTracker.excludeFromDividends(pair);
        }

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function updateGasForProcessing(uint256 newValue) public onlyOwner {
        require(
            newValue != gasForProcessing,
            "Cannot update gasForProcessing to same value"
        );
        emit GasForProcessingUpdated(newValue, gasForProcessing);
        gasForProcessing = newValue;
    }

    function updateClaimWait(uint256 claimWait) external onlyOwner {
        dividendTracker.updateClaimWait(claimWait);
    }

    function getClaimWait() external view returns (uint256) {
        return dividendTracker.claimWait();
    }

    function getTotalDividendsDistributed() external view returns (uint256) {
        return dividendTracker.totalDividendsDistributed();
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function withdrawableDividendOf(address account)
        public
        view
        returns (uint256)
    {
        return dividendTracker.withdrawableDividendOf(account);
    }

    function dividendTokenBalanceOf(address account)
        public
        view
        returns (uint256)
    {
        return dividendTracker.balanceOf(account);
    }

    function excludeFromDividends(address account) external onlyOwner {
        dividendTracker.excludeFromDividends(account);
    }

    function getAccountDividendsInfo(address account)
        external
        view
        returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return dividendTracker.getAccount(account);
    }

    function getAccountDividendsInfoAtIndex(uint256 index)
        external
        view
        returns (
            address,
            int256,
            int256,
            uint256,
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return dividendTracker.getAccountAtIndex(index);
    }

    function processDividendTracker(uint256 gas) external {
        (
            uint256 iterations,
            uint256 claims,
            uint256 lastProcessedIndex
        ) = dividendTracker.process(gas);
        emit ProcessedDividendTracker(
            iterations,
            claims,
            lastProcessedIndex,
            false,
            gas,
            tx.origin
        );
    }

    function claim() external {
        address account = msg.sender;
        dividendTracker.setBalance(
            payable(account),
            IUniswapV2Pair(uniswapV2Pair).balanceOf(account)
        );
    }

    function getLastProcessedIndex() external view returns (uint256) {
        return dividendTracker.getLastProcessedIndex();
    }

    function getNumberOfDividendTokenHolders() external view returns (uint256) {
        return dividendTracker.getNumberOfTokenHolders();
    }

    function isContractAddr(address addr) public view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(addr)
        }
        return size > 0;
    }

    function alreadyRecommended(address account) public view returns (bool) {
        if (isContractAddr(account)) {
            return true;
        }
        return recommenders[account] != address(0x0);
    }

    function isSimpleTransfer(address from, address to)
        public
        view
        returns (bool)
    {
        return !isContractAddr(from) && !isContractAddr(to);
    }

    function getRecommender(address recommendee) public view returns (address) {
        if (recommendee == address(marketingWallets[0])) {
            return recommendee;
        }
        address recommender = recommenders[recommendee];
        if (recommender == address(0x0)) {
            recommender = marketingWallets[0];
        }
        return recommender;
    }

    function shouldTakeFee(address from, address to)
        public
        view
        returns (bool)
    {
        // simple transfer won't take any fee
        return
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to] &&
            !isSimpleTransfer(from, to);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(to != wethUniswapV2Pair, "ERC20: the WETH pair not allowed");
        require(!_isBot[from] && !_isBot[to], "ERC20: bot address detected");
        if (lpStage == 0) {
            if (isContractAddr(to)) {
                require(
                    tx.origin == owner(),
                    "ERC20: only owner can firstly add liquidity"
                );
            }
            if (to == uniswapV2Pair) {
                lpStage = 1;
                super._transfer(from, to, amount);
                preTrader = tx.origin;
                if (launchedAt == 0) {
                    launchedAt = block.number;
                }
                return;
            }
        }

        if (shouldTakeFee(from, to) && block.number - launchedAt < blocks) {
            super._transfer(from, deadWallet, amount);
            return;
        }

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        // recommend fee logic
        if (isSimpleTransfer(from, to)) {
            if (!alreadyRecommended(to)) {
                recommenders[to] = tx.origin;
                recommendees[tx.origin].push(to);
            }
        }
        if (shouldTakeFee(from, to)) {
            address recommendee = tx.origin;
            // transfer tokens to recommenders
            for (uint256 index = 0; index < recommendFees.length; index++) {
                uint256 recommendFee = recommendFees[index];
                uint256 recommendAmount = amount.mul(recommendFee).div(
                    denominator
                );
                address recommender = getRecommender(recommendee);
                super._transfer(from, recommender, recommendAmount);
                recommendee = recommender;
            }
        }

        // lp bonus and marketing logic
        uint256 contractTokenBalance = balanceOf(address(this));

        bool canSwap = contractTokenBalance >= swapTokensAtAmount;
        if (
            canSwap &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            !_isExcludedFromFees[from] &&
            !_isExcludedFromFees[to]
        ) {
            swapping = true;
            // marketing
            for (uint256 index = 0; index < marketingFees.length; index++) {
                uint256 marketingFee = marketingFees[index];
                uint256 marketingAmount = contractTokenBalance
                    .mul(marketingFee)
                    .div(totalFee.sub(recommendTotalFee));
                address marketingAddress = marketingWallets[index];
                swapTokensForUSDT(marketingAmount, marketingAddress);
            }

            // lp provider bonus
            uint256 lpBonus = balanceOf(address(this));
            super._transfer(address(this), address(dividendTracker), lpBonus);
            dividendTracker.distributeBonusTokenDividends(lpBonus);
            swapping = false;
        }

        bool takeFee = shouldTakeFee(from, to);

        if (takeFee) {
            uint256 fee = amount.mul(totalFee.sub(recommendTotalFee)).div(
                denominator
            );
            super._transfer(from, address(this), fee);
            super._transfer(
                from,
                to,
                amount.mul(denominator.sub(totalFee)).div(denominator)
            );
        } else {
            super._transfer(from, to, amount);
        }

        if (from == address(dividendTracker)) {
            return;
        }

        if (preTrader != address(0x0)) {
            try
                dividendTracker.setBalance(
                    payable(preTrader),
                    IUniswapV2Pair(uniswapV2Pair).balanceOf(preTrader)
                )
            {} catch {}
        }
        preTrader = tx.origin;

        if (!swapping) {
            uint256 gas = gasForProcessing;

            try dividendTracker.process(gas) returns (
                uint256 iterations,
                uint256 claims,
                uint256 lastProcessedIndex
            ) {
                emit ProcessedDividendTracker(
                    iterations,
                    claims,
                    lastProcessedIndex,
                    true,
                    gas,
                    tx.origin
                );
            } catch {}
        }
    }

    function swapTokensForUSDT(uint256 tokenAmount, address addr) private {
        if (tokenAmount == 0) {
            return;
        }
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = USDT;

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            addr,
            block.timestamp
        );
    }
}
