// File: contracts\AUTOLAND.sol

pragma solidity ^0.8.0;
contract AUTOLAND is AccessControl {
    using SafeERC20 for IERC20;

    struct UserInfo {
        uint256 shares; // ㄦ?
        uint256 lastDepositedTime; // ㄦ€ㄦ?
        uint256 landAtLastUserAction; // €ㄦland
        uint256 lastUserActionTime; // €?
        uint256 lastLevelAwardBlock; // €у? =?lastLevelAwardBlock?
    }

    mapping(address => UserInfo) public userInfo;

    uint256 public lastHarvestBlock; // €block℃
    uint256 public immutable perBlockAward; // ?
    address public exchangeAddress; // €ㄦ
    address public awardAddress; // 
    uint256 public totalShares; // 
    uint256 public totalLevelAward; // ?5%?
    address public landAddress;
    address public landLpStakeAddress; // landpㄦ㈡lp?
    IERC20 public landToken;

    uint256[13] private newAccountAwards = [
        1000,
        800,
        500,
        300,
        200,
        200,
        200,
        100,
        100,
        100,
        50,
        50,
        50
    ]; // 1W€

    uint256 public awardsPercentTotal; // 

    uint256 token1AwardLimit = 200 * 1e18; // ǔ?

    uint256 public immutable lockTime;

    event Deposit(
        address indexed sender,
        uint256 amount,
        uint256 shares,
        uint256 lastDepositedTime
    );

    event Withdraw(address indexed sender, uint256 amount, uint256 shares);

    constructor(
        address exchangeAddress_,
        address awardAddress_,
        address landAddress_,
        address landLpStakeAddress_,
        uint256 perBlockAward_,
        uint256 lockTime_
    ) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        exchangeAddress = exchangeAddress_;
        awardAddress = awardAddress_; // €?
        landAddress = landAddress_;
        landToken = IERC20(landAddress);
        landLpStakeAddress = landLpStakeAddress_;
        perBlockAward = perBlockAward_;
        lockTime = lockTime_;

        for (uint256 index = 0; index < newAccountAwards.length; index++) {
            awardsPercentTotal = awardsPercentTotal + newAccountAwards[index];
        }
    }

    uint32 isInHarvest = 1;

    modifier onlyInHarvest() {
        require(isInHarvest == 1, "only in InHarvest");
        isInHarvest = 0;
        _;
        isInHarvest = 1;
    }

    /**
     * @notice ,awardAddress€
     * @dev Only possible when contract
     */
    function harvest() public notContract onlyInHarvest {
        require(totalShares > 0, "Total share must larger than zero!");
        uint256 harvestAmount = getHarvestAmount(); // €
        require(harvestAmount > 0, "Amount must larger than zero!");
        lastHarvestBlock = block.number;
        landToken.safeTransferFrom(awardAddress, address(this), harvestAmount);
        totalLevelAward =
            totalLevelAward +
            (harvestAmount * awardsPercentTotal) /
            10000;
    }

    function getHarvestAmount() public view returns (uint256) {
        uint256 harvestAmount = (block.number - lastHarvestBlock) *
            perBlockAward; // €

        uint256 awardAllowance = landToken.allowance(
            awardAddress,
            address(this)
        );
        uint256 awardBalance = landToken.balanceOf(awardAddress);

        if (harvestAmount == 0 || awardAllowance == 0 || awardBalance == 0) {
            return 0;
        }

        // тт,ㄤュ?
        if (awardBalance < awardAllowance && awardBalance < harvestAmount) {
            return awardBalance;
        } else if (
            awardAllowance < awardBalance && awardAllowance < harvestAmount
        ) {
            return awardAllowance;
        }

        return harvestAmount;
    }

    /**
     * @notice Deposits funds into the Cake Vault
     * @dev Only possible when contract not paused.
     * @param _amount: number of tokens to deposit (in CAKE)
     */
    function deposit(uint256 _amount) external notContract {
        require(_amount > 0, "Nothing to deposit");

        UserInfo storage user = userInfo[msg.sender];

        if (totalShares > 0) {
            if (getHarvestAmount() > 0) {
                harvest();
            }
        } else {
            lastHarvestBlock = block.number; // totalshare==0ｄ€
        }

        // ㄦラ
        if (user.shares == 0) {
            user.lastLevelAwardBlock = block.number;
        } else if (getOutLevelAward(msg.sender) > 0) {
            giveOutLevelAward(1);
        }

        uint256 pool = balanceOf();
        tranferLandByExchangeAddress(msg.sender, address(this), _amount);
        uint256 currentShares = 0;
        if (totalShares != 0) {
            currentShares = (_amount * totalShares) / (pool);
        } else {
            currentShares = _amount;
        }

        user.shares = user.shares + currentShares;
        user.lastDepositedTime = block.timestamp;

        totalShares = totalShares + currentShares;

        user.landAtLastUserAction = (user.shares * balanceOf()) / totalShares;
        user.lastUserActionTime = block.timestamp;

        emit Deposit(msg.sender, _amount, currentShares, block.timestamp);
    }

    /**
     * @notice Withdraws all funds for a user
     */
    function withdrawAll() external notContract {
        withdraw(userInfo[msg.sender].shares);
    }

    /**
     * @notice Withdraws from funds from the Cake Vault
     * @param _shares: Number of shares to withdraw
     */
    function withdraw(uint256 _shares) public notContract {
        UserInfo storage user = userInfo[msg.sender];

        require(_shares > 0, "Nothing to withdraw");
        require(_shares <= user.shares, "Withdraw amount exceeds balance");
        require(
            block.timestamp > (user.lastDepositedTime + lockTime),
            "locked"
        );

        if (getHarvestAmount() > 0) {
            harvest();
        }
        // у?
        if (getOutLevelAward(msg.sender) > 0) {
            giveOutLevelAward(1);
        }

        uint256 currentAmount = (balanceOf() * _shares) / totalShares;
        user.shares = user.shares - _shares;
        totalShares = totalShares - _shares;

        if (user.shares > 0) {
            user.landAtLastUserAction =
                (balanceOf() * user.shares) /
                totalShares; // €℃ㄦand
        } else {
            user.landAtLastUserAction = 0;
        }
        user.lastUserActionTime = block.timestamp;
        tranferLandByExchangeAddress(address(this), msg.sender, currentAmount);

        emit Withdraw(msg.sender, currentAmount, _shares);
    }

    function getUnlockTime(address addr) public view returns (uint256) {
        UserInfo storage user = userInfo[addr];

        if (block.timestamp > (user.lastDepositedTime + lockTime)) {
            return 0;
        }

        return (user.lastDepositedTime + lockTime);
    }

    function getUserBalance(address addr) public view returns (uint256) {
        UserInfo storage user = userInfo[addr];

        if (totalShares == 0) {
            // ｅ€?
            return 0;
        }

        return (balanceOf() * user.shares) / totalShares;
    }

    function giveOutLevelAwardExternal() external {
        giveOutLevelAward(0);
    }

    /**
     * 
     */
    function giveOutLevelAward(uint32 awardType) internal {
        if (awardType == 0) {
            if (getHarvestAmount() > 0) {
                harvest();
            } //ｄц?
        }

        require(totalLevelAward > 0, "Level award had empty!");
        UserInfo storage user = userInfo[msg.sender];
        require(user.shares > 0, "address share must large than zero!");

        require(
            getOutLevelAward(msg.sender) > 0,
            "Level award mast large than zero"
        );

        uint256 outAmount = getOutLevelAward(msg.sender);
        if (outAmount > totalLevelAward) {
            outAmount = totalLevelAward;
        }
        totalLevelAward = totalLevelAward - outAmount;
        user.lastLevelAwardBlock = block.number;
        address parentAddress = getParent(msg.sender);
        if (parentAddress == address(0)) {
            IUserParent2(landAddress).burn(outAmount);
        } else {
            uint256 burnAmount = outAmount;
            for (uint256 index = 0; index < newAccountAwards.length; index++) {
                uint256 singleAwardAmount = (outAmount *
                    newAccountAwards[index]) / 10000;
                if (getToken1Value(parentAddress) >= token1AwardLimit) {
                    // ぇ€
                    burnAmount = burnAmount - singleAwardAmount;
                    tranferLandByExchangeAddress(
                        address(this),
                        parentAddress,
                        singleAwardAmount
                    );
                }
                parentAddress = getParent(parentAddress);
                if (parentAddress == address(0)) {
                    break;
                }
            }
            // ﹀
            if (burnAmount > 0) {
                IUserParent2(landAddress).burn(burnAmount);
            }
        }
    }

    bool public isCanEmergencyWithdraw = false;

    modifier onlyCanEmergencyWithdraw() {
        require(isCanEmergencyWithdraw, "emergency withdraw is not open!");
        _;
    }

    function setCanEmergencyWithdraw(bool isCanEmergencyWithdraw_)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        isCanEmergencyWithdraw = isCanEmergencyWithdraw_;
    }

    function emergencyWithdraw() external onlyCanEmergencyWithdraw {
        UserInfo storage user = userInfo[msg.sender];
        require(user.shares > 0, "Nothing to withdraw");
        require(available() > 0, "Nothing to withdraw");

        if (user.shares > totalShares) {
            user.shares = totalShares;
        }
        uint256 currentAmount = (available() * user.shares) / totalShares;
        user.shares = 0;
        totalShares = totalShares - user.shares;

        user.lastUserActionTime = block.timestamp;
        tranferLandByExchangeAddress(address(this), msg.sender, currentAmount);

        emit Withdraw(msg.sender, currentAmount, user.shares);
    }

    /**
     * @notice Custom logic for how much the vault allows to be borrowed
     * @dev The contract puts 100% of the tokens to work.
     */
    function available() public view returns (uint256) {
        return landToken.balanceOf(address(this));
    }

    function tranferLandByExchangeAddress(
        address fromAddress_,
        address toAddress_,
        uint256 amount_
    ) internal {
        // ㄤ€?
        if (fromAddress_ == address(this)) {
            landToken.safeTransfer(exchangeAddress, amount_);
        } else {
            landToken.safeTransferFrom(fromAddress_, exchangeAddress, amount_);
        }

        landToken.safeTransferFrom(exchangeAddress, toAddress_, amount_); // €api
    }

    function getToken1Value(address addr) public view returns (uint256) {
        return ILandStakeOnAutoLand(landLpStakeAddress).getToken1Value(addr);
    }

    function getParent(address addr) public view returns (address) {
        return IUserParent2(landAddress).getParent(addr);
    }

    /**
     * ＄у€-€**0.35*(ㄦ€?＄?
     */

    function getOutLevelAward(address addr) public view returns (uint256) {
        UserInfo storage user = userInfo[addr];

        if (user.shares == 0) {
            return 0;
        }

        uint256 outLevelAward = ((block.number - user.lastLevelAwardBlock) *
            perBlockAward *
            awardsPercentTotal *
            user.shares) /
            totalShares /
            10000;

        if (outLevelAward > totalLevelAward) {
            return totalLevelAward;
        }

        return outLevelAward;
    }

    function getApy() external view returns (uint256) {
        if (balanceOf() == 0) {
            return 0;
        }
        return
            ((balanceOf() + (perBlockAward * 10252800)) * 1e12) / balanceOf();
    }

    /**
     * ㄤ＄ㄦ?
     */
    function balanceOf() public view returns (uint256) {
        return landToken.balanceOf(address(this)) - totalLevelAward; // €?
    }

    /**
     * @notice Checks if the msg.sender is a contract or a proxy
     */
    modifier notContract() {
        require(!_isContract(msg.sender), "contract not allowed");
        require(msg.sender == tx.origin, "proxy contract not allowed");
        _;
    }

    /**
     * @notice Checks if address is a contract
     * @dev It prevents contract from being targetted
     */
    function _isContract(address addr) internal view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(addr)
        }
        return size > 0;
    }
}

interface ILandStakeOnAutoLand {
    function getToken1Value(address addr) external view returns (uint256);
}
