contract AccessManager is AccessControl {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    modifier onlyAdmin() {
        require(
            isAdmin(_msgSender()),
            "AccessManager: caller has not admin role"
        );
        _;
    }

    modifier onlyMinter() {
        require(
            isMinter(_msgSender()),
            "AccessManager: caller has not minter role"
        );
        _;
    }

    modifier onlyPauser() {
        require(
            isPauser(_msgSender()),
            "AccessManager: caller has not pauser role"
        );
        _;
    }

    function _addAllRoles(address to) internal {
        _grantRole(DEFAULT_ADMIN_ROLE, to);
        _grantRole(ADMIN_ROLE, to);
        _grantRole(MINTER_ROLE, to);
        _grantRole(PAUSER_ROLE, to);
    }

    function _removeAllRoles(address to) internal {
        _revokeRole(DEFAULT_ADMIN_ROLE, to);
        _revokeRole(ADMIN_ROLE, to);
        _revokeRole(MINTER_ROLE, to);
        _revokeRole(PAUSER_ROLE, to);
    }

    function addAdmin(address admin) public {
        grantRole(ADMIN_ROLE, admin);
    }

    function removeAdmin(address admin) public {
        revokeRole(ADMIN_ROLE, admin);
    }

    function isAdmin(address admin) public view returns (bool) {
        return hasRole(ADMIN_ROLE, admin);
    }

    function addMinter(address minter) public {
        grantRole(MINTER_ROLE, minter);
    }

    function removeMinter(address minter) public {
        revokeRole(MINTER_ROLE, minter);
    }

    function isMinter(address minter) public view returns (bool) {
        return hasRole(MINTER_ROLE, minter);
    }

    function addPauser(address pauser) public {
        grantRole(PAUSER_ROLE, pauser);
    }

    function removePauser(address pauser) public {
        revokeRole(PAUSER_ROLE, pauser);
    }

    function isPauser(address pauser) public view returns (bool) {
        return hasRole(PAUSER_ROLE, pauser);
    }
}

