abstract contract Bitanica is Ownable, IERC20, AccessManager, Pausable {
    string internal _name;
    string internal _symbol;
    uint8 internal _decimals;
    uint256 private _totalSupply;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint8 internal _feePercent; // MAX VALUE 10000
    address internal _feeAddress;
    uint256 public constant MAX_PERCENT = uint256(10000);

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function calculateFeeAmount(
        uint256 amount
    ) internal view returns (uint256 feeAmount) {
        feeAmount = (amount * _feePercent) / MAX_PERCENT;
    }

    function transferOwnership(address newOwner) public override onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );

        _removeAllRoles(_msgSender());
        _addAllRoles(newOwner);

        _transferOwnership(newOwner);
    }

    function setName(string memory name) public onlyAdmin {
        _name = name;
    }

    function setSymbol(string memory symbol) public onlyAdmin {
        _symbol = symbol;
    }

    function mint(address to, uint256 amount) public onlyMinter {
        _mint(to, amount);
    }

    function burn(address from, uint256 amount) public onlyMinter {
        _burn(from, amount);
    }

    function pause() public onlyPauser {
        _pause();
    }

    function unpause() public onlyPauser {
        _unpause();
    }

    function transfer(
        address to,
        uint256 amount
    ) public override whenNotPaused returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, amount);
        return true;
    }

    function approve(
        address spender,
        uint256 amount
    ) public override whenNotPaused returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public override whenNotPaused returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function increaseAllowance(
        address spender,
        uint256 addedValue
    ) public whenNotPaused returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, _allowances[owner][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(
        address spender,
        uint256 subtractedValue
    ) public whenNotPaused returns (bool) {
        address owner = _msgSender();
        uint256 currentAllowance = _allowances[owner][spender];
        require(
            currentAllowance >= subtractedValue,
            "Bitanica: decreased allowance below zero"
        );
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }
        return true;
    }

    function _transfer(address from, address to, uint256 amount) internal {
        require(
            from != address(0),
            "Bitanica: transfer from the zero address"
        );
        require(to != address(0), "Bitanica: transfer to the zero address");

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "Bitanica: transfer amount exceeds balance"
        );

        uint256 fee = calculateFeeAmount(amount);
        uint256 amountWithFee = 0;
        unchecked {
            amountWithFee = amount - fee;
            _balances[from] = fromBalance - amountWithFee;
        }
        _balances[to] += amountWithFee;
        _balances[_feeAddress] += fee;

        emit Transfer(from, to, amountWithFee);
        emit Transfer(from, _feeAddress, fee);
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "Bitanica: mint to the zero address");

        _totalSupply += amount;
        unchecked {
            _balances[account] += amount;
        }
        emit Transfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal {
        require(
            account != address(0),
            "Bitanica: burn from the zero address"
        );

        uint256 accountBalance = _balances[account];
        require(
            accountBalance >= amount,
            "Bitanica: burn amount exceeds balance"
        );
        unchecked {
            _balances[account] = accountBalance - amount;
        }
        _totalSupply -= amount;

        emit Transfer(account, address(0), amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(
            owner != address(0),
            "Bitanica: approve from the zero address"
        );
        require(
            spender != address(0),
            "Bitanica: approve to the zero address"
        );

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(
                currentAllowance >= amount,
                "Bitanica: insufficient allowance"
            );
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function setFeeTo(address feeTo) external onlyAdmin {
        _feeAddress = feeTo;
    }

    function feeTo() public view returns (address) {
        return _feeAddress;
    }

    function feePercent() public view returns (uint8) {
        return _feePercent;
    }

    function setFeePercent(uint8 feePercent) external onlyAdmin {
        require(feePercent <= MAX_PERCENT, "Bitanica: invalid percent");
        _feePercent = feePercent;
    }
}

library Strings {
