    "contracts/lib/ICHIVaultDeployer.sol": {
      "content": "// SPDX-License-Identifier: BUSL-1.1
pragma solidity 0.7.6;

import {ICHIVault} from '../ICHIVault.sol';
import {IAlgebraFactory} from '@cryptoalgebra/v1-core/contracts/interfaces/IAlgebraFactory.sol';

library ICHIVaultDeployer {

    function createICHIVault(
        address pool, 
        address token0,
        bool allowToken0,
        address token1,
        bool allowToken1,
        uint32 twapPeriod,
        uint256 vaultIndex
    ) public returns(address ichiVault) {

        ichiVault = address(
            new ICHIVault{salt: keccak256(abi.encodePacked(
                msg.sender, 
                token0, 
                allowToken0, 
                token1, 
                allowToken1)
            )}
                (pool, 
                allowToken0, 
                allowToken1, 
                msg.sender, 
                twapPeriod,
                vaultIndex)
        );
    }
}"
