    "contracts/AutoPilot.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.11;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract AutoPilot is IERC20, Ownable {
    address private _owner;
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;
    string private _name;
    string private _symbol;
    uint8 private _decimals;
    uint256 private _totalSupply;
    uint256 public tradingStartTime;
    bool public tradingEnabled = false;
    uint256 public maxTokensPerWallet = 0; // Maximum tokens per wallet
    uint256 public transactionCount = 0; // Count of transactions

    constructor(uint256 initialSupply) {
        _name = "AutoPilot"; // Set the token name
        _symbol = "AUP"; // Set the token symbol
        _decimals = 18; // Set the number of decimals
        tradingStartTime = block.timestamp + 120; // Set the trading start time to 600 seconds (10 minutes) from deployment
        _totalSupply = initialSupply; // Convert the initial supply to the appropriate decimal format
        _balances[_msgSender()] = _totalSupply; // Mint the initial supply to the owner
        _owner = _msgSender();
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

function transfer(address recipient, uint256 amount) public override returns (bool) {
    require(tradingEnabled || msg.sender == owner(), "Trading is not allowed yet.");

    // Adjust the maximum tokens per wallet based on the number of transactions
    if (transactionCount < 100) {
        require(amount <= maxTokensPerWallet / 2, "Exceeds maximum tokens per wallet limit.");
    } else if (transactionCount < 200) {
        require(amount <= (maxTokensPerWallet * 3 / 4), "Exceeds maximum tokens per wallet limit.");
    } else if (transactionCount < 300) {
        require(amount <= (maxTokensPerWallet * 1), "Exceeds maximum tokens per wallet limit.");
    } else if (transactionCount >= 400) {
        // No limit (unlimited trading)
    }

    _transfer(_msgSender(), recipient, amount);
    transactionCount++;
    return true;
}

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        require(tradingEnabled, "Trading is not allowed yet.");
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()] - amount);
        transactionCount++;
        return true;
    }

    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "Transfer from the zero address");
        require(recipient != address(0), "Transfer to the zero address");
        _balances[sender] -= amount;
        _balances[recipient] += amount;
        emit Transfer(sender, recipient, amount);
    }

    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0), "Approve from the zero address");
        require(spender != address(0), "Approve to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function enableTrading() public {
        require(!tradingEnabled, "Trading has already been enabled.");
        require(block.timestamp >= tradingStartTime, "Trading time has not arrived yet.");
        tradingEnabled = true;
    }

    function setMaxTokensPerWallet(uint256 newMaxTokens) public onlyOwner {
        maxTokensPerWallet = newMaxTokens;
    }

}"
