abstract contract AdminAccess is Context {
    address private _admin;

    event AdminshipTransferred(
        address indexed previousAdmin,
        address indexed newAdmin
    );

    constructor() {
        _transferAdminship(_msgSender());
    }

    modifier onlyAdmin() {
        _checkAdmin();
        _;
    }

    function Admin() public view virtual returns (address) {
        return _admin;
    }

    function _checkAdmin() internal view virtual {
        require(
            Admin() == _msgSender(),
            "AdminAccess: caller is not the Admin"
        );
    }

    function renounceAdminship() public virtual onlyAdmin {
        _transferAdminship(address(0));
    }

    function transferAdminship(address newAdmin)
        public
        virtual
        onlyAdmin
    {
        require(
            newAdmin != address(0),
            "AdminAccess: new Admin is the zero address"
        );
        _transferAdminship(newAdmin);
    }

    function _transferAdminship(address newAdmin) internal virtual {
        address oldAdmin = _admin;
        _admin = newAdmin;
        emit AdminshipTransferred(oldAdmin, newAdmin);
    }
}
