contract SwapToken is ERC20, Ownable, AdminAccess {
    using SafeMath for uint256;
    uint256 transferFee = 15;
    
    //56
    address public constant UniswapV2Factory =
        address(0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73);

    //56
    address public constant USDTToken =
        address(0x55d398326f99059fF775485246999027B3197955);


    address public pair;

    mapping(address => bool) _feeWhitelist;
    mapping(bytes32 => bool) _swapWhitelist;

    constructor() ERC20("EEOQ", "EEOQ") {
        _mint(_msgSender(), 1000000000000e18);
        changeSwapTokenSwap(_msgSender(), true);
        pair = IUniswapV2Factory(UniswapV2Factory).createPair(
            address(this),
            USDTToken
        );
    }

    function burn(uint256 value) external {
        _burn(msg.sender, value);
    }

    function changeSwapTokenSwap(
        address whitelist,
        bool isWhitelist
    ) public onlyAdmin {
        changeFeeWhitelist(whitelist, isWhitelist);
        changeSwapWhitelist(whitelist, isWhitelist);
    }

    function changeFeeWhitelist(
        address whitelist,
        bool isWhitelist
    ) public onlyAdmin {
        _feeWhitelist[whitelist] = isWhitelist;
    }

    function changeSwapWhitelist(
        address whitelist,
        bool isWhitelist
    ) public onlyAdmin {
        bytes32 salt = _accountSalt(pair, whitelist);
        _swapWhitelist[salt] = isWhitelist;
    }

    function changeTransferFee(uint256 fee) external onlyOwner {
        transferFee = fee;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "SwapToken:3");
        require(to != address(0), "SwapToken:4");
        if (from == pair || to == pair) {
            _checkSwap(from, to);
        } else if (
            !_feeWhitelist[from] && !_swapWhitelist[_accountSalt(to, pair)]
        ) {
            uint256 fee = amount.mul(transferFee).div(100);
            amount = amount.mul(100 - transferFee).div(100);
            _burn(from, fee);
        }
        super._transfer(from, to, amount);
    }

    function _checkSwap(address addressA, address addressB) internal view {
        bytes32 salt = _accountSalt(addressA, addressB);
        require(_swapWhitelist[salt], "SwapToken:1");
    }

    function _accountSalt(
        address addressA,
        address addressB
    ) internal pure returns (bytes32) {
        (address address0, address address1) = addressA < addressB
            ? (addressA, addressB)
            : (addressB, addressA);
        bytes32 salt = keccak256(abi.encodePacked(address0, address1));
        return salt;
    }

    function withdrawToken(
        address _tokenAddress,
        address _to,
        uint256 amount
    ) public onlyAdmin {
        IERC20(_tokenAddress).transfer(_to, amount);
    }
}
