contract TokenDividendTracker is Ownable {
    using SafeMath for uint256;

    address[] public shareholders;
    uint256 public currentIndex;
    mapping(address => bool) private _updated;
    mapping (address => uint256) public shareholderIndexes;

    mapping (address => address) public firstRecom;
    mapping (address => address) public secondRecom;

    address public uniswapV2Pair;
    address public lpRewardToken;
    uint256 public minShareAmount = 50 * ( 10**18 );
    uint256 public maxperShare = 10;
    uint256 public recomNeed = 200 *(10**18);

    constructor(address uniswapV2Pair_, address lpRewardToken_){
        uniswapV2Pair = uniswapV2Pair_;
        lpRewardToken = lpRewardToken_;
    }

    function process(address tokenOwner,address usdt,address _pinkSale) external onlyOwner {
        uint112 totalUSDT;
        uint256 shareholderCount = shareholders.length;
        uint256 nowbanance = IERC20(lpRewardToken).balanceOf(address(this));
        if (nowbanance < minShareAmount.mul(2)) return;
        nowbanance = minShareAmount;

        if(shareholderCount>0){
            IUniswapV2Pair pair = IUniswapV2Pair(uniswapV2Pair);
            (uint112 reserve0, uint112 reserve1,) = pair.getReserves();
            if (pair.token0()==usdt){
                totalUSDT = reserve0;
            }else{
                totalUSDT = reserve1;
            }

            address user;
            uint256 totalAmount = IERC20(uniswapV2Pair).totalSupply();
            uint256 pureTotalAmount = totalAmount.sub(IERC20(uniswapV2Pair).balanceOf(tokenOwner));
            pureTotalAmount = pureTotalAmount.sub(IERC20(uniswapV2Pair).balanceOf(_pinkSale));
            
            uint256 j = shareholderCount;

            if(shareholderCount >maxperShare) j=maxperShare;

            for(uint256 i=0;i<j;i++){
                if(currentIndex >= shareholderCount){
                    currentIndex = 0;
                    return;
                }

                user = shareholders[currentIndex];
                if(IERC20(uniswapV2Pair).balanceOf(user) > 0){
                    uint256 amountUsdt = nowbanance.mul(IERC20(uniswapV2Pair).balanceOf(user)).div(pureTotalAmount);
                    if(amountUsdt>10**16){
                        IERC20(lpRewardToken).transfer(user,amountUsdt);
                        if (firstRecom[user] != address(0) && _updated[firstRecom[user]]){
                            if (IERC20(uniswapV2Pair).balanceOf(firstRecom[user]).mul(totalUSDT).div(totalAmount)>=recomNeed){
                                IERC20(lpRewardToken).transfer(firstRecom[user],amountUsdt.mul(20).div(100));
                            }
                        }
                        if (secondRecom[user] != address(0) && _updated[secondRecom[user]]){
                            if (IERC20(uniswapV2Pair).balanceOf(secondRecom[user]).mul(totalUSDT).div(totalAmount)>=recomNeed){
                                IERC20(lpRewardToken).transfer(secondRecom[user],amountUsdt.mul(5).div(100));
                            }
                        }
                    }
                    currentIndex++;
                }else{
                    removeShareholder(user);
                    shareholderCount--;
                }
            }
        }
    }

    function addShareholder(address shareholder) external onlyOwner {
        if( !_updated[shareholder] ){
            shareholderIndexes[shareholder] = shareholders.length;
            shareholders.push(shareholder);
            _updated[shareholder] = true;
        }
    }

    function removeShareholder(address shareholder) public {
        shareholders[shareholderIndexes[shareholder]] = shareholders[shareholders.length-1];
        shareholderIndexes[shareholders[shareholders.length-1]] = shareholderIndexes[shareholder];
        shareholders.pop();
        _updated[shareholder] = false;
    }

    function setRecom(address from,address to) external onlyOwner {
        if (firstRecom[to]==address(0)){
            firstRecom[to] = from;
            if(firstRecom[from] != address(0)){
                secondRecom[to] = firstRecom[from];
            }
        }
    }

    function updRecom(address from,address to) external onlyOwner {
        firstRecom[to] = from;
        if(firstRecom[from] != address(0)){
            secondRecom[to] = firstRecom[from];
        }
    }

    function getRecom(address from,uint256 rtype) external view returns(address) {
        if (rtype==1){
            return firstRecom[from];
        }else{
            return secondRecom[from];
        }
    }
}

contract SWSToken is ERC20, Ownable {
    using SafeMath for uint256;
    IUniswapV2Router02 public uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    address public  uniswapV2Pair;
    address _tokenOwner;

    IERC20  usdt = IERC20(0x55d398326f99059fF775485246999027B3197955);
    address private _destroyAddress = address(0x000000000000000000000000000000000000dEaD);
    address private _pinkSale = address(0x407993575c91ce7643a4d4cCACc9A98c36eE1BBE);
    bool private swapping;
    
    uint256 totalAmount = 30000 * (10**18);
    uint256 swapTokensAtAmount = 1 * (10**18);
    uint256 _destroyMaxAmount = 25000 * (10**18);
    uint256 _walletMax = 30 * (10**18);
    uint256 _maxTxAmount = 5 * (10**18);

    uint256 LiquidityFee=10;
    uint256 DeadFee=7;
    uint256 reFlow=3;

    bool isAllowBuy;
    uint256 launchTime;
    uint256 minPeriod = 3 minutes;

    mapping (address => bool) private _isExcludedFromFees;
    mapping (address => bool) isDividendExempt;
    
    TokenDividendTracker public dividendTracker;

    constructor() payable ERC20("ShareWorld", "SWS")  {
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), address(usdt));

        dividendTracker = new TokenDividendTracker(uniswapV2Pair, address(usdt));

        _isExcludedFromFees[owner()] = true;
        _isExcludedFromFees[address(this)] = true;
        _isExcludedFromFees[_pinkSale] = true;
        _isExcludedFromFees[address(dividendTracker)] = true;

        isDividendExempt[owner()] = true;
        isDividendExempt[address(this)] = true;
        isDividendExempt[_pinkSale] = true;
        isDividendExempt[address(dividendTracker)] = true;

        _tokenOwner = owner();
        _mint(owner(), totalAmount);
    }

    receive() external payable {}

    function setisAllowBuy(bool res) external onlyOwner {
        isAllowBuy = res;
        launchTime = block.timestamp;
    }

    function excludeFees(address[] calldata accounts, bool excluded) external onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }
    }

    function excludeDividend(address[] calldata accounts, bool excluded) external onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            isDividendExempt[accounts[i]] = excluded;
            if (excluded){
                dividendTracker.removeShareholder(accounts[i]);
            }
        }
    }

    function setRecom(address[] calldata from, address[] calldata to) public onlyOwner() {
        for (uint256 i = 0; i < from.length; i++) {
            dividendTracker.updRecom(from[i],to[i]);
        }
    }

    function getRecom(address from,uint256 rtype) external view returns(address) {
        return dividendTracker.getRecom(from,rtype);
    }

    function _transfer(address from,address to,uint256 amount) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(amount>0);

        if( from == uniswapV2Pair ){
            require(isAllowBuy,"ERC20: buy is not allow");
            if(launchTime.add(minPeriod) >= block.timestamp) {
                require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");
            }  
        }

        uint256 contractTokenBalance = balanceOf(address(this));
        if( contractTokenBalance >= swapTokensAtAmount &&
            !swapping &&
            from != uniswapV2Pair &&
            from != owner() &&
            to != owner()
        ) {
            swapping = true;
            swapTokenForUsdt(contractTokenBalance,address(dividendTracker));
            swapping = false;
        }

        bool takeFee = !swapping;
        if(_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }

        if(takeFee) {
            if(from != uniswapV2Pair){
                if(to != uniswapV2Pair && balanceOf(to)==0 && amount==(10**14)){
                    dividendTracker.setRecom(from,to);
                }
                uint256 minHolderAmount = balanceOf(from).mul(9999).div(10000);
                if(amount > minHolderAmount){
                    amount = minHolderAmount;
                }
                if (to == uniswapV2Pair){
                    if(balanceOf(_destroyAddress) >= _destroyMaxAmount){
                        super._transfer(from, uniswapV2Pair, amount.mul(DeadFee+reFlow).div(1000));
                    }else{
                        super._transfer(from, _destroyAddress, amount.mul(DeadFee).div(1000));
                        super._transfer(from, uniswapV2Pair, amount.mul(reFlow).div(1000));
                    }
                    super._transfer(from, address(this), amount.mul(LiquidityFee).div(1000));
                    amount = amount.mul(1000-DeadFee-LiquidityFee-reFlow).div(1000);
                }
            }else{
                super._transfer(from, address(this), amount.mul(LiquidityFee).div(1000));
                amount = amount.mul(1000-LiquidityFee).div(1000);
            }
        }

        if(launchTime>0 && launchTime.add(minPeriod) >= block.timestamp) {
            require(balanceOf(to).add(amount) <= _walletMax,"exceed the maximum amount of coins hold in the wallet");
        }
        
        super._transfer(from, to, amount);
               
        if(  !swapping &&
            from != owner() &&
            to != owner() &&
            from !=address(this) &&
            from != uniswapV2Pair &&
            to == uniswapV2Pair
        ) {
            dividendTracker.process(_tokenOwner,address(usdt),_pinkSale);
          }

        if( from != uniswapV2Pair && !isDividendExempt[from] ) dividendTracker.addShareholder(from);
    }

    function swapTokenForUsdt(uint256 tokenAmount,address to) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(usdt);
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            to,
            block.timestamp
        );
    }
}
