pragma solidity 0.8.11;

interface ISender {
    event SetOperator(
        address indexed sender_,
        address indexed oldOperator_,
        address indexed newOperator_,
        uint256 timestamp_
    );

    event Withdraw(
        address indexed user_,
        address indexed token_,
        uint256 timestamp_
    );

    event Sent(
        address indexed token_,
        address[] target_,
        uint256[] amounts_,
        uint256 timestamp_
    );

    function operator() external view returns (address);

    function setOperator(address newOperator_) external;

    function withdraw(address user_, address token_) external;

    function send(address token_, address[] memory target_, uint256[] memory amounts_) external;
