    "contracts/Sender.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.11;

// solhint-disable not-rely-on-time, max-states-count

// inheritance
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./interface/ISender.sol";

// libs
import "@openzeppelin/contracts-upgradeable/utils/math/SafeMathUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

// interfaces
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";


contract Sender is ReentrancyGuard, Ownable, ISender {
    using SafeMathUpgradeable for uint256;

    address public override operator;

    constructor(address operator_) public {
        operator = operator_;
    }

    modifier onlyOperator() {
        require(msg.sender == operator, "operator: wut?");
        _;
    }

    function setOperator(address newOperator_) external onlyOwner {
        emit SetOperator({
        sender_ : msg.sender,
        oldOperator_ : operator,
        newOperator_ : newOperator_,
        timestamp_ : block.timestamp
        });

        operator = newOperator_;
    }

    function withdraw(address user_, address token_) external override nonReentrant onlyOwner {
        uint256 balance;
        balance = IERC20(token_).balanceOf(address(this));
        IERC20(token_).transfer(user_, balance);
        emit Withdraw({
        user_ : user_,
        token_ : token_,
        timestamp_ : block.timestamp
        });
    }

    function send(address token_, address[] memory target_, uint256[] memory amounts_) external override nonReentrant onlyOperator {
        require(target_.length == amounts_.length, "the target size and amounts size not equals");
        for (uint256 _idx = 0; _idx < target_.length; _idx++) {
            IERC20(token_).transfer(target_[_idx], amounts_[_idx]);
        }
        emit Sent({
        token_ : token_,
        target_ : target_,
        amounts_ : amounts_,
        timestamp_ : block.timestamp
        });
    }
}"
