pragma solidity ^0.5.16;

// ----------------------------------------------------------------------------
// ERC Token Standard #20 Interface
// By Khamphet Soumphonephakdy
// ----------------------------------------------------------------------------

contract ERC20Interface {
    function totalSupply() public view returns (uint);
    function balanceOf(address tokenOwner) public view returns (uint balance);
    function allowance(address tokenOwner, address spender) public view returns (uint remaining);
    function transfer(address to, uint tokens) public returns (bool success);
    function approve(address spender, uint tokens) public returns (bool success);
    function transferFrom(address from, address to, uint tokens) public returns (bool success);

    event Transfer(address indexed from, address indexed to, uint tokens);
    event Approval(address indexed tokenOwner, address indexed spender, uint tokens);
    event Burn(address indexed from, uint tokens); 
    event Airdrop(address indexed from, address[] indexed recipients, uint[] tokens); 
}

// ----------------------------------------------------------------------------
// Safe Math Library
// ----------------------------------------------------------------------------

contract SafeMath {
    function safeAdd(uint a, uint b) public pure returns (uint c) {
        c = a + b;
        require(c >= a);
    }

    function safeSub(uint a, uint b) public pure returns (uint c) {
        require(b <= a);
        c = a - b;
    }

    function safeMul(uint a, uint b) public pure returns (uint c) {
        c = a * b;
        require(a == 0 || c / a == b);
    }

    function safeDiv(uint a, uint b) public pure returns (uint c) {
        require(b > 0);
        c = a / b;
    }
}

contract SomkoylabCoin is ERC20Interface, SafeMath {
    string public name;
    string public symbol;
    uint8 public decimals;

    uint256 public _totalSupply;

    mapping(address => uint) balances;
    mapping(address => mapping(address => uint)) allowed;

    // Controller of the contract (the owner)
    address public controller;

    modifier onlyController() {
        require(msg.sender == controller, "Caller is not the controller");
        _;
    }

    /**
     * Constructor function
     *
     * Initializes the contract with initial supply tokens to the creator
     */
    constructor() public {
        name = "SomkoyCoin";
        symbol = "SKYC";
        decimals = 18;
        _totalSupply = 250000000000000000000000000;

        // Set the controller as the creator's address
        controller = msg.sender;

        balances[controller] = _totalSupply;
        emit Transfer(address(0), controller, _totalSupply);
    }

    function totalSupply() public view returns (uint) {
        return _totalSupply - balances[address(0)];
    }

    function balanceOf(address tokenOwner) public view returns (uint balance) {
        return balances[tokenOwner];
    }

    function allowance(address tokenOwner, address spender) public view returns (uint remaining) {
        return allowed[tokenOwner][spender];
    }

    function approve(address spender, uint tokens) public returns (bool success) {
        allowed[msg.sender][spender] = tokens;
        emit Approval(msg.sender, spender, tokens);
        return true;
    }

    function transfer(address to, uint tokens) public returns (bool success) {
        balances[msg.sender] = safeSub(balances[msg.sender], tokens);
        balances[to] = safeAdd(balances[to], tokens);
        emit Transfer(msg.sender, to, tokens);
        return true;
    }

    function transferFrom(address from, address to, uint tokens) public returns (bool success) {
        balances[from] = safeSub(balances[from], tokens);
        allowed[from][msg.sender] = safeSub(allowed[from][msg.sender], tokens);
        balances[to] = safeAdd(balances[to], tokens);
        emit Transfer(from, to, tokens);
        return true;
    }

    /**
     * Burn tokens
     */
    function burn(uint tokens) public onlyController {
        require(tokens <= balances[controller], "Insufficient balance");
        balances[controller] = safeSub(balances[controller], tokens);
        _totalSupply = safeSub(_totalSupply, tokens);
        emit Burn(controller, tokens);
    }

    /**
     * Airdrop tokens to the community
     */
    function airdrop(address[] memory recipients, uint[] memory tokens) public onlyController {
        require(recipients.length == tokens.length, "Arrays length mismatch");
        
        for (uint i = 0; i < recipients.length; i++) {
            address recipient = recipients[i];
            uint amount = tokens[i];

            require(recipient != address(0), "Invalid recipient address");
            require(amount > 0, "Invalid token amount");

            balances[controller] = safeSub(balances[controller], amount);
            balances[recipient] = safeAdd(balances[recipient], amount);

            emit Transfer(controller, recipient, amount);
        }

        emit Airdrop(controller, recipients, tokens);
    }
}
