      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

contract Stake2Earn is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    uint256 public constant year = 360 days;

    struct StageDetail {
        uint256 maxStake;
        uint256 totalStake;
        uint256 apy;
        uint256 bonusPool;
        uint256 remainingPool;
        uint256 startTime;
        uint256 endTime;
    }

    struct StakeDetail {
        uint256 lastStakeOrClaimTime;
        uint256 stakeAmount;
        uint256 reward;
    }

    mapping(uint256 => StageDetail) public timeStages;
    mapping(address => mapping(uint256 => StakeDetail)) public stakes;
    uint256 public immutable min;
    uint256 public immutable max;
    IERC20 public immutable SCT;

    event Stake(address user, uint256 stage, uint256 amount);
    event Claim(address user, uint256 stage, uint256 reward);
    event UnStake(address user, uint256 stage, uint256 amount, uint256 reward);

    constructor(uint256 min_, uint256 max_, uint256 startTime_, IERC20 SCT_) {
        min = min_;
        max = max_;
        SCT = SCT_;

        timeStages[1] = StageDetail(
            10000000 * 10**18, 0, 30, 0, 0, startTime_, startTime_+30 days
        );

        uint256 bonusPool1 = timeStages[1].maxStake * timeStages[1].apy / 1200;
        timeStages[1].bonusPool = bonusPool1;
        timeStages[1].remainingPool = bonusPool1;

        timeStages[2] = StageDetail(
            20000000 * 10**18, 0, 40, 0, 0, startTime_, startTime_+90 days
        );

        uint256 bonusPool2 = timeStages[2].maxStake * timeStages[2].apy / 400;
        timeStages[2].bonusPool = bonusPool2;
        timeStages[2].remainingPool = bonusPool2;

        timeStages[3] = StageDetail(
            20000000 * 10**18, 0, 50, 0, 0, startTime_, startTime_+180 days
        );

        uint256 bonusPool3 = timeStages[3].maxStake * timeStages[3].apy / 200;
        timeStages[3].bonusPool = bonusPool3;
        timeStages[3].remainingPool = bonusPool3;
    }

    function checkStageTimeAndAmount(uint256 stage_, uint256 amount_, address staker_) internal view {
        require(amount_ >= min && amount_ <= max, "invalid amount");

        StageDetail memory detail = timeStages[stage_];
        require(block.timestamp >= detail.startTime && block.timestamp < detail.endTime, "invalid stake time");
        require(detail.maxStake >= detail.totalStake + amount_, "reach max stage stake amount");

        StakeDetail memory userStake = stakes[staker_][stage_];
        require(userStake.stakeAmount + amount_ <= max, "reach max user stake amount");
    }

    function stake(uint256 stage_, uint256 amount_) external nonReentrant whenNotPaused {
        checkStageTimeAndAmount(stage_, amount_, msg.sender);

        SCT.safeTransferFrom(msg.sender, address(this), amount_);

        StageDetail storage detail = timeStages[stage_];
        detail.totalStake += amount_;

        StakeDetail storage userStake = stakes[msg.sender][stage_];

        // cal reward
        userStake.reward = queryReward(msg.sender, stage_);

        userStake.lastStakeOrClaimTime = block.timestamp;
        userStake.stakeAmount += amount_;

        emit Stake(msg.sender, stage_, amount_);
    }

    function claim(uint256 stage_) external nonReentrant whenNotPaused {
        StageDetail storage detail = timeStages[stage_];
        StakeDetail storage userStake = stakes[msg.sender][stage_];

        uint256 now = block.timestamp;
        if (now > detail.endTime) {
            now = detail.endTime;
        }

        require(userStake.stakeAmount != 0, "no stake detail");
        require(now > userStake.lastStakeOrClaimTime, "invalid claim cond");

        userStake.reward = queryReward(msg.sender, stage_);
        require(userStake.reward != 0, "no reward amount");

        require(detail.remainingPool >= userStake.reward, "not enough reward pool");
        detail.remainingPool -= userStake.reward;

        userStake.lastStakeOrClaimTime = now;
        SCT.safeTransfer(msg.sender, userStake.reward);
        emit Claim(msg.sender, stage_, userStake.reward);

        userStake.reward = 0;
    }

    function unStake(uint256 stage_) external nonReentrant whenNotPaused {
        StageDetail storage detail = timeStages[stage_];
        require(detail.endTime < block.timestamp, "not reach unstake time");

        StakeDetail storage userStake = stakes[msg.sender][stage_];
        require(userStake.stakeAmount != 0, "no stake detail");

        userStake.reward = queryReward(msg.sender, stage_);

        detail.totalStake -= userStake.stakeAmount;

        if (userStake.reward > 0) {
            require(detail.remainingPool >= userStake.reward, "not enough reward pool");
            detail.remainingPool -= userStake.reward;
        }

        uint256 total = userStake.stakeAmount + userStake.reward;
        SCT.safeTransfer(msg.sender, total);
        emit UnStake(msg.sender, stage_, userStake.stakeAmount, userStake.reward);

        userStake.reward = 0;
        userStake.stakeAmount = 0;
        userStake.lastStakeOrClaimTime = detail.endTime;
    }

    function pause() external onlyOwner whenNotPaused {
        _pause();
    }

    function unpause() external onlyOwner whenPaused {
        _unpause();
    }

    function release() external onlyOwner {
        SCT.safeTransfer(owner(), SCT.balanceOf(address(this)));
    }

    function queryReward(address user, uint256 stage) public view returns (uint256) {
        StakeDetail memory userStake = stakes[user][stage];
        if (userStake.stakeAmount == 0) return 0;

        StageDetail memory detail = timeStages[stage];
        uint256 now = block.timestamp;
        if (now > detail.endTime) now = detail.endTime;

        uint256 timeRange = now - userStake.lastStakeOrClaimTime;
        uint256 calReward = userStake.stakeAmount * detail.apy / 100 * timeRange / year;
        return userStake.reward + calReward;
    }
}"
