contract Vault is ReentrancyGuard{

    using SafeMath for uint256;
    address public nftcontract_;
    uint256 public nft_;
    event ReceivedERC721(address indexed from, uint256 tokenId);
    event ReceivedERC1155(address indexed from, uint256 id, uint256 value);

    constructor(address _nftcontract, uint256 _nft) {
        nft_ = _nft;
        nftcontract_ = _nftcontract;
    }

    receive () external payable {

    }
    function onERC1155Received(
        address,
        address from,
        uint256 id,
        uint256 value,
        bytes memory
    ) public virtual returns (bytes4) {
        emit ReceivedERC1155(from, id, value);
        return this.onERC1155Received.selector;
    }

    function onERC721Received(
        address,
        address from,
        uint256 tokenId,
        bytes memory
    ) public virtual returns (bytes4) {
        emit ReceivedERC721(from, tokenId);
        return this.onERC721Received.selector;
    }

    function WITHDRAWETH(address payable payto_,  uint256 value_) public nonReentrant returns(bool){

        require(msg.sender == nftcontract_, 'caller not approved');
        (bool success,) = payto_.call{value: value_}("");
        require(success, 'error withdrawing eth');
        return success;
    }

    function WITHDRAWTOKEN(address payable payto_, address token_, uint256 value_) public nonReentrant returns(bool){

        require(msg.sender == nftcontract_, 'caller not approved');
        require(IERC20(token_).balanceOf(address(this))>= value_ , 'balance not equal to value');
        (bool success) = IERC20(token_).transfer(payto_,value_);
        require(success, 'error withdrawing token');
        return success;
    }

    function WITHDRAWNFT(address nftContract_, uint256 nftout_, uint256 value_,address payto_) public nonReentrant returns(bool){

        require(msg.sender == nftcontract_, 'caller not approved');

      if (IERC165(nftContract_).supportsInterface(0xd9b67a26)) {

            require(IERC1155(nftContract_).balanceOf(address(this), nftout_) >= value_, "nft not in this wallet");
            IERC1155(nftContract_).safeTransferFrom(address(this), payto_, nftout_, value_, "");

      } else if (IERC165(nftContract_).supportsInterface(0x80ac58cd)) { // ERC721

            IERC721 nft = IERC721(nftContract_);
            require(nft.ownerOf(nftout_) == address(this), "nft not in this wallet");
            nft.safeTransferFrom(address(this), payto_, nftout_);
      }
        return true;
    }
}

contract BagFactory is CloneFactory {
    using SafeMath for uint256;

    event BagCreated(address bag, address indexed nftcontract, uint256 nft);

    function createBag(address nftcontract_, uint256 nft_) public returns (address) {
        Vault newBag = new Vault(nftcontract_, nft_);
        emit BagCreated(address(newBag), nftcontract_, nft_);
        return address(newBag);
    }
}


