contract AfricanHyenaPets is ERC721, Ownable {

  using Counters for Counters.Counter;
  using SafeMath for uint256;

  uint256 public constant TOTAL_SUPPLY = 10_000;
  //4 tribes + handlers and handler assets
  uint256 public CURRENT_SUPPLY = 2_200;
  // tribe 1
  uint256 public MINT_PRICE = 0.12 ether;
  uint256 public constant MAX_PRICE = 2 ether;
  address public VILLAGEVAULT;
  address public BAG;

  uint256 public petID;
  mapping(uint256=>address) public petToWallet;
  mapping(address=>uint256) public walletTopet;
  mapping(address=>bool) public isbattlecontract;
  mapping(address=>bool) private isAdmin;
  mapping(address=>address) public tellAfriend;
  mapping(uint256=>bool) public bnbLocked;
  mapping(uint256=>uint256) public bnbUnLockDate;
  mapping(uint256=>mapping(address=>bool)) public tokenLocked;
  mapping(uint256=>mapping(address=>uint256)) public tokenUnLockDate;
  mapping(uint256=>mapping(address=>mapping(uint256=>bool))) public nftLocked;
  mapping(uint256=>mapping(address=>mapping(uint256=>uint256))) public nftUnLockDate;
  mapping(address => uint256) public userGasUsed;
  mapping(uint256=>uint256) public nextPetReward;

  address public wallet = payable(0xDEa2BD4f31fC22892C42965d92d6FadAe4EDc75A);
  string public baseTokenURI;
  Counters.Counter private currentTokenId;

  constructor(string memory uri_) ERC721("African Hyena Pets", "AHP") {
    isAdmin[msg.sender] = true;
    isAdmin[wallet] = true;
    isAdmin[0x5c148A793C67E2933b437A876b2D1D29c4Cc86B6] = true;
    baseTokenURI = uri_;
  }
  event Minted(address owner, uint256 indexed pet, address petwallet);
  event ETHDeposited(uint256 indexed pet, uint256 value);
  event BNBLocked(uint256 indexed pet, uint256 unlockdate_);
  event TokensLocked(uint256 indexed pet, address token_, uint256 unlockdate_);
  event NFTLocked(uint256 indexed pet, address nftContract_, uint256 id_);
  event ETHWithdrawn(uint256 indexed pet, uint256 value);
  event TokensWithdrawn(uint256 indexed pet, address token_, uint256 value_);
  event NFTWithdrawn(uint256 indexed pet_, address nftContract_, uint256 id_, uint256 value_);
  event battleTokenPaid(address indexed battlecontract, uint256 wpet_, uint256 lpet_, address token_, uint256 value_);
  event friendAdded(address indexed referrer, address friend);
  event friendBonusPaid(address indexed friend, address referrer, uint256 bonus);
  event battleContractSet(address indexed contract_, bool value_);

  function mintTo(address recipient, uint256 quantity_) public payable {

    uint256 tokenId = currentTokenId.current();
    require(tokenId <= CURRENT_SUPPLY && quantity_>0, "error quantity/supply");
    if(!isAdmin[msg.sender]){
      require(msg.value >= MINT_PRICE.mul(quantity_), "price error");
    }
    uint256 gasStart = gasleft();

    for (uint256 i = 0; i < quantity_; i++) {

      currentTokenId.increment();
      uint256 newItemId = currentTokenId.current();
      _safeMint(recipient, newItemId);

      BagFactory bagFactory = new BagFactory();
      address bag_ = bagFactory.createBag(address(this),newItemId);
      walletTopet[bag_] = newItemId;
      petToWallet[newItemId] = bag_;

    if(!isAdmin[msg.sender]){
      if(newItemId%6==0 && MINT_PRICE < MAX_PRICE){
        uint256 bump = 0.0015 ether;
        MINT_PRICE = MINT_PRICE.add(bump);
      }
    }
      petID = newItemId;
      emit Minted(msg.sender, newItemId, bag_);
    }

    if(!isAdmin[msg.sender]){

      uint256 value_ = msg.value;
      bool success = false;
      if(tellAfriend[msg.sender]!=address(0)){
          uint256 reward_ = value_.mul(20).div(100);
          value_ = value_.sub(reward_);
          (success,) = tellAfriend[msg.sender].call{value: reward_}("");
          require(success, "reward failed");
      }
      (success,) = wallet.call{value: value_}("");
      require(success, "pay failed");

    }

    uint256 gasUsed = gasStart - gasleft();
    userGasUsed[msg.sender] += gasUsed;
  }
    function lockBNB(uint256 pet_, uint256 unlockdate_) public{

        require(IERC721(address(this)).ownerOf(pet_) == msg.sender, 'you do not own this pet');
        uint256 gasStart = gasleft();

        address bag_ = petToWallet[pet_];
        require(address(bag_).balance > 0, "No BNB to lock");
        require(unlockdate_ % 1000 != 0, "timestamp must be in seconds");
        require(unlockdate_ > block.timestamp, "Invalid unlock date");
        require(bnbLocked[pet_] == false, "BNB already locked");
        bnbLocked[pet_] = true;
        bnbUnLockDate[pet_] = unlockdate_;
        uint256 gasUsed = gasStart - gasleft();
        userGasUsed[msg.sender] += gasUsed;

        emit BNBLocked(pet_, unlockdate_);

    }
    function lockToken(uint256 pet_, uint256 unlockdate_, address token_) public{

        require(IERC721(address(this)).ownerOf(pet_) == msg.sender, 'you do not own this pet');
        uint256 gasStart = gasleft();

        address bag_ = petToWallet[pet_];
        uint256 tokenBalance = IERC20(token_).balanceOf(bag_);
        require(tokenBalance > 0, "No tokens to lock");
        require(unlockdate_ % 1000 != 0, "Timestamp must be in seconds");
        require(unlockdate_ > block.timestamp, "Invalid unlock date");
        require(tokenLocked[pet_][token_] == false, "Tokens already locked");
        tokenLocked[pet_][token_] = true;
        tokenUnLockDate[pet_][token_] = unlockdate_;
        uint256 gasUsed = gasStart - gasleft();
        userGasUsed[msg.sender] += gasUsed;

        emit TokensLocked(pet_, token_, unlockdate_);
    }

    function lockNFT(address nftContract_, uint256 id_, uint256 pet_, uint256 unlockdate_) public {
        require(IERC721(address(this)).ownerOf(pet_) == msg.sender, 'you do not own this pet');
        require(unlockdate_ % 1000 != 0, "Timestamp must be in seconds");
        require(unlockdate_ > block.timestamp, "Invalid unlock date");
        uint256 gasStart = gasleft();

        address bag_ = petToWallet[pet_];

        if (IERC165(nftContract_).supportsInterface(0xd9b67a26)) { // ERC1155
            require(IERC1155(nftContract_).balanceOf(bag_, id_) > 0, "nft not in bag");
            require(!nftLocked[pet_][nftContract_][id_], "NFT already locked");

        } else if (IERC165(nftContract_).supportsInterface(0x80ac58cd)) { // ERC721
            require(IERC721(nftContract_).ownerOf(id_) == bag_, "You do not own this token");
            require(!nftLocked[pet_][nftContract_][id_], "NFT already locked");

        } else {
            revert("Unsupported NFT type");
        }

        nftLocked[pet_][nftContract_][id_] = true;
        nftUnLockDate[pet_][nftContract_][id_] = unlockdate_;

        uint256 gasUsed = gasStart - gasleft();
        userGasUsed[msg.sender] += gasUsed;
        emit NFTLocked(pet_, nftContract_, id_);
    }
    function withdrawBNB(uint256 value_, uint256 pet_) public {

        require(IERC721(address(this)).ownerOf(pet_)==msg.sender, 'you do not own this pet');
        address  bag_ = petToWallet[pet_];
        require(bag_ != address(0), "Bag not created for pet");
        require(bag_.balance >= value_, "Insufficient balance");
        require(block.timestamp > bnbUnLockDate[pet_], "BNB locked");
        (bool success) = i1155(petToWallet[pet_]).WITHDRAWETH(msg.sender,value_);
        require(success, "BNB transfer failed");
        bnbLocked[pet_] = false;
        bnbUnLockDate[pet_] = 0;
        emit ETHWithdrawn(pet_,value_);

    }

  function withdrawToken(uint256 pet_, address token_, uint256 value_) public{
    address bag_;
    if(pet_==0){
        require(isAdmin[msg.sender], 'not an admin');
        bag_ = VILLAGEVAULT;
    }else{
        require(IERC721(address(this)).ownerOf(pet_)==msg.sender, 'you do not own this pet');
        bag_ = petToWallet[pet_];
    }
    require(bag_ != address(0), "Bag not created for pet");
    require(IERC20(token_).balanceOf(bag_) >= value_, "Insufficient balance");
    require(block.timestamp > tokenUnLockDate[pet_][token_], "Tokens locked");

    (bool success) = i1155(bag_).WITHDRAWTOKEN(msg.sender,token_,value_);
    require(success, 'error withdrawing token');

  }
  function withdrawNFT(uint256 pet_, address nftContract_, uint256 nftout_, uint256 value_) public{
        address bag_;
        if(pet_==0){
            require(isAdmin[msg.sender], 'not an admin');
            bag_ = VILLAGEVAULT;
        }else{
            require(IERC721(address(this)).ownerOf(pet_)==msg.sender, 'you do not own this pet');
            bag_ = petToWallet[pet_];
        }
        require(bag_ != address(0), "Bag not created for pet");
        require(block.timestamp > nftUnLockDate[pet_][nftContract_][nftout_], "NFT locked");

      if (IERC165(nftContract_).supportsInterface(0xd9b67a26)) {

        require(IERC1155(nftContract_).balanceOf(bag_, nftout_) >= value_, "nft not in bag");

      } else if (IERC165(nftContract_).supportsInterface(0x80ac58cd)) { // ERC721

          require(IERC721(nftContract_).ownerOf(nftout_) == bag_, "nft not in bag");

      } else {

          revert("Unsupported NFT type");
      }

    (bool success) = i1155(bag_).WITHDRAWNFT(nftContract_, nftout_, value_, msg.sender);
    require(success, 'error withdrawing token');
  }

  function battlePayToken(uint256 wpet_, uint256 lpet_, address lholder_, address token_, uint256 value_) public{

    require(isbattlecontract[msg.sender],'not approved');
    require(IERC721(address(this)).ownerOf(lpet_)==lholder_, 'loser does not own this pet');
    address lbag_ = petToWallet[lpet_];
    address wbag_ = petToWallet[wpet_];
    require(lbag_ != address(0), "Bag not created for losing pet");
    require(wbag_ != address(0), "Bag not created for winning pet");

    require(IERC20(token_).balanceOf(lbag_) >= value_, "Insufficient balance");

    (bool success) = i1155(lbag_).WITHDRAWTOKEN(wbag_,token_,value_);
    require(success, "Token transfer failed");
    emit battleTokenPaid(msg.sender,wpet_, lpet_, token_, value_);

  }

  function gasRefund(uint256 pet_) public {
    address payto = payable(msg.sender);
    uint256 value = userGasUsed[msg.sender];

    if(pet_>0){

        require(IERC721(address(this)).ownerOf(pet_)==msg.sender, 'you do not own this pet');
        require(petToWallet[pet_] != address(0), "Bag not created for pet");
        require(block.timestamp + 24 hours > nextPetReward[pet_] || nextPetReward[pet_] ==0, "pet reward not ready");
        payto = payable(petToWallet[pet_]);
        nextPetReward[pet_] = block.timestamp + 24 hours;
        value = 0.02 ether;
        bnbLocked[pet_] = true;
        bnbUnLockDate[pet_] = block.timestamp + 90 days;

    }else{
        require(value >0, 'not allowned');
    }
    require(VILLAGEVAULT.balance >= value, "vault low on BNB");
    (bool success) = i1155(VILLAGEVAULT).WITHDRAWETH(payto,value);
    require(success, "gas refund failed");
    if(pet_<1){
        userGasUsed[msg.sender] = 0;
    }
  }

  function getPets(address holder_) public view returns (uint256[] memory, address[] memory, uint256[] memory, bool[] memory, uint256[] memory, string[] memory) {
      uint256[] memory pets = getMyPets(holder_);
      address[] memory bags = new address[](pets.length);
      bool[] memory bnblocked = new bool[](pets.length);
      uint256[] memory bnbunlockdate = new uint256[](pets.length);
      uint256[] memory balance = new uint256[](pets.length);
      string[] memory uri = new string[](pets.length);
      for (uint256 i = 0; i < pets.length; ++i) {
          bags[i] = petToWallet[pets[i]];
          balance[i] = petToWallet[pets[i]].balance;
          bnblocked[i] = bnbLocked[pets[i]];
          bnbunlockdate[i] = bnbUnLockDate[pets[i]];
          uri[i] = tokenURI(pets[i]);
      }
      return (pets, bags, balance, bnblocked, bnbunlockdate, uri);
  }

  function getPET(uint256 pet_) public view returns(address,address,uint256,bool,uint256,string memory){
    string memory uri = tokenURI(pet_);
    address owner_ = ownerOf(pet_);
    uint256 balance_ = petToWallet[pet_].balance;
    return(owner_,petToWallet[pet_],balance_,bnbLocked[pet_],bnbUnLockDate[pet_],uri);
  }

  function setVillageVault() public{
      require(isAdmin[msg.sender], "not allowed");
      require(VILLAGEVAULT == address(0), "vault already set");
      BagFactory bagFactory = new BagFactory();
      address bag_ = bagFactory.createBag(address(this),0);
      VILLAGEVAULT = bag_;
  }

