      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
// import "hardhat/console.sol";
contract Vault is Ownable {
    event DepositBNB (address indexed from, uint qty);
    event WithdrawBNB (address indexed to, uint qty);
    event Deposit (address indexed token, address indexed from,  uint qty);
    event Withdraw (address indexed token, address indexed to, uint qty);

    event WithdrawTo (address indexed token, address indexed to, uint qty, uint fee, uint qtyAfterFee);

    address public feeTo;

    mapping(address => uint) public historyFee;

    constructor(address _feeTo) {
        feeTo = _feeTo;
    }

    function changeFeeTo(address _feeTo) public onlyOwner {
        feeTo = _feeTo;
    }

    function deposit(address _token, uint _qty) public {
        require(IERC20(_token).transferFrom(_msgSender(), address(this), _qty), 'Vault: Fail to deposit');
        emit Deposit(_token, _msgSender(), _qty);
    }

    function transfer(address _token, address _to, uint _originQty, uint _feePercent) public onlyOwner {
        (uint _fee, uint _qtyAfterfee) = _calcFee(_originQty, _feePercent);

        emit WithdrawTo(_token, _to, _originQty, _fee, _qtyAfterfee);

        _payOutToken(_token, _to, _qtyAfterfee);

        _payOutToken(_token, feeTo, _fee);

        historyFee[_token] += _fee;
    }

    function mutiTransfer(address _token, address[] calldata _to, uint[] calldata _originQty, uint _feePercent) public onlyOwner {
        require(_to.length == _originQty.length, "Vault: Array Set Error");
        uint _count = _to.length;
        uint _totalFee = 0;
        for (uint i = 0; i < _count; i++) {
            // 5 * 10**3 / 10**3 / 10**2
            // 5 % = 5000
            (uint _fee, uint _qtyAfterfee) = _calcFee(_originQty[i], _feePercent);

            emit WithdrawTo(_token, _to[i], _originQty[i], _fee, _qtyAfterfee);
            // to User
            _payOutToken(_token, _to[i], _qtyAfterfee);
            // to system Fee
            _payOutToken(_token, feeTo, _fee);

            _totalFee += _fee;
        }

        historyFee[_token] += _totalFee;
    }

    function withdrawBNB(uint _qty) public onlyOwner {
        (bool send, ) = payable(_msgSender()).call{ value: _qty }("");
        require(send, "Vault: Fail to withdraw");
        emit WithdrawBNB(_msgSender(), _qty);
    }

    function withdraw(address _token, uint _qty) public onlyOwner {
        _payOutToken(_token, _msgSender(), _qty);

        emit Withdraw(_token, _msgSender(), _qty);
    }

    function _payOutToken(address _token, address _to, uint _qty) internal {
        require(IERC20(_token).transfer(_to, _qty), 'Vault: Insufficient Balance');
    }

    function _calcFee(uint _originQty, uint _feePercent) internal pure returns (uint, uint) {
        uint _fee = _originQty * _feePercent / 10 ** 5;

        uint _qtyAfterfee = _originQty - _fee;

        return (_fee, _qtyAfterfee);
    }

    receive() external payable {
        if (msg.value > 0) {
            emit DepositBNB(_msgSender(), msg.value);
        }
    }
    fallback() external payable {}
}
