// File contracts/TEO.sol

contract TEO is ERC20, Ownable {
    using SafeERC20 for IERC20;
    using SafeMathT for uint256;

    event NewTax(
        address sender,
        address recipient,
        uint256 amountAfterTax,
        uint256 tax
    );
    event NewTaxDetail(
        address sender,
        address recipient,
        uint256 tax,
        uint256 taxType
    );
    event NormalTransfer(
        address pool,
        address sender,
        address recipient,
        uint256 amount
    );
    event NewRefund(uint256 random, address lastSender, uint256 refund);

    uint256 internal constant MAX_SUPPLY = 21 * 10 ** 12 * 10 ** 18;
    uint256 internal constant MAX_HOLDING = 100;
    uint256 internal constant DENOMINATOR = 10 ** 18;
    uint24 internal constant FEE = 10000;

    address internal constant usdtAddr =
        0x55d398326f99059fF775485246999027B3197955;
    address internal constant blackHole =
        0x000000000000000000000000000000000000dEaD;
    address internal constant factoryAddr =
        0xdB1d10011AD0Ff90774D0C6Bb92e5C5c8b4461F7;
    address internal constant positionManagerAddr =
        0x7b8A01B39D58278b5DE7e48c8449c9f4F5170613;
    address internal constant quoter1 =
        0x78D78E420Da98ad378D7799bE8f4AF69033EB077;
    address internal constant quoter2 =
        0x78D78E420Da98ad378D7799bE8f4AF69033EB077;

    INonfungiblePositionManager public positionManager;
    ISwapRouter public swapRouter;
    address private boss;
    address public shareToken;
    address public achievementToken;
    address public uniswapV3Pool;
    uint256 public lpTokenId;
    mapping(address => address) public referrals;
    //9
    mapping(address => uint256) public referralAchievement;
    mapping(address => uint256) public referralAchievement2;

    mapping(address => mapping(address => bool)) private _pendingReferrals;
    mapping(address => bool) private _isConfirmed;
    mapping(address => bool) private _hasBought;
    uint256 public startTime;

    uint256 public totalMiningFactor;
    uint256 public totalDivPointsHolder;
    uint256[5] public totalDivPointsAngel;
    mapping(address => uint256) public lastMiningFactor;
    mapping(address => uint256) public lastDivPointsHolder;
    mapping(address => mapping(uint256 => uint256)) public lastDivPointsAngel;

    uint256 public lastDistributionTime;
    uint256 public accumulatedMining;
    uint256 public accumulatedHolder;
    uint256[5] public accumulatedAngel;

    uint256 internal tradeDelta = 30 days;
    uint256 internal maxHoldDelta = 30 days;
    uint256 internal angelRewardRatio = 5;
    uint256[5] public angelLevelCount;

    //9
    mapping(address => uint256) public depositUSDTHistory;

    constructor(address _boss, address _firstPlayer) ERC20('TEO', 'TEO') {
        boss = _boss;
        startTime = block.timestamp;

        positionManager = INonfungiblePositionManager(positionManagerAddr);
        _mint(address(this), MAX_SUPPLY);
        _approve(address(this), positionManagerAddr, MAX_SUPPLY);

        referrals[_firstPlayer] = _boss;
        referrals[quoter1] = _boss;
        referrals[quoter2] = _boss;
        referrals[blackHole] = _boss;
        referrals[_boss] = _boss;
        referrals[address(this)] = _boss;
    }

    function init(address _shareToken, address _achievementToken) public {
        require(owner() == msg.sender, '!init');
        shareToken = _shareToken;
        achievementToken = _achievementToken;
        uint160 sqrtPriceX96;
        int24 tickLower;
        int24 tickUpper;

        (address token0, address token1) = _sortTokens(address(this), usdtAddr);
        uint256 amount0Desired;
        uint256 amount1Desired;
        if (token0 == address(this)) {
            amount0Desired = MAX_SUPPLY;
            amount1Desired = 0;
            //price 1e-9;
            sqrtPriceX96 = 2505414483750479311864138;
            tickLower = -207200;
            tickUpper = 887200;
        } else {
            amount0Desired = 0;
            amount1Desired = MAX_SUPPLY;
            //price 1e9;
            sqrtPriceX96 = 2505414483750479311864138015696100;
            tickLower = -887200;
            tickUpper = 207200;
        }

        address poolv3 = positionManager.createAndInitializePoolIfNecessary(
            token0,
            token1,
            FEE,
            sqrtPriceX96
        );
        uniswapV3Pool = poolv3;
        referrals[uniswapV3Pool] = boss;

        //1
        INonfungiblePositionManager.MintParams
            memory params = INonfungiblePositionManager.MintParams({
                token0: token0,
                token1: token1,
                fee: FEE,
                tickLower: tickLower,
                tickUpper: tickUpper,
                amount0Desired: amount0Desired,
                amount1Desired: amount1Desired,
                amount0Min: (amount0Desired * 99) / 100,
                amount1Min: (amount1Desired * 99) / 100,
                recipient: address(this),
                deadline: block.timestamp + 600
            });
        (lpTokenId, , , ) = positionManager.mint(params);
        renounceOwnership();
    }

    function _sortTokens(
        address tokenA,
        address tokenB
    ) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, '!IDENTICAL_ADDRESSES');
        (token0, token1) = tokenA < tokenB
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        require(token0 != address(0), '!ZERO_ADDRESS');
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (account == uniswapV3Pool || account == address(this))
            return super.balanceOf(account);
        else return _ableMining(account); //4
    }

    //4
    function _ableMining(address account) internal view returns (uint256) {
        (, uint factor) = _ableMiningDistribution();
        return
            super.balanceOf(account).mul(DENOMINATOR.add(factor)).div(
                DENOMINATOR.add(lastMiningFactor[account])
            );
    }

    //8
    function _getHolderDividends(
        address account
    ) internal view returns (uint256) {
        return totalDivPointsHolder.sub(lastDivPointsHolder[account]);
    }

    //9
    //level from 0
    function _getAngelDividends(
        address account,
        uint256 level
    ) internal view returns (uint256) {
        return
            totalDivPointsAngel[level].sub(lastDivPointsAngel[account][level]);
    }

    function _processTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        _updateDistribution();
        _updateAccount(sender);
        if (sender != recipient) _updateAccount(recipient);
        if (amount > 0) {
            uint256 estimateUSDT = _estimateUSDT(amount);
            if (sender == uniswapV3Pool) {
                //6
                if (block.timestamp < startTime.add(tradeDelta)) {
                    require(referrals[recipient] != address(0), '!referred');
                }
                //9
                uint256 oldLevel = _getAngelLevel(recipient);
                depositUSDTHistory[recipient] = depositUSDTHistory[recipient]
                    .add(estimateUSDT);
                referralAchievement[referrals[recipient]] = referralAchievement[
                    referrals[recipient]
                ].add(estimateUSDT);
                referralAchievement2[
                    referrals[referrals[recipient]]
                ] = referralAchievement2[referrals[referrals[recipient]]].add(
                    estimateUSDT
                );
                uint256 newLevel = _getAngelLevel(recipient);
                if (newLevel > 0) {
                    if (newLevel > oldLevel) {
                        for (uint256 l = newLevel; l > oldLevel; l--) {
                            angelLevelCount[l - 1]++;
                        }
                    }
                }

                if (!_isBigHolder(recipient)) {
                    uint256 tax = amount.mul(12).div(100);
                    uint256 amountAfterTax = amount.sub(tax);
                    _transfer(sender, recipient, amountAfterTax);
                    _transfer(sender, address(this), tax);
                    _distributeTax(recipient, tax);
                    emit NewTax(sender, recipient, amountAfterTax, tax);
                } else {
                    _transfer(sender, recipient, amount);
                }
            } else if (recipient == uniswapV3Pool && sender != address(this)) {
                //9
                uint256 oldLevel = _getAngelLevel(sender);
                depositUSDTHistory[sender] = depositUSDTHistory[sender].sub0(
                    estimateUSDT
                );
                referralAchievement[referrals[recipient]] = referralAchievement[
                    referrals[recipient]
                ].sub0(estimateUSDT);
                referralAchievement2[
                    referrals[referrals[recipient]]
                ] = referralAchievement2[referrals[referrals[recipient]]].sub0(
                    estimateUSDT
                );
                uint256 newLevel = _getAngelLevel(sender);
                if (oldLevel > 0) {
                    if (newLevel < oldLevel) {
                        for (uint256 l = oldLevel; l > newLevel; l--) {
                            angelLevelCount[l - 1]--;
                        }
                    }
                }

                _transfer(sender, recipient, amount);
                if (!_isBigHolder(sender)) {
                    uint256 tax = amount.mul(12).div(88);
                    _transfer(sender, address(this), tax);
                    _distributeTax(sender, tax);
                    emit NewTax(sender, recipient, amount, tax);
                }
            } else {
                _transfer(sender, recipient, amount);
                //5
                if (sender != address(this)) {
                    if (referrals[sender] == address(0)) {
                        if (_pendingReferrals[recipient][sender]) {
                            referrals[sender] = recipient;
                            _pendingReferrals[recipient][sender] = false;
                        }
                    } else if (
                        recipient != sender &&
                        !_pendingReferrals[recipient][sender] &&
                        !_pendingReferrals[sender][recipient] &&
                        referrals[recipient] == address(0) &&
                        referrals[sender] != recipient
                    ) {
                        _pendingReferrals[sender][recipient] = true;
                    }
                }
                emit NormalTransfer(uniswapV3Pool, sender, recipient, amount);
            }

            if (
                recipient != uniswapV3Pool &&
                recipient != boss &&
                recipient != address(this) &&
                recipient != blackHole &&
                !_isBigHolder(recipient) &&
                !_pendingReferrals[recipient][sender] &&
                block.timestamp < startTime.add(maxHoldDelta) //7
            ) {
                require(
                    balanceOf(recipient) <= totalSupply().div(MAX_HOLDING),
                    '!1%'
                );
            }
        }
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _processTransfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _processTransfer(sender, recipient, amount);
        _approve(sender, msg.sender, allowance(sender, msg.sender).sub(amount));
        return true;
    }

    function _updateAccount(address account) internal returns (bool) {
        if (account == uniswapV3Pool || account == address(this)) return false;

        uint256 owing = balanceOf(account).sub(super.balanceOf(account));
        if (owing > 0) _transfer(address(this), account, owing);
        if (lastMiningFactor[account] != totalMiningFactor)
            //4
            lastMiningFactor[account] = totalMiningFactor;
        return true;
    }

    //8
    function _updateAccountHolder(address account) internal returns (bool) {
        uint256 owing = _ableDividend(account);
        if (owing > 0) {
            _transfer(address(this), account, owing);
            if (lastDivPointsHolder[account] != totalDivPointsHolder)
                lastDivPointsHolder[account] = totalDivPointsHolder;
        }
        return true;
    }

    //9
    function _updateAccountAngel(address account) internal returns (bool) {
        (uint256 owing, , , , , ) = _ableAchievement(account);
        if (owing > 0) {
            _transfer(address(this), account, owing);
            for (uint256 i; i < 5; i++)
                if (lastDivPointsAngel[account][i] != totalDivPointsAngel[i])
                    lastDivPointsAngel[account][i] = totalDivPointsAngel[i];
        }
        return true;
    }

    //3
    function _distributeTax(address user, uint256 tax) private {
        uint256 toBlackHole = tax.mul(10).div(100);
        uint256 toHolder = tax.mul(10).div(100);
        uint256 toAngel = tax.mul(10).div(100);
        uint256 toRefs = tax.mul(5).div(100);
        uint256 toRefs2 = tax.mul(5).div(100);
        uint256 toMiningPool = tax.mul(60).div(100);

        if (tax > 0) {
            _transfer(address(this), blackHole, toBlackHole);
            emit NewTaxDetail(address(this), blackHole, toBlackHole, 1);

            if (referrals[user] != address(0)) {
                _transfer(address(this), referrals[user], toRefs);
                emit NewTaxDetail(user, referrals[user], toRefs, 2);
            } else {
                _transfer(address(this), boss, toRefs);
                emit NewTaxDetail(user, boss, toRefs, 2);
            }
            if (referrals[referrals[user]] != address(0)) {
                _transfer(address(this), referrals[referrals[user]], toRefs2);
                emit NewTaxDetail(user, referrals[referrals[user]], toRefs2, 3);
            } else {
                _transfer(address(this), boss, toRefs);
                emit NewTaxDetail(user, boss, toRefs, 3);
            }

            accumulatedMining = accumulatedMining.add(toMiningPool);
            accumulatedHolder = accumulatedHolder.add(toHolder);
            for (uint256 i; i < 5; i++) {
                accumulatedAngel[i] = accumulatedAngel[i].add(toAngel.div(5));
            }
        }
    }

    function _ableMiningDistribution()
        private
        view
        returns (uint rest, uint factor)
    {
        uint256 timeElapsed = block.timestamp.sub(lastDistributionTime);
        if (timeElapsed > 10 days) timeElapsed = 10 days;
        uint256 distribution = accumulatedMining
            .mul(10)
            .mul(timeElapsed)
            .div(86400)
            .div(100);
        rest = accumulatedMining.sub(distribution);
        uint256 totalDiv = _getTotalDivPointsDen();
        if (totalDiv > 0)
            factor = (DENOMINATOR.add(totalMiningFactor))
                .mul(totalDiv.add(distribution))
                .div(totalDiv)
                .sub(DENOMINATOR);
        else factor = totalMiningFactor;
    }

    function _updateDistribution() private {
        (accumulatedMining, totalMiningFactor) = _ableMiningDistribution(); //4
        _updateHolderDistribution(); //8
        _updateAngelDistribution(); //9
        lastDistributionTime = block.timestamp;
    }

    function _updateHolderDistribution() private {
        uint256 timeElapsed = block.timestamp.sub(lastDistributionTime);
        if (timeElapsed > 10 days) timeElapsed = 10 days;
        uint256 distribution = accumulatedHolder
            .mul(10)
            .mul(timeElapsed)
            .div(86400)
            .div(100);
        accumulatedHolder = accumulatedHolder.sub(distribution);
        totalDivPointsHolder = totalDivPointsHolder.add(
            distribution.mul(DENOMINATOR).div(IERC20(shareToken).totalSupply())
        );
    }

    function _updateAngelDistribution() private {
        uint256 timeElapsed = block.timestamp.sub(lastDistributionTime);
        uint256 maxDays = (100 * 86400) / angelRewardRatio;
        if (timeElapsed > maxDays) timeElapsed = maxDays;
        for (uint256 i; i < 5; i++) {
            uint256 distribution = accumulatedAngel[i]
                .mul(angelRewardRatio)
                .mul(timeElapsed)
                .div(86400)
                .div(100);
            uint256 totalDiv = angelLevelCount[i];
            accumulatedAngel[i] = accumulatedAngel[i].sub(distribution);
            if (totalDiv > 0) {
                totalDivPointsAngel[i] = totalDivPointsAngel[i].add(
                    distribution.mul(DENOMINATOR).div(totalDiv)
                );
            }
        }
    }

    function _getTotalDivPointsDen() private view returns (uint256) {
        return
            totalSupply().sub(super.balanceOf(address(this))).sub(
                super.balanceOf(uniswapV3Pool)
            );
    }

    function _collectFees() private {
        INonfungiblePositionManager.CollectParams
            memory params = INonfungiblePositionManager.CollectParams({
                tokenId: lpTokenId,
                recipient: boss,
                amount0Max: type(uint128).max,
                amount1Max: type(uint128).max
            });

        positionManager.collect(params);
    }

    //6
    function setTradeDelta(uint256 newDelta) public {
        require(_isBigHolder(msg.sender), '!big');
        tradeDelta = newDelta;
    }

    //7
    function setMaxHoldDelta(uint256 newDelta) public {
        require(_isBigHolder(msg.sender), '!big');
        maxHoldDelta = newDelta;
    }

    function _isBigHolder(
        address user
    ) internal view returns (bool isBigHolder) {
        isBigHolder =
            IERC20(shareToken).balanceOf(user).mul(5) >= //20%
            IERC20(shareToken).totalSupply();
    }

    function claimReward() public {
        claimRewardAccount(msg.sender);
    }

    function claimRewardAccount(address account) public {
        if (account == boss) {
            _collectFees();
        }
        _updateDistribution();
        _updateAccount(account);
        _updateAccountHolder(account);
        _updateAccountAngel(account);
    }

    //8
    function ableDividend(address user) public view returns (uint256) {
        return _ableDividend(user);
    }

    function _ableDividend(address user) internal view returns (uint256) {
        uint256 newDividends = _getHolderDividends(user);
        return
            (newDividends.mul(IERC20(shareToken).balanceOf(user))).div(
                DENOMINATOR
            );
    }

    //9
    function ableAchievement(address user) public view returns (uint256 able) {
        (able, , , , , ) = _ableAchievement(user);
    }

    function ableAchievement5(
        address user
    )
        public
        view
        returns (
            uint256 level1,
            uint256 level2,
            uint256 level3,
            uint256 level4,
            uint256 level5
        )
    {
        (, level1, level2, level3, level4, level5) = _ableAchievement(user);
    }

    function _ableAchievement(
        address user
    )
        internal
        view
        returns (uint256, uint256, uint256, uint256, uint256, uint256)
    {
        uint256 curLevel = _getAngelLevel(user);
        uint256 ableReward = 0;
        uint256 newDividends = 0;
        uint256[5] memory levelReward;
        for (uint256 i; i < curLevel; i++) {
            newDividends = _getAngelDividends(user, i);
            levelReward[i] = newDividends.div(DENOMINATOR);
            ableReward = ableReward.add(newDividends);
        }
        return (
            ableReward.div(DENOMINATOR),
            levelReward[0],
            levelReward[1],
            levelReward[2],
            levelReward[3],
            levelReward[4]
        );
    }

    //9
    //level from 1
    function _getAngelLevel(
        address user
    ) internal view returns (uint256 level) {
        uint256 angelBalance = _teoaBalanceOf(user);
        if (angelBalance < DENOMINATOR.mul(20000)) {
            level = 0;
        } else if (angelBalance < DENOMINATOR.mul(50000)) {
            level = 1;
        } else if (angelBalance < DENOMINATOR.mul(100000)) {
            level = 2;
        } else if (angelBalance < DENOMINATOR.mul(200000)) {
            level = 3;
        } else if (angelBalance < DENOMINATOR.mul(500000)) {
            level = 4;
        } else {
            level = 5;
        }
    }

    //9
    function _estimateUSDT(uint256 teoAmount) internal view returns (uint256) {
        (address token0, ) = _sortTokens(address(this), usdtAddr);
        (uint160 sqrtPriceX96, , , , , , ) = IUniswapV3Pool(uniswapV3Pool)
            .slot0();
        uint256 price = uint256(sqrtPriceX96).mul(sqrtPriceX96).div(2 ** 96);
        if (token0 != address(this)) {
            price = SafeMath.div((1 << 192), price);
        }
        teoAmount = teoAmount.mul(1000000).div(1000000 - 10000 - 5000); // FEE: 1% slippage: 0.5%
        return teoAmount.mul(price).div(2 ** 96);
    }

    //9
    function setAngelRewardRatio(uint256 ratio) public {
        require(_isBigHolder(msg.sender), '!big');
        angelRewardRatio = ratio;
    }

    //9
    fallback() external {
        claimReward();
    }

    //10
    function transferBatch(
        address[] memory _toList,
        uint256[] memory _amountList
    ) public {
        require(_toList.length == _amountList.length, '!length');
        for (uint256 i; i < _toList.length; i++) {
            _processTransfer(msg.sender, _toList[i], _amountList[i]);
        }
    }

    function teoaBalanceOf(address account) external view returns (uint256) {
        return _teoaBalanceOf(account);
    }

    function _teoaBalanceOf(address account) internal view returns (uint256) {
        return
            depositUSDTHistory[account].add(referralAchievement[account]).add(
                referralAchievement2[account].div(2)
            );
    }
}

