interface ITEO {
    function claimReward(address) external;

    function teoaBalanceOf(address) external view returns (uint256);
}

// File contracts/TEOA.sol

contract TEOA {
    string public constant name = 'TEO Achievement';
    string public constant symbol = 'TEOA';
    uint8 public constant decimals = 18;
    ITEO public immutable teoToken;

    constructor(address _TEO) {
        teoToken = ITEO(_TEO);
    }

    function balanceOf(address account) public view returns (uint256) {
        return teoToken.teoaBalanceOf(account);
    }
}

// File contracts/TEOS.sol

contract TEOS is ERC20, Ownable {
    using SafeMathT for uint256;

    ITEO public teoToken;

    constructor(address _owner, address _TEO) ERC20('TEO Share', 'TEOS') {
        _mint(_owner, 100 * 10 ** 18);
        teoToken = ITEO(_TEO);
        transferOwnership(_owner);
    }

    function _processTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal {
        _updateAccount(sender);
        _updateAccount(recipient);
        if (amount > 0) {
            _transfer(sender, recipient, amount);
        }
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _processTransfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _processTransfer(sender, recipient, amount);
        _approve(sender, msg.sender, allowance(sender, msg.sender).sub(amount));
        return true;
    }

    function _updateAccount(address account) internal returns (bool) {
        if (account == address(this)) {
            return false;
        }
        teoToken.claimReward(account);
        return true;
    }

    function mint(address recipient, uint256 amount) external {
        require(owner() == msg.sender, '!owner');
        _mint(recipient, amount);
    }
}
