library UniswapV2Library {

    // returns sorted token addresses, used to handle return values from pairs sorted in this order
    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, 'UniswapV2Library: IDENTICAL_ADDRESSES');
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'UniswapV2Library: ZERO_ADDRESS');
    }

    // calculates the CREATE2 address for a pair without making any external calls
    function pairFor(address factory, address tokenA, address tokenB) internal view returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        pair = IUniswapV2Factory(factory).getPair(token0, token1);
    }

    // fetches and sorts the reserves for a pair
    function getReserves(address factory, address tokenA, address tokenB) internal view returns (uint reserveA, uint reserveB) {
        (address token0,) = sortTokens(tokenA, tokenB);
        (uint reserve0, uint reserve1,) = IUniswapV2Pair(pairFor(factory, tokenA, tokenB)).getReserves();
        (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
    }

    // given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
    function quote(uint amountA, uint reserveA, uint reserveB) internal pure returns (uint amountB) {
        require(amountA > 0, 'UniswapV2Library: INSUFFICIENT_AMOUNT');
        require(reserveA > 0 && reserveB > 0, 'UniswapV2Library: INSUFFICIENT_LIQUIDITY');
        amountB = (amountA * reserveB) / reserveA;
    }
}

interface ILiquidusFeeEstimation {
    function userFee(address user) external view returns (uint256);
}

contract LPMigrator is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    ILiquidusFeeEstimation feeCalculateContract = ILiquidusFeeEstimation(0x31248f375263Eaa3B3DBdfcd8EAE60890360cD75);

    mapping(address => bool) public whitelistedRouter;

    address public feeAddress;


    function addWhitelistRouter(address router) public onlyOwner {
        whitelistedRouter[router] =  true;
    }

    function removeWhitelistRouter(address router) public onlyOwner {
        whitelistedRouter[router] =  false;
    }

    function getFee(address user) public view returns(uint256) {
        return feeCalculateContract.userFee(user);
    }

    function setFeeAddress(address newFeeAddress) public onlyOwner {
        require(newFeeAddress != address(0), "Address shouldn't be zero");
        feeAddress = newFeeAddress;
    }

    function setFeeEstimatingContract(address feeContractAddress) public onlyOwner {
        require(feeContractAddress != address(0), "Address shouldn't be zero");
        feeCalculateContract = ILiquidusFeeEstimation(feeContractAddress);
    }

    // msg.sender should have approved 'liquidity' amount of LP token of 'tokenA' and 'tokenB'
    function migrate(
        address oldRouter,
        address newRouter,
        address lpToken,
        address oldStable,
        address newStable,
        address stableRouter
    ) public nonReentrant{
        require(whitelistedRouter[oldRouter] && whitelistedRouter[newRouter], "Check router addresses");
    
        address tokenA = IUniswapV2Pair(lpToken).token0();
        address tokenB = IUniswapV2Pair(lpToken).token1();
        require(IUniswapV2Factory(IUniswapV2Router02(oldRouter).factory()).getPair(tokenA, tokenB) == lpToken, "Check oldRouter and lpToken address, missmatch");
        
        //Take fee
        IUniswapV2Pair(lpToken).transferFrom(msg.sender, feeAddress, IUniswapV2Pair(lpToken).balanceOf(msg.sender) * getFee(msg.sender) / 10000);

        // Remove liquidity from the old router with permit
        (uint256 amountA, uint256 amountB) = removeLiquidity(lpToken);

        // Check if need to swap stable coin
        if(oldStable != address(0) && newStable != address(0) && stableRouter != address(0)){
            uint256 amountOldStable = tokenA == oldStable ? amountA : amountB;

            address[] memory path = new address[](3);
            path[0] = oldStable;
            path[1] = IUniswapV2Router02(stableRouter).WETH();
            path[2] = newStable;

            IERC20(oldStable).approve(stableRouter, amountOldStable);
            IUniswapV2Router02(stableRouter).swapExactTokensForTokens(
                amountOldStable,
                0,
                path,
                address(this),
                block.timestamp);
            
            // Check slippage
            require(IERC20(newStable).balanceOf(address(this)) / IERC20(newStable).decimals() >= (amountOldStable * 90 / 100) / IERC20(oldStable).decimals(), "Slippage is too high");

            if(tokenA == oldStable){
                tokenA = newStable;
                amountA = IERC20(newStable).balanceOf(address(this));
            }else {
                tokenB = newStable;
                amountB = IERC20(newStable).balanceOf(address(this));
            }
        }

        // Add liquidity to the newRouter
        (uint256 pooledAmountA, uint256 pooledAmountB, ) = addLiquidity(
            newRouter,
            tokenA,
            tokenB,
            amountA,
            amountB,
            0,
            0,
            msg.sender
        );

        // Send remaining tokens to msg.sender
        if (amountA > pooledAmountA) {
            IERC20(tokenA).safeTransfer(msg.sender, amountA - pooledAmountA);
        }
        if (amountB > pooledAmountB) {
            IERC20(tokenB).safeTransfer(msg.sender, amountB - pooledAmountB);
        }
    }

    function removeLiquidity(
        address lpToken
    ) internal returns (uint256 amount0, uint256 amount1) {
        IUniswapV2Pair pair = IUniswapV2Pair(lpToken);
        pair.transferFrom(msg.sender, address(pair), pair.balanceOf(msg.sender));
        (amount0, amount1) = pair.burn(address(this));
    }

    function addLiquidity(
        address router,
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to
    ) internal
    returns (
        uint256 amountA,
        uint256 amountB,
        uint256 liquidity
    )
    {
        (amountA, amountB) = _addLiquidity(router, tokenA, tokenB, amountADesired, amountBDesired, amountAMin, amountBMin);
        address pair = IUniswapV2Factory(IUniswapV2Router02(router).factory()).getPair(tokenA, tokenB);
        IERC20(tokenA).safeTransfer(pair, amountA);
        IERC20(tokenB).safeTransfer(pair, amountB);
        liquidity = IUniswapV2Pair(pair).mint(to);
    }

    function _addLiquidity(
        address router,
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin
    ) internal returns (uint256 amountA, uint256 amountB) {
        // create the pair if it doesn't exist yet
        IUniswapV2Factory factory = IUniswapV2Factory(IUniswapV2Router02(router).factory());
        if (factory.getPair(tokenA, tokenB) == address(0)) {
            factory.createPair(tokenA, tokenB);
        }
        (uint256 reserveA, uint256 reserveB) = UniswapV2Library.getReserves(address(factory), tokenA, tokenB);
        if (reserveA == 0 && reserveB == 0) {
            (amountA, amountB) = (amountADesired, amountBDesired);
        } else {
            uint256 amountBOptimal = UniswapV2Library.quote(amountADesired, reserveA, reserveB);
            if (amountBOptimal <= amountBDesired) {
                require(amountBOptimal >= amountBMin, 'UniswapV2Router: INSUFFICIENT_B_AMOUNT');
                (amountA, amountB) = (amountADesired, amountBOptimal);
            } else {
                uint256 amountAOptimal = UniswapV2Library.quote(amountBDesired, reserveB, reserveA);
                assert(amountAOptimal <= amountADesired);
                require(amountAOptimal >= amountAMin, 'UniswapV2Router: INSUFFICIENT_A_AMOUNT');
                (amountA, amountB) = (amountAOptimal, amountBDesired);
            }
        }
    }
}
