      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract XMetaToken is ERC20 {

    ///////////////////////////////////////////////////
    //      address
    ///////////////////////////////////////////////////
    address public immutable playToEarnAddr;
    address public immutable pledgeRewardAddr;
    address public immutable ecologicalFundAddr;
    address public immutable coreTeamAddr;
    address public immutable communityOperationAddr;
    address public immutable publicsaleAddr;
    address public immutable privatePlacementAddr;

    uint256 public constant PLAY_TO_EARN_RATE = 30;
    uint256 public constant PLEDGE_REWARD_RATE = 15;
    uint256 public constant ECOLOGICAL_FUND_RATE = 9;
    uint256 public constant CORE_TEAM_RATE = 20;
    uint256 public constant COMMUNITY_OPERATION_RATE = 8;
    uint256 public constant PUBLIC_SALE_RATE = 10;
    uint256 public constant PRIVATE_PLACEMENT_RATE = 8;
    uint256 public constant MAX_CAPPED = 900000000 ether;
    uint256 public constant PERCENT_BASE = 100;

    constructor(address _playToEarnAddr, address _pledgeRewardAddr, address _ecologicalFundAddr, address _coreTeamAddr,
        address _communityOperationAddr, address _publicsaleAddr, address _privatePlacementAddr)
        ERC20("X-Metaverse", "XMETA") {
        playToEarnAddr = _playToEarnAddr;
        pledgeRewardAddr = _pledgeRewardAddr;
        ecologicalFundAddr = _ecologicalFundAddr;
        coreTeamAddr = _coreTeamAddr;
        communityOperationAddr = _communityOperationAddr;
        publicsaleAddr = _publicsaleAddr;
        privatePlacementAddr = _privatePlacementAddr;

        _mint(_playToEarnAddr, (MAX_CAPPED * PLAY_TO_EARN_RATE) / PERCENT_BASE);
        _mint(_pledgeRewardAddr, (MAX_CAPPED * PLEDGE_REWARD_RATE) / PERCENT_BASE);
        _mint(_ecologicalFundAddr, (MAX_CAPPED * ECOLOGICAL_FUND_RATE) / PERCENT_BASE);
        _mint(_coreTeamAddr, (MAX_CAPPED * CORE_TEAM_RATE) / PERCENT_BASE);
        _mint(_communityOperationAddr, (MAX_CAPPED * COMMUNITY_OPERATION_RATE) / PERCENT_BASE);
        _mint(_publicsaleAddr, (MAX_CAPPED * PUBLIC_SALE_RATE) / PERCENT_BASE);
        _mint(_privatePlacementAddr, (MAX_CAPPED * PRIVATE_PLACEMENT_RATE) / PERCENT_BASE);
    }

    function _mint(address account, uint256 amount) internal virtual override(ERC20) {
        super._mint(account, amount) ;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20) {
        super._beforeTokenTransfer(from, to, amount) ;
    }

}
