pragma solidity ^0.8.0;

contract LuckyCharms is ERC721Base, GallerLaunchpad, ReentrancyGuard {
	using Counters for Counters.Counter;
    using SafeMath for uint256;

    Counters.Counter private supply;

    string public provenance = "";
    string private baseURI;
    string private uriExtension = ".json";
    string private unrevealedURI;

    uint256 public immutable maxSupply;
    uint256 public maxMintPerTx = 50;
    uint256 public price = 0.2 ether;

    bool public mintLive = false;
    bool public whitelistActive = true;
    bool public revealed = false;
    mapping(address => bool) private whitelistedMap;

    constructor(
        string memory _name,
        string memory _symbol,
        uint256 _maxSupply,
        address _launchpad,
        uint256 _launchpadMaxSupply
    )
        ERC721(_name, _symbol)
        GallerLaunchpad(_launchpad, _launchpadMaxSupply) {maxSupply = _maxSupply;}


    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function mint(uint256 _amount) external payable {

    if (msg.sender != owner()) {
        if(whitelistActive == true) {
            require(isWhitelisted(msg.sender), "User is not whitelisted");
        }
        require(msg.value == price * _amount, "Insufficient funds");
    }
        _mintTo(msg.sender, _amount);
    }

    function mintFor(address _to, uint256 _amount) external payable {

    if (msg.sender != owner()) {
        if(whitelistActive == true) {
            require(isWhitelisted(_to), "User is not whitelisted");
        }
        require(msg.value == price * _amount, "Insufficient funds");
    }
        _mintTo(_to, _amount);
    }

    function isWhitelisted(address user) public view returns (bool) {
        return whitelistedMap[user];
    }

    function walletOfOwner(address _owner)
        public
        view
        returns (uint256[] memory)
    {
        uint256 ownerTokenCount = balanceOf(_owner);
        uint256[] memory tokenIds = new uint256[](ownerTokenCount);
        for (uint256 i; i < ownerTokenCount; i++) {
        tokenIds[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokenIds;
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override
        returns (string memory)
    {
        require(
        _exists(tokenId),
        "ERC721Metadata: URI query for nonexistent token"
        );
        
        if(revealed == false) {
            return unrevealedURI;
        }

        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, Strings.toString(tokenId), uriExtension))
            : "";
    }


    // only owner
    function setRevealed(bool _state) public onlyOwner {
        revealed = _state;
    }

    function setPrice(uint256 _price) external onlyOwner {
        require(_price > 0, "Invalid price");
        price = _price;
    }

    function setmaxMintPerTx(uint256 _maxMintPerTx) public onlyOwner {
        maxMintPerTx = _maxMintPerTx;
    }

    function setwhitelistActive(bool _whitelistActive) external onlyOwner {
        whitelistActive = _whitelistActive;
    }

    function whitelistUsers(address[] calldata _users) public onlyOwner {
        for (uint256 i = 0; i < _users.length; ++i) {
            whitelistedMap[_users[i]] = true;
        }
    }

    function setmintLive(bool _state) external onlyOwner {
        mintLive = _state;
    }

    function setProvenanceHash(string memory provenanceHash) external onlyOwner {
        provenance = provenanceHash;
    }

    function setBaseURI(string memory _newBaseURI) external onlyOwner {
        baseURI = _newBaseURI;
    }

    function seturiExtension(string memory _uriExtension) public onlyOwner {
        uriExtension = _uriExtension;
    }
    
    function setUnrevealedURI(string memory _unrevealedURI) public onlyOwner {
        unrevealedURI = _unrevealedURI;
    }

    function withdraw() public payable onlyOwner {
        (bool os, ) = payable(owner()).call{value: address(this).balance}("");
        require(os);
    }

    function _mintTo(address _to, uint256 _amount) internal override(GallerLaunchpad) nonReentrant {
        require(mintLive, "Minting is paused");
        require(_amount > 0 && _amount <= maxMintPerTx, "Invalid amount");
        require((totalSupply() + _amount) <= maxSupply, "Exceeds max supply");
        
        for (uint256 i = 0; i < _amount; ++i) {
            supply.increment();
            _safeMint(_to, supply.current());
        }
    }
}
