contract LongToken is BEP20('LT', 'LT') {
    address public marketing;
    address public reflow;
    address public uniswapV2Router;
    address public uniswapV2Pair;
    address public admin;
    bool    public feeTo;
    address public dead;
    address public syrup;
    mapping (address => bool) public whitelist;
    mapping (address => bool) public blacklist;
    uint256 public openBlock;

    constructor(address _router,address[] memory users)public {
        mint(users[0], 150000000e18);
        marketing = users[1];
        reflow = users[2];
        admin = msg.sender;
        uniswapV2Router = _router;
        dead = 0x000000000000000000000000000000000000dEaD;
        feeTo = true;
        whitelist[address(this)] = true;
        for (uint i=0; i<users.length; i++){
            whitelist[users[i]] = true;
        }
        uniswapV2Pair = IUniswapV2Factory(IUniswapV2Router(uniswapV2Router).factory()).createPair(
            address(this),
            IUniswapV2Router(uniswapV2Router).WETH()
        );
    }

    receive() external payable{}

    modifier onlyAdmin(){
        require(admin == msg.sender,"Caller is not owner");
        _;
    }

    function setAdmin(address _admin) external  onlyAdmin{
        admin = _admin;
    }

    function setFeeTo(bool _feeTo) external  onlyAdmin{
        feeTo = _feeTo;
    }

    function setReceiverAddress(address _marketing,address _reflow) external onlyAdmin{
        marketing = _marketing;
        reflow = _reflow;
    }

    function setSyrupAddress(address _syrup) external  onlyAdmin{
        syrup = _syrup;
    }

    /// @notice Creates `_amount` token to `_to`. Must only be called by the owner (MasterChef).
    function mint(address _to, uint256 _amount) public onlyOwner {
        _mint(_to, _amount);
        _moveDelegates(address(0), _delegates[_to], _amount);
    }

    // Copied and modified from YAM code:
    // https://github.com/yam-finance/yam-protocol/blob/master/contracts/token/YAMGovernanceStorage.sol
    // https://github.com/yam-finance/yam-protocol/blob/master/contracts/token/YAMGovernance.sol
    // Which is copied and modified from COMPOUND:
    // https://github.com/compound-finance/compound-protocol/blob/master/contracts/Governance/Comp.sol

    mapping (address => address) internal _delegates;

    /// @notice A checkpoint for marking number of votes from a given block
    struct Checkpoint {
        uint32 fromBlock;
        uint256 votes;
    }

    /// @notice A record of votes checkpoints for each account, by index
    mapping (address => mapping (uint32 => Checkpoint)) public checkpoints;

    /// @notice The number of checkpoints for each account
    mapping (address => uint32) public numCheckpoints;

    /// @notice The EIP-712 typehash for the contract's domain
    bytes32 public constant DOMAIN_TYPEHASH = keccak256("EIP712Domain(string name,uint256 chainId,address verifyingContract)");

    /// @notice The EIP-712 typehash for the delegation struct used by the contract
    bytes32 public constant DELEGATION_TYPEHASH = keccak256("Delegation(address delegatee,uint256 nonce,uint256 expiry)");

    /// @notice A record of states for signing / validating signatures
    mapping (address => uint) public nonces;

      /// @notice An event thats emitted when an account changes its delegate
    event DelegateChanged(address indexed delegator, address indexed fromDelegate, address indexed toDelegate);

    /// @notice An event thats emitted when a delegate account's vote balance changes
    event DelegateVotesChanged(address indexed delegate, uint previousBalance, uint newBalance);

    function delegates(address delegator)
        external
        view
        returns (address)
    {
        return _delegates[delegator];
    }


    function _transfer(address sender, address recipient, uint256 amount) internal virtual override {
        require(!blacklist[sender],"ERC20:Not allowed");
        if(openBlock == 0 && IBEP20(uniswapV2Pair).totalSupply() > 0) openBlock = block.number;
        uint256 fee;
        if (isTransaction(sender,recipient)){
            uint256 fee0 = amount * 2 / 100;
            uint256 fee1 = amount  * 1 / 100;
            if (sender == uniswapV2Pair){
                super._transfer(sender, marketing, fee0);
                super._transfer(sender, dead, fee0 + fee1);
                if(whetherSetBlacklist(recipient)) blacklist[recipient] = true;
            }else {
                super._transfer(sender, address(this), fee0 * 2);
                super._transfer(sender, dead, fee1);
            }
            fee = fee0 * 2 + fee1; 
        }
        super._transfer(sender, recipient, amount - fee);

         
        if (whetherReflow(sender,recipient)){
            uint256 onehalf = balanceOf(address(this)) * 75 / 100;
            _swapTokenForBNB(onehalf, address(this));
            uint256 half = onehalf / 75 * 25;
            uint256 balance = address(this).balance * 25 / 100;
            uint256 balanceToMarketing = balance / 25 * 50;
            payable(marketing).transfer(balanceToMarketing);
            _addLuidity(half,balance);
        }

    }

    function isTransaction(address sender, address recipient) internal view returns(bool isTx){
        bool pair = (sender == uniswapV2Pair) || (recipient == uniswapV2Pair);
        bool white = whitelist[sender] || whitelist[recipient];
        isTx = pair && !white && feeTo;
    }

    function whetherSetBlacklist(address recipient) internal view returns(bool add){
        add = openBlock + 30 > block.number && !whitelist[recipient] && recipient != uniswapV2Router;
    }

    function whetherReflow(address sender, address recipient) internal view returns(bool isReflow){
        bool pair = (sender == uniswapV2Pair) || (recipient == uniswapV2Pair);
        bool router = (sender == uniswapV2Router) || (recipient == uniswapV2Router);
        uint256 _toFlow = balanceOf(address(this));
        isReflow = !pair && !router && (_toFlow > 0) && feeTo && sender != syrup;
    }


    function _swapTokenForBNB(uint256 amount,address receiver)internal{
        _approve(address(this),uniswapV2Router, amount);
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = IUniswapV2Router(uniswapV2Router).WETH();
        IUniswapV2Router(uniswapV2Router).swapExactTokensForETHSupportingFeeOnTransferTokens(
            amount, 
            0, 
            path, 
            receiver, 
            block.timestamp + 10
        );
    }

    function _addLuidity(uint256 amount,uint256 balance) internal{

        _approve(address(this),uniswapV2Router, amount);
        IUniswapV2Router(uniswapV2Router).addLiquidityETH{value:balance}(
            address(this),
            amount,
            0,
            0,
            reflow,
            block.timestamp + 5
        );

    }

    function delegate(address delegatee) external {
        return _delegate(msg.sender, delegatee);
    }

    function delegateBySig(
        address delegatee,
        uint nonce,
        uint expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    )
        external
    {
        bytes32 domainSeparator = keccak256(
            abi.encode(
                DOMAIN_TYPEHASH,
                keccak256(bytes(name())),
                getChainId(),
                address(this)
            )
        );

        bytes32 structHash = keccak256(
            abi.encode(
                DELEGATION_TYPEHASH,
                delegatee,
                nonce,
                expiry
            )
        );

        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                domainSeparator,
                structHash
            )
        );

        address signatory = ecrecover(digest, v, r, s);
        require(signatory != address(0), "LONG::delegateBySig: invalid signature");
        require(nonce == nonces[signatory]++, "LONG::delegateBySig: invalid nonce");
        require(now <= expiry, "LONG::delegateBySig: signature expired");
        return _delegate(signatory, delegatee);
    }

    function getCurrentVotes(address account)
        external
        view
        returns (uint256)
    {
        uint32 nCheckpoints = numCheckpoints[account];
        return nCheckpoints > 0 ? checkpoints[account][nCheckpoints - 1].votes : 0;
    }

    function getPriorVotes(address account, uint blockNumber)
        external
        view
        returns (uint256)
    {
        require(blockNumber < block.number, "LONG::getPriorVotes: not yet determined");

        uint32 nCheckpoints = numCheckpoints[account];
        if (nCheckpoints == 0) {
            return 0;
        }

        // First check most recent balance
        if (checkpoints[account][nCheckpoints - 1].fromBlock <= blockNumber) {
            return checkpoints[account][nCheckpoints - 1].votes;
        }

        // Next check implicit zero balance
        if (checkpoints[account][0].fromBlock > blockNumber) {
            return 0;
        }

        uint32 lower = 0;
        uint32 upper = nCheckpoints - 1;
        while (upper > lower) {
            uint32 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
            Checkpoint memory cp = checkpoints[account][center];
            if (cp.fromBlock == blockNumber) {
                return cp.votes;
            } else if (cp.fromBlock < blockNumber) {
                lower = center;
            } else {
                upper = center - 1;
            }
        }
        return checkpoints[account][lower].votes;
    }

    function _delegate(address delegator, address delegatee)
        internal
    {
        address currentDelegate = _delegates[delegator];
        uint256 delegatorBalance = balanceOf(delegator); // balance of underlying CAKEs (not scaled);
        _delegates[delegator] = delegatee;

        emit DelegateChanged(delegator, currentDelegate, delegatee);

        _moveDelegates(currentDelegate, delegatee, delegatorBalance);
    }

    function _moveDelegates(address srcRep, address dstRep, uint256 amount) internal {
        if (srcRep != dstRep && amount > 0) {
            if (srcRep != address(0)) {
                // decrease old representative
                uint32 srcRepNum = numCheckpoints[srcRep];
                uint256 srcRepOld = srcRepNum > 0 ? checkpoints[srcRep][srcRepNum - 1].votes : 0;
                uint256 srcRepNew = srcRepOld.sub(amount);
                _writeCheckpoint(srcRep, srcRepNum, srcRepOld, srcRepNew);
            }

            if (dstRep != address(0)) {
                // increase new representative
                uint32 dstRepNum = numCheckpoints[dstRep];
                uint256 dstRepOld = dstRepNum > 0 ? checkpoints[dstRep][dstRepNum - 1].votes : 0;
                uint256 dstRepNew = dstRepOld.add(amount);
                _writeCheckpoint(dstRep, dstRepNum, dstRepOld, dstRepNew);
            }
        }
    }

    function _writeCheckpoint(
        address delegatee,
        uint32 nCheckpoints,
        uint256 oldVotes,
        uint256 newVotes
    )
        internal
    {
        uint32 blockNumber = safe32(block.number, "LONG::_writeCheckpoint: block number exceeds 32 bits");

        if (nCheckpoints > 0 && checkpoints[delegatee][nCheckpoints - 1].fromBlock == blockNumber) {
            checkpoints[delegatee][nCheckpoints - 1].votes = newVotes;
        } else {
            checkpoints[delegatee][nCheckpoints] = Checkpoint(blockNumber, newVotes);
            numCheckpoints[delegatee] = nCheckpoints + 1;
        }

        emit DelegateVotesChanged(delegatee, oldVotes, newVotes);
    }

    function safe32(uint n, string memory errorMessage) internal pure returns (uint32) {
        require(n < 2**32, errorMessage);
        return uint32(n);
    }

    function getChainId() internal pure returns (uint) {
        uint256 chainId;
        assembly { chainId := chainid() }
        return chainId;
    }
}

