pragma solidity ^0.8.0;

interface IPausable {
    function _setMintPaused(address target, bool _paused) external;
}

contract EmergencyPause {
    address public owner;

    modifier onlyOwner() {
        require(msg.sender == owner, "Not the contract owner");
        _;
    }

    constructor() {
        owner = msg.sender;
    }

    function emergencyPause(address pausableContract, address[] memory targets) external onlyOwner {
        for (uint256 i = 0; i < targets.length; i++) {
            IPausable(pausableContract)._setMintPaused(targets[i], true);
        }
    }

    function emergencyUnpause(address pausableContract, address[] memory targets) external onlyOwner {
        for (uint256 i = 0; i < targets.length; i++) {
            IPausable(pausableContract)._setMintPaused(targets[i], false);
        }
    }
}
