contract RhythmMigrator is ReentrancyGuard {
    using SafeMath for uint256;

    mapping(address => bool) private migrations;
    mapping(address => uint256) private whitelist;
    address private _owner;
    bool public withdrawEnabled;

    IERC20 private _v2;

    event Withdraw(address recipient, uint256 amount);
    event OwnerChange(address previousOwner, address newOwner);
    
    constructor(address v2) {
        _v2 = IERC20(v2);
        _owner = msg.sender;
        withdrawEnabled = false;
    }
    
    modifier onlyOwner() {
        require(_owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    function setTrading(bool value) public onlyOwner() {
        withdrawEnabled = value;
    }

    function isTradingEnabled() public view returns (bool) {
        return withdrawEnabled;
    }

    function setV2Contract(address v2) public onlyOwner() {
        _v2 = IERC20(v2);
    }

    function setOwner(address newOwner) public onlyOwner() {
        require(newOwner != _owner, 'Already owner');
        address oldOwner = _owner;
        _owner = newOwner;

        emit OwnerChange(oldOwner, newOwner);
    }

    // Ensure no team addresses / Unicrypt addresses / Exchange addresses are on the whitelist.
    // Any Hotbit / other Exchange holders will have to manually be added after LP has been migrated.
    function addToWhitelist(address[] memory addresses, uint256[] memory amounts) public onlyOwner() {
        require(addresses.length == amounts.length, "Length of addresses and amounts must match");
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelist[addresses[i]] = amounts[i];
        }
    }

    function isWhitelisted(address account) public view returns (bool) {
        return whitelist[account] > 0;
    }

    function getClaimableV2TokensStandard(address account) public view returns (uint256) {
        return whitelist[account];
    }

    function getClaimableV2Tokens(address account) public view returns (uint256) {
        return whitelist[account].mul(10**9);
    }

    function withdrawV2Tokens() nonReentrant public {
        require(migrations[msg.sender] == false, 'You have already claimed V2 $RHYTHM tokens.');
        require(isWhitelisted(msg.sender), 'Not whitelisted.');
        require(withdrawEnabled, "Rhythm: Trading is currently disabled. Please check our Telegram for more details.");
        
        uint256 v2TokenClaimable = whitelist[msg.sender].mul(10**9);

        migrations[msg.sender] = true;
        whitelist[msg.sender] = 0;
        
        _v2.transfer(msg.sender, v2TokenClaimable);
        
        emit Withdraw(msg.sender, v2TokenClaimable);
    }

    function hasClaimed(address account) public view returns (bool) {
        return migrations[account];
    }

    function adminWithdrawV2Tokens() public onlyOwner() {
        _v2.transfer(_owner, _v2.balanceOf(address(this)));
    }

    function getV2Contract() public view returns (IERC20) {
        return _v2;
    }

    function getOwner() public view returns (address) {
        return _owner;
    }
}
