pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

interface IUniswapV2Router01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountToken, uint amountETH);
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountToken, uint amountETH);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

interface IUniswapV2Router02 is IUniswapV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountETH);
    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

contract StackLiquidityDrive is Ownable {
    using SafeMath for uint256;

    // FAIR LAUNCH
    uint256 public totalBNBDonated;
    mapping (address => uint256) public _bnbDonated;
    mapping (address => uint256) public _claimed;
    uint256 public totalClaimableTokens;
    uint256 public totalClaimedTokens;
    uint256 public endedOn;
    uint256 public estimatedPercentageOfSupply;

    IUniswapV2Router02 public uniswapV2Router;
    IERC20 public token;
    IERC20 public usdcToken;

    uint256 public participants;
    uint256 public totalTxs;

    // NTT Holders
    mapping(address => bool) public nttHolders;

    // Liquidity wallet
    address public liquidityWallet;
    // Team wallet
    address public teamWallet;

    event LiquidityDonation(address from, uint256 donation);
    event TokenClaim(address from, uint256 tokens);

    modifier notLaunched {
        require(endedOn == 0, "Token already succcessfully launched");
        _;
    }

    constructor (uint256 _estimatedPercentageOfSupply) {
        require(_estimatedPercentageOfSupply <= 300, "ERROR: amounts should set less than 30%.");

        // claimable will use an estimate until the drive is over
        estimatedPercentageOfSupply = _estimatedPercentageOfSupply;

        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        liquidityWallet = address(0x95fefDC145F65fceD05d54Eb57b93A724507c300);
        teamWallet = address(0xbCDe557b8b6818818Bd1DA5Abc63F3d441a2b6eb);
        usdcToken = IERC20(0x8AC76a51cc950d9822D68b83fE1Ad97B32Cd580d);
    }

     /**
     * @dev Receive function to handle BNB that was send straight to the contract
     */
    receive() external payable {
        if (msg.sender != address(uniswapV2Router)) {
            require(false, "Do not send funds directly to this contract");
        }
    }

    function donate(uint256 amounts) external payable notLaunched {
        require(msg.value >= 0.005 ether || amounts > 0, "Minimum BNB should be greater than 0.005 or USDC amounts should be greater than 0");

        address _sender = _msgSender();
        uint256 _value = 0;

        if (amounts > 0) {
            _value = _swapUSDCForBNB(amounts);
        } else {
            _value = msg.value;    
        }

        uint256 donationShare = 0;
        uint256 teamShare = 0;

        if (nttHolders[_sender]) {
            donationShare = _value;
        } else {
            donationShare = _value.mul(95).div(100);
            teamShare = _value.mul(5).div(100);
        }

        // track participants
        if (_bnbDonated[_sender] == 0) {
            participants = participants.add(1);
        }

        // add donation
        _bnbDonated[_sender] = _bnbDonated[_sender].add(donationShare);
        totalBNBDonated = totalBNBDonated.add(donationShare);

        payable(liquidityWallet).transfer(donationShare);

        emit LiquidityDonation(_sender, donationShare);

        // send teamShare to team wallet
        if (teamShare > 0) {
            payable(teamWallet).transfer(teamShare);
        }

        totalTxs = totalTxs.add(1);
    }

    function claimTokens() external {
        require(endedOn > 0, "Token not launched yet");

        address _sender = _msgSender();

        //check if there are tokens to claim
        require(_bnbDonated[_sender] > 0, "No donations made prior to launch");
        require(_claimed[_sender] == 0, "This account has already claimed tokens");

        uint256 tokens = availableOf(_sender);
        _claimed[_sender] = tokens;

        //Send the tokens
        token.transfer(_sender, tokens);

        emit TokenClaim(_sender, tokens);

        totalClaimedTokens = totalClaimedTokens.add(tokens);

        totalTxs = totalTxs.add(1);
    } 

    function end() onlyOwner notLaunched external {
        require(token.balanceOf(address(this)) > 0, "Tokens must be transfered to the drive before it can be ended");

        //donations will no longer be processed
        endedOn = block.timestamp;

        //Set the amount of tokens to be distributed
        totalClaimableTokens = token.balanceOf(address(this));

        //transfer bnb funds to token, will be used to add liquidity
        address payable tokenAddr = payable(address(token));
        tokenAddr.transfer(address(this).balance);
    }

    function availableOf(address from) public view returns (uint256) {
        uint256 totalTokens = (totalClaimableTokens > 0) ? totalClaimableTokens : token.totalSupply().mul(estimatedPercentageOfSupply).div(1000);
        return (totalBNBDonated > 0) ? totalTokens.mul(_bnbDonated[from]).div(totalBNBDonated) : 0;
    }

    function _swapUSDCForBNB(uint256 amounts) private returns (uint256) {
        usdcToken.transferFrom(msg.sender, address(this), amounts);

        uint256 initialBNBBalance = address(this).balance;
        _swapTokens(amounts);
        uint256 bnbSwapped = address(this).balance.sub(initialBNBBalance);

        return bnbSwapped;
    }

    function _swapTokens(uint256 tokenAmount) private {
        usdcToken.approve(address(uniswapV2Router), tokenAmount);

        address[] memory path = new address[](2);
        path[0] = address(usdcToken);
        path[1] = uniswapV2Router.WETH();

         // make the swap
        uniswapV2Router.swapExactTokensForETH(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function manageNTTHolders(address[] calldata addresses, bool status) external onlyOwner {
        for (uint i = 0; i < addresses.length; i++) {
            nttHolders[addresses[i]] = status;
        }
    }

    function setStackToken(address tokenAddress) external onlyOwner {
        require(address(token) != tokenAddress, "ERROR: Can not set to same address.");
        token = IERC20(tokenAddress);
    }

    function updateUsdcToken(address tokenAddress) external onlyOwner {
        require(address(usdcToken) != tokenAddress, "ERROR: Can not set to same address.");
        usdcToken = IERC20(tokenAddress);
    }

    function updateSwapRouter(address routerAddress) external onlyOwner {
        require(address(uniswapV2Router) != routerAddress, "ERROR: Can not set to same address.");
        uniswapV2Router = IUniswapV2Router02(routerAddress);
    }

    function updateLiquidityWallet(address newAddress) external onlyOwner {
        require(liquidityWallet != newAddress, "ERROR: Can not set to same address.");
        liquidityWallet = newAddress;
    }

    function updateTeamTreasuryWallet(address treasuryAddress) external onlyOwner {
        require(teamWallet != treasuryAddress, "ERROR: Can not set to same address.");
        teamWallet = treasuryAddress;
    }

    function setEstimatedPercentageOfSupply(uint256 percentage) external onlyOwner {
        require(percentage <= 300, "ERROR: amounts should set less than 30%.");
        estimatedPercentageOfSupply = percentage;
    }
}
