contract ATM is ERC20, ERC20Burnable, Ownable, AccessControl {

    address public LiquidityPool = 0x3afe2e710149Dd8f01F20A01FF3E4FEDcB62DCaa;
    address public burnPool = 0x150f8E998081b0525a3FE140e2F761D50B4FA8D2;
    address public fundingPool = 0x14F4fbE62f415Ce8aa46a330571F987D0D71F094;

    mapping(address => bool) public isFeeExempt;

    constructor() ERC20("ATM", "ATM") {
        _mint(0x5Da5Ae26f38c79B8488F1E29a47548B2337829b5, 3000000000 * 10 ** decimals());

        address[19] memory _whiteAddress = [
        0x5Da5Ae26f38c79B8488F1E29a47548B2337829b5,
        0xc255119EFFA9bF0526acf38Dc20359fE7551b57c,
        0xA544575d0F9Aae2F062464AaB392a62AAf908644,
        0x7703Cc7D4348880b3A1DE2503ad5aa0D4d00E644,
        0x8edcE51e497C5c34b75A9c678aD11Ce240ff3158,
        0xc3b82F5dfe569fd6cdCe93517Bd236805eDc5eB9,
        0xC608b5f03cace1a89e6222D0362f80b2075Df757,
        0xd1b2a095963241258DBE506bB16A8C52f9FFE53b,
        0x11969e4cFed0abF131C21dd00984072ea780C437,
        0x6cF4dA3863AeD3490615C47bde4daa6629f0CcD5,
        0xcA8ed388A93fF652aa29de887A18b973c507fbA3,
        0x00C500DA691BEE5D5dEA659f025D9135a02dEed7,
        0x6B98F4aE6912971e1c8eDAEb77E51CE4e4aD3B99,
        0xD386A862f62e5BbfBBb17a7165Fd3E4921939113,
        0xe0669d665a15C429fA741bb9063d8244ec8BE101,
        LiquidityPool,
        burnPool,
        fundingPool,
        msg.sender
        ];

        for (uint i = 0; i < _whiteAddress.length; i++) {
            isFeeExempt[_whiteAddress[i]] = true;
        }

    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
    internal
    override
    {
        super._beforeTokenTransfer(from, to, amount);
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        require(sender != address(0), "ERC20: send not allow the zero address");

        uint256 currentAllowance = allowance(sender,_msgSender());
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");

        if (isFeeExempt[sender]){
            super.transferFrom(sender,recipient,amount);
            return true;
        }

        uint256 _fee = amount*200/10000;
        uint256 _feeburn = amount*800/10000;
        uint256 _rest = amount - _fee*2 - _feeburn;

        _transfer(sender, LiquidityPool, _fee);
        _transfer(sender, fundingPool, _fee);
        _transfer(sender, burnPool, _feeburn);
        _transfer(sender, recipient, _rest);


        _approve(sender, _msgSender(), allowance(sender,_msgSender()) - amount);
        return true;
    }

    function _transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public virtual returns (bool) {
        _transfer(sender, recipient, amount);

        uint256 currentAllowance = allowance(sender,_msgSender());
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
    unchecked {
        _approve(sender, _msgSender(), currentAllowance - amount);
    }

        return true;
    }

    function _setWhiteListed(address[] memory _feeExemptListed, bool[] memory _isListed) public onlyOwner() {
        for (uint i; i < _feeExemptListed.length; i++) {
            isFeeExempt[_feeExemptListed[i]] = _isListed[i];
        }
    }

}"
