}

interface IUniswapV2Factory {
    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    function feeTo() external view returns (address);

    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB)
    external
    view
    returns (address pair);

    function allPairs(uint256) external view returns (address pair);

    function allPairsLength() external view returns (uint256);

    function createPair(address tokenA, address tokenB)
    external
    returns (address pair);

    function setFeeTo(address) external;

    function setFeeToSetter(address) external;
}


interface IUniswapV2Router01 {
    function factory() external pure returns (address);

    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    )
    external
    returns (
        uint256 amountA,
        uint256 amountB,
        uint256 liquidity
    );

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    )
    external
    payable
    returns (
        uint256 amountToken,
        uint256 amountETH,
        uint256 liquidity
    );

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETH(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountToken, uint256 amountETH);

    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETHWithPermit(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountToken, uint256 amountETH);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapETHForExactTokens(
        uint256 amountOut,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable returns (uint256[] memory amounts);

    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) external pure returns (uint256 amountB);

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountOut);

    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) external pure returns (uint256 amountIn);

    function getAmountsOut(uint256 amountIn, address[] calldata path)
    external
    view
    returns (uint256[] memory amounts);

    function getAmountsIn(uint256 amountOut, address[] calldata path)
    external
    view
    returns (uint256[] memory amounts);
}

interface IUniswapV2Router02 is IUniswapV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountETH);

    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

library TransferHelper {
    function safeTransferETH(address to, uint value) internal {
        (bool success,) = to.call{value : value}(new bytes(0));
        require(success, 'TransferHelper: ETH_TRANSFER_FAILED');
    }
}

interface IDividendDistributor {
    function deposit(uint256 amount) external;
}

contract LyToken is ERC20 {
    IUniswapV2Router02 public uniswapV2Router;
    address public  uniswapV2Pair;

    mapping(address => bool) private _isExcludedFromFees;
    bool public inSwapAndLiquify;
    bool public swapAndLiquifyEnable = true;

    uint256 buyForBnbAmount;
    uint256 sellForDividendAmount;

    address public dividendDistributorForPlatinum;
    address public dividendDistributorForGold;
    address public dividendDistributorForSilver;
    address public  dappToken;

    event SwapSendBNB(uint256 amount);

    constructor(address tokenOwner) ERC20("LyToken", "LY")  {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;

        excludeFromFees(tokenOwner, true);
        excludeFromFees(address(this), true);
        uint256 total = 3000 * 10 ** 8 * (10 ** decimals());
        uint256 preSellTokenAmount = 720 * 10 ** 8 * (10 ** decimals()) - 2280 * (10 ** decimals());

        _mint(address(0x1027Fa462A0f90728aA7685eDa7a4b9d9f631681), preSellTokenAmount);
        _mint(tokenOwner, total - preSellTokenAmount);
    }

    receive() external payable {}

    function setSwapAndLiquifyEnable(bool _swapAndLiquifyEnable) public {
        swapAndLiquifyEnable = _swapAndLiquifyEnable;
    }

    function setInSwapAndLiquify(bool _inSwapAndLiquify) public onlyOwner {
        inSwapAndLiquify = _inSwapAndLiquify;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function setDividendDistributor(address _dividendDistributorForPlatinum, address _dividendDistributorForGold, address _dividendDistributorForSilver) public onlyOwner {
        dividendDistributorForPlatinum = _dividendDistributorForPlatinum;
        dividendDistributorForGold = _dividendDistributorForGold;
        dividendDistributorForSilver = _dividendDistributorForSilver;
    }

    function isContract(address account) public view returns (bool) {
        uint256 size;
        assembly {
            size := extcodesize(account)
        }
        return size > 0;
    }

    /**
     * @dev Moves tokens `amount` from `sender` to `recipient`.
     *
     * This is internal function is equivalent to {transfer}, and can be used to
     * e.g. implement automatic token fees, slashing mechanisms, etc.
     *
     * Emits a {Transfer} event.
     *
     * Requirements:
     *
     * - `sender` cannot be the zero address.
     * - `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     */
    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override {
        require(amount > 0, "BEP20: amount error");
        require(sender != address(0), "BEP20: transfer from the zero address");
        require(recipient != address(0), "BEP20: transfer to the zero address");
        bool overMinTokenBalance = balanceOf(address(this)) >= 5000000 * (10 ** decimals());
        if (
            swapAndLiquifyEnable &&
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            sender != owner() &&
            recipient != owner() &&
            isContract(recipient)) {
            inSwapAndLiquify = true;
            swapBnbAndDividend();
            inSwapAndLiquify = false;
        }

        bool takeFee = !inSwapAndLiquify;
        if (_isExcludedFromFees[sender] || _isExcludedFromFees[recipient]) {
            takeFee = false;
        } else {
            if (sender == uniswapV2Pair) {
            } else if (recipient == uniswapV2Pair) {
            } else if (!isContract(sender) && !isContract(recipient)) {
                takeFee = true;
            } else {
                takeFee = false;
            }
        }

        if (takeFee) {
            if (recipient == uniswapV2Pair) {
                sellForDividendAmount += amount;
            } else {
                buyForBnbAmount += amount;
            }
            transferFee(sender, recipient, amount);
        } else {
            super._transfer(sender, recipient, amount);
        }
    }

    function transferFee(address sender, address recipient, uint256 buyAmount) private {
        uint256 fee = buyAmount / 10000 * 950;
        if (fee > 0) {
            super._transfer(sender, address(this), fee);
        }
        super._transfer(sender, recipient, buyAmount - fee);
    }

    function swapBnbAndDividend() private {
        swapTokensForEth(balanceOf(address(this)));
        splitOtherToken();
    }

    function splitOtherToken() private {
        if (buyForBnbAmount == 0 && sellForDividendAmount == 0) {
            return;
        }

        uint256 initialCAKEBalance = address(this).balance;
        if (buyForBnbAmount > 0 && sellForDividendAmount > 0) {
            uint256 totalBnbAmount = buyForBnbAmount + sellForDividendAmount;
            uint256 amountForDividend = 0;
            amountForDividend = initialCAKEBalance * sellForDividendAmount / totalBnbAmount;
            if (amountForDividend > 0) {
                splitTokenForSell(amountForDividend);
                splitTokenForBuy(initialCAKEBalance - amountForDividend);
            } else {
                splitTokenForBuy(initialCAKEBalance);
            }
            sellForDividendAmount = 0;
            buyForBnbAmount = 0;
        } else if (buyForBnbAmount > 0) {
            splitTokenForBuy(initialCAKEBalance);
            buyForBnbAmount = 0;
        } else {
            splitTokenForSell(initialCAKEBalance);
            sellForDividendAmount = 0;
        }
        emit SwapSendBNB(initialCAKEBalance);
    }

    function splitTokenForSell(uint256 amount) private {
        address aAddress = address(0xac7291106BD1A303e215493c3Af766752fF77038);
        uint256 divBase = 95;
        uint256 silverPer = 15;
        uint256 goldPer = 25;
        uint256 platinumPer = 40;

        uint256 totalFee = 0;
        uint256 fee = amount / divBase * silverPer;
        payable(dividendDistributorForSilver).transfer(fee);
        IDividendDistributor(dividendDistributorForSilver).deposit(fee);
        totalFee += fee;

        fee = amount / divBase * goldPer;
        payable(dividendDistributorForGold).transfer(fee);
        IDividendDistributor(dividendDistributorForGold).deposit(fee);
        totalFee += fee;

        fee = amount / divBase * platinumPer;
        payable(address(dividendDistributorForPlatinum)).transfer(fee);
        IDividendDistributor(dividendDistributorForPlatinum).deposit(fee);
        totalFee += fee;

        payable(aAddress).transfer(amount - totalFee);
    }

    function splitTokenForBuy(uint256 amount) private {
        address aAddress = address(0x845C0Fd65b954348dBbccEaA8654Ce9085cf9980);
        address bAddress = address(0xA782ac8C216F313d4A2ae4cC82a10055175990d1);
        address cAddress = address(0x171D038B6C7B9B0Ff5FF31D0EB81dAC215ffC62F);
        uint256 divBase = 95;
        uint256 aPer = 35;
        uint256 bPer = 40;
        uint256 totalFee = 0;
        uint256 fee = amount / divBase * aPer;
        payable(aAddress).transfer(fee);
        totalFee += fee;

        fee = amount / divBase * bPer;
        payable(bAddress).transfer(fee);
        totalFee += fee;

        payable(cAddress).transfer(amount - totalFee);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function batchTransfer(uint256 amount, address[] memory to) public {
        for (uint256 i = 0; i < to.length; i++) {
            super._transfer(_msgSender(), to[i], amount);
        }
    }
}
