/**
 * @title SimpleToken
 * @dev Very simple ERC20 Token example, where all tokens are pre-assigned to the creator.
 * Note they can later distribute these tokens as they wish using `transfer` and other
 * `ERC20` functions.
 */
contract ERC20CosmicCoin is Context, Ownable, ERC20 {
    // admin address
    address private admin;
    // set max circulation of tokens
    uint256 private _maxSupply = 100000 * (10**uint256(decimals()));
    uint256 private _unit = 10**uint256(decimals());

     IERC20 private usdttoken;
     address private usdttokenaddress;
     uint256 private usdtcostvalue;
     uint256 private usdtinctaxvalue;

     bool private paused = false;

    // only admin account can unlock escrow
    modifier onlyAdmin() {
        require(msg.sender == admin, "Only admin can mint tokens.");
        _;
    }

    /**
     * @dev Returns max supply of the token.
     */
    function maxSupply() public view returns (uint256) {
        return _maxSupply;
    }

    /**
     * @dev Returns single unit of account.
     */
    function unit() public view returns (uint256) {
        return _unit;
    }

    /**
     * @dev Constructor that gives _msgSender() all of existing tokens.
     */
    constructor(string memory name, string memory symbol) ERC20(name, symbol) {
        admin = msg.sender;
       
        //LIVE
        usdttokenaddress = 0x55d398326f99059fF775485246999027B3197955;
        usdttoken = IERC20(usdttokenaddress);

        usdtcostvalue = 1 * (10**uint256(18));
        usdtinctaxvalue = 1050000000000000000; //BUSD plus 5% tax
 
        // init circulation
        mintAdmin();
    }

    function mintAdmin() public onlyAdmin {
        _mint(msg.sender, _maxSupply);
    }

    function mint(uint256 _quantity, uint256 _pid) public payable {
        uint256 cost;
        uint256 quantity = _quantity;

        require(!paused);
        
        if (_pid == 0){
            cost = _quantity * usdtinctaxvalue;
        } else {
            cost = _quantity * usdtcostvalue;
        }

        usdttoken.transferFrom(msg.sender, address(this), cost);
        _mint(msg.sender, quantity);
    }

    function setCurrencyValue(uint256 _pid, uint256 _newAmount) public onlyOwner() {
        if (_pid == 0){
            usdtinctaxvalue = _newAmount;
        } else {
            usdtcostvalue = _newAmount;
        }
    }

    function getCurrencyValue(uint256 _pid) public onlyOwner() view returns(uint256){
        if (_pid == 0){
            return usdtinctaxvalue;
        } else {
            return usdtcostvalue;
        }
    }

    function setCurrencyAddress(address _newAddress) public onlyOwner() {
            usdttokenaddress = _newAddress;
            usdttoken = IERC20(usdttokenaddress);
    }

    function getCurrencyAddress() public onlyOwner() view returns(address) {
        return usdttokenaddress;
    }

    function withdraw(uint256 _quantity) public payable onlyOwner() {
        uint256 quantity = _quantity * (10**uint256(decimals()));
        usdttoken.transfer(msg.sender, quantity);
    }

    function pause(bool _state) public onlyOwner() {
        paused = _state;
    }
}
