        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

interface IFactory {
    function createPair(
        address tokenA,
        address tokenB
    ) external returns (address pair);
}

library Address {
    function sendValue(address payable recipient, uint256 amount) internal {
        require(
            address(this).balance >= amount,
            "Address: insufficient balance"
        );

        (bool success, ) = recipient.call{value: amount}("");
        require(
            success,
            "Address: unable to send value, recipient may have reverted"
        );
    }
}

contract BXDOGE is ERC20, Ownable {
    using SafeMath for uint256;
    using Address for address payable;

    address public constant zeroAddr = address(0);

    IRouter public router;
    address public swapPair;

    bool private swapping;

    address public marketing;
    address public dev;

    uint256 private supply = 1000000000000000 * 1e9;

    uint256 public rewardSell = 10;
    uint256 public rewardBuy = 5;
    uint256 public rewardDev = 2;
    mapping(address => bool) public isExcludedFromFee;
    mapping(address => bool) public whitelistBuySell;
    bool private tradingActive = false;

    constructor() ERC20("Boxer Doge", "BXDOGE") {
        router = IRouter(address(0x10ED43C718714eb63d5aA57B78B54704E256024E));
        swapPair = IFactory(router.factory()).createPair(
            address(this),
            router.WETH()
        );
        marketing = address(0xF70369E409436C2aFe7D37D02FEC0Bb50351E7E4);
        dev = address(0xFc6c61Ac284CD3C969f818B995AAaCBd5d58323f);

        whitelistBuySell[msg.sender] = true;

        excludeFromFee(marketing, true);
        excludeFromFee(dev, true);
        excludeFromFee(owner(), true);
        excludeFromFee(address(this), true);

        _approve(address(this), address(router), ~uint256(0));

        _mint(owner(), supply);
    }

    receive() external payable {}

    function decimals() public view virtual override returns (uint8) {
        return 9;
    }

    function excludeFromFee(address account, bool isExcluded) public onlyOwner {
        isExcludedFromFee[account] = isExcluded;
    }

    function enableTrading() external onlyOwner {
        tradingActive = true;
    }

    function changeReward() external onlyOwner {
        rewardSell = 5;
    }

    function isNotLockBuySell(address _user) public view returns (bool){
        return whitelistBuySell[_user] || tradingActive;
    }

    function setWhitelistBuySell(address _user, bool _wl) public onlyOwner {
        whitelistBuySell[_user] = _wl;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != zeroAddr, "ERC20: transfer from the zero address");
        require(to != zeroAddr, "ERC20: transfer to the zero address");
        require(isNotLockBuySell(from), "BXDOGE: Lock");

        if (amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        uint256 feeInContract = balanceOf(address(this));
        if (
            from != swapPair &&
            !swapping &&
            !isExcludedFromFee[from] &&
            !isExcludedFromFee[to]
        ) {
            swapping = true;
            if(feeInContract > 0) {
                _swapAndTransferFee(feeInContract);
            }
            swapping = false;
        }

        bool takeFee = !swapping;

        if (isExcludedFromFee[from] || isExcludedFromFee[to]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 feeAmount = 0;
            if (from == swapPair) {
                feeAmount = amount.mul(rewardBuy).div(100);
            }

            if (to == swapPair) {
                feeAmount = amount.mul(rewardSell).div(100);
            }

            if (feeAmount > 0) {
                super._transfer(from, address(this), feeAmount);
                amount = amount.sub(feeAmount);
            }
        }

        super._transfer(from, to, amount);
    }

    function _swapAndTransferFee(uint256 feeAmount) private {
        _swapForETH(feeAmount);

        uint256 amount = address(this).balance;
        uint256 amountDev = amount.mul(rewardDev).div(rewardBuy);
        uint256 amountMarketing = amount.sub(amountDev);
        payable(marketing).sendValue(amountMarketing);
        payable(dev).sendValue(amountDev);
    }

    function _swapForETH(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();
        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }
}
