      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.2;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Snapshot.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";


contract StableFarmXUSDToken is AccessControl, ERC20Snapshot, Pausable, ReentrancyGuard {
    using SafeMath for uint256;
    using Address for address;

    bytes32 public constant PAUSE_ROLE = keccak256("PAUSE_ROLE");

    bytes32 public constant SNAPSHOT_ROLE = keccak256("SNAPSHOT_ROLE");

    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");

    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");

    mapping(address => bool) private frozen;

    mapping(address => bool) private whitelist;

    bool private isWhitelist;

    uint256 public volumeLimit;

    bool public volumeLimitSwitch;

    uint256 public holdLimit;

    bool public holdLimitSwitch;

    constructor(string memory name_, string memory symbol_ ,address _DFA) AccessControl() ERC20Snapshot() ERC20(name_, symbol_) {
        _setupRole(DEFAULT_ADMIN_ROLE, _DFA);

    }

    function createSnapshot() external onlyRole(SNAPSHOT_ROLE) returns (uint256) {
        return _snapshot();
    }

    function pauseToken() external onlyRole(PAUSE_ROLE) returns (bool) {
        _pause();
        return true;
    }

    function unpauseToken() external onlyRole(PAUSE_ROLE) returns (bool) {
        _unpause();
        return true;
    }

    function burn(address _to, uint256 _value) external onlyRole(MINTER_ROLE) returns (bool) {
        frozen[_to] = false;
        _burn(_to, _value);
        frozen[_to] = true;
        return true;
    }

    function mint(address _to, uint256 _value) external onlyRole(MINTER_ROLE) returns (bool) {
        _mint(_to, _value);
        frozen[_to] = true;
        return true;
    }

    function freeze(address _to) external virtual onlyRole(DEFAULT_ADMIN_ROLE) returns (bool) {
        frozen[_to] = true;
        return true;
    }

    function unfreeze(address _to) external virtual onlyRole(DEFAULT_ADMIN_ROLE) returns (bool) {
        frozen[_to] = false;
        return true;
    }

    function isFrozen(address _to) external view virtual returns (bool) {
        return frozen[_to];
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override nonReentrant {
        super._beforeTokenTransfer(from, to, amount);
        require(!paused(), "ERC20Pausable: token transfer paused");
        require(frozen[from] == false, "ERC20Blockable: Frozen by admin"); // Blacklist
    }
}
"
