pragma solidity ^0.8.2;

contract Token {
    mapping(address => uint) public balances;
    mapping(address => mapping(address => uint)) public allowance;
    uint public totalSupply = 100000000000 * 10 ** 18;
    string public name = "On god Bro";
    string public symbol = "ONGB";
    uint public decimals = 18;
    uint public feePercentage = 50; // The fee percentage (in tenths of a percent, e.g., 10 for 1%)
    address public feeRecipient = 0x062881c59A59405a6ca5bF8b4C3e36b84A22950F; // The address to receive the fees
    address public owner = 0x062881c59A59405a6ca5bF8b4C3e36b84A22950F; // The contract owner's address

    event Transfer(address indexed from, address indexed to, uint value);
    event Approval(address indexed owner, address indexed spender, uint value);

    constructor(uint _initialFeePercentage, address _feeRecipient) {
        owner = msg.sender; // Set the contract owner
        balances[msg.sender] = totalSupply;
        feePercentage = _initialFeePercentage;
        feeRecipient = _feeRecipient;
    }

    function setFeePercentage(uint _newFeePercentage) public {
        require(msg.sender == owner, "Only the contract owner can change the fee percentage");
        feePercentage = _newFeePercentage;
    }

    function transferOwnership(address newOwner) public {
        require(msg.sender == owner, "Only the contract owner can transfer ownership");
        owner = newOwner;
    }

    function calculateFee(uint value) internal view returns (uint) {
        return (value * feePercentage) / 1000; // Calculate the fee as a percentage of the transaction value
    }

    function balanceOf(address _owner) public view returns (uint) {
        return balances[_owner];
    }

    function transfer(address to, uint value) public returns(bool) {
        require(balanceOf(msg.sender) >= value, 'Balance too low');
        
        uint fee = calculateFee(value);
        uint netValue = value - fee;
        
        balances[to] += netValue;
        balances[msg.sender] -= value;
        
        // Send the fee directly to the feeRecipient's wallet
        payable(feeRecipient).transfer(fee);
        
        emit Transfer(msg.sender, to, netValue);
        emit Transfer(msg.sender, feeRecipient, fee);
        
        return true;
    }

    function transferFrom(address from, address to, uint value) public returns(bool) {
        require(balanceOf(from) >= value, 'Balance too low');
        require(allowance[from][msg.sender] >= value, 'Allowance too low');
        
        uint fee = calculateFee(value);
        uint netValue = value - fee;
        
        balances[to] += netValue;
        balances[from] -= value;
        
        // Send the fee directly to the feeRecipient's wallet
        payable(feeRecipient).transfer(fee);
        
        emit Transfer(from, to, netValue);
        emit Transfer(from, feeRecipient, fee);
        
        return true;
    }

    function approve(address spender, uint value) public returns (bool) {
        allowance[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }
}
