      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./library/TransferHelper.sol";
import "./library/EnumerableSet.sol";

contract CombinedMining is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;
    EnumerableSet.AddressSet private _whiteListOfContracts;
    EnumerableSet.AddressSet private _blackListOfUser;

    struct UserInfo {
        uint256 amount;
        uint256 rewardDebt;
    }

    struct PoolInfo {
        IERC20 lpToken;
        uint256 allocPoint;
        uint256 lastRewardBlock;
        uint256 accCoinPerShare;
        uint256 coinAmount;
    }

    address public coin;
    // reward tokens for per block.
    uint256 public coinPerBlock;
    // Info of each pool.
    PoolInfo[] public poolInfo;
    // Info of each user that stakes LP tokens.
    mapping(uint256 => mapping(address => UserInfo)) public userInfo;
    // Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint = 0;
    // The block number when coin mining starts.
    uint256 public startBlock;
    // The block number when coin mining end;
    uint256 public endBlock;
    // reward cycle default 1day
    uint256 public cycle;

    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount);

    constructor(address _coin, uint256 _cycle) {
        coin = _coin;
        cycle = _cycle;
    }

    function getSet(uint256 _type) internal view returns (EnumerableSet.AddressSet storage ads) {
        if (_type == 0) {
            return _whiteListOfContracts;
        } else {
            return _blackListOfUser;
        }
    }

    function addAddress(uint256 _type, address acc) external onlyOwner returns (bool) {
        EnumerableSet.AddressSet storage _set = getSet(_type);
        return _set.add(acc);
    }

    function delAddress(uint256 _type, address acc) external onlyOwner returns (bool) {
        EnumerableSet.AddressSet storage _set = getSet(_type);
        return _set.remove(acc);
    }

    function getAddressLength(uint256 _type) public view returns (uint256) {
        EnumerableSet.AddressSet storage _set = getSet(_type);
        return _set.length();
    }

    function isAddressOf(uint256 _type, address acc) public view returns (bool) {
        EnumerableSet.AddressSet storage _set = getSet(_type);
        return _set.contains(acc);
    }

    function getAddress(uint256 _type, uint256 index) public view returns (address) {
        EnumerableSet.AddressSet storage _set = getSet(_type);
        require(index <= getAddressLength(_type) - 1, "index out of bounds");
        return _set.at(index);
    }

    function poolLength() external view returns (uint256) {
        return poolInfo.length;
    }

    function newReward(
        uint256 _coinAmount,
        uint256 _newPerBlock,
        uint256 _startBlock
    ) external onlyOwner {
        require(block.number > endBlock && _startBlock >= endBlock, "Not finished");
        massUpdatePools();
        uint256 beforeAmount = IERC20(coin).balanceOf(address(this));
        TransferHelper.safeTransferFrom(coin, msg.sender, address(this), _coinAmount);
        uint256 afterAmount = IERC20(coin).balanceOf(address(this));
        uint256 balance = afterAmount.sub(beforeAmount);
        require(balance == _coinAmount, "Error balance");
        require(balance > 0 && (cycle * _newPerBlock) <= balance, "Balance not enough");
        coinPerBlock = _newPerBlock;
        startBlock = _startBlock;
        endBlock = _startBlock.add(cycle);
        updatePoolLastRewardBlock(_startBlock);
    }

    function updatePoolLastRewardBlock(uint256 _lastRewardBlock) private {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            PoolInfo storage pool = poolInfo[pid];
            pool.lastRewardBlock = _lastRewardBlock;
        }
    }

    function setCycle(uint256 _newCycle) public onlyOwner {
        cycle = _newCycle;
    }

    function add(
        uint256 _allocPoint,
        IERC20 _lpToken,
        bool _withUpdate
    ) public onlyOwner {
        require(address(_lpToken) != address(0), "lpToken is the zero address");
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > startBlock ? block.number : startBlock;
        totalAllocPoint = totalAllocPoint.add(_allocPoint);
        poolInfo.push(PoolInfo({lpToken: _lpToken, allocPoint: _allocPoint, lastRewardBlock: lastRewardBlock, accCoinPerShare: 0, coinAmount: 0}));
    }

    function set(
        uint256 _pid,
        uint256 _allocPoint,
        bool _withUpdate
    ) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
        poolInfo[_pid].allocPoint = _allocPoint;
    }

    function massUpdatePools() public {
        uint256 length = poolInfo.length;
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    function updatePool(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        uint256 number = block.number > endBlock ? endBlock : block.number;
        if (number <= pool.lastRewardBlock) {
            return;
        }
        uint256 lpSupply;
        if (address(pool.lpToken) == coin) {
            lpSupply = pool.coinAmount;
        } else {
            lpSupply = pool.lpToken.balanceOf(address(this));
        }
        if (lpSupply == 0) {
            pool.lastRewardBlock = number;
            return;
        }
        uint256 multiplier = number.sub(pool.lastRewardBlock);
        uint256 coinReward = multiplier.mul(coinPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
        pool.accCoinPerShare = pool.accCoinPerShare.add(coinReward.mul(1e25).div(lpSupply));
        pool.lastRewardBlock = number;
    }

    function pending(uint256 _pid, address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][_user];
        uint256 accCoinPerShare = pool.accCoinPerShare;
        uint256 lpSupply;
        if (address(pool.lpToken) == coin) {
            lpSupply = pool.coinAmount;
        } else {
            lpSupply = pool.lpToken.balanceOf(address(this));
        }
        uint256 number = block.number > endBlock ? endBlock : block.number;
        if (number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 multiplier = number.sub(pool.lastRewardBlock);
            uint256 coinReward = multiplier.mul(coinPerBlock).mul(pool.allocPoint).div(totalAllocPoint);
            accCoinPerShare = accCoinPerShare.add(coinReward.mul(1e25).div(lpSupply));
        }
        return user.amount.mul(accCoinPerShare).div(1e25).sub(user.rewardDebt);
    }

    function deposit(uint256 _pid, uint256 _amount) public {
        if (Address.isContract(msg.sender)) {
            require(isAddressOf(0, msg.sender), "Illegal, rejected ");
        }
        require(!isAddressOf(1, msg.sender), "Illegal, rejected");
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pendingAmount = user.amount.mul(pool.accCoinPerShare).div(1e25).sub(user.rewardDebt);
            if (pendingAmount > 0) {
                safeCoinTransfer(msg.sender, pendingAmount, pool.coinAmount);
            }
        }
        if (_amount > 0) {
            pool.lpToken.safeTransferFrom(address(msg.sender), address(this), _amount);
            user.amount = user.amount.add(_amount);
            if (address(pool.lpToken) == coin) {
                pool.coinAmount = pool.coinAmount.add(_amount);
            }
        }
        user.rewardDebt = user.amount.mul(pool.accCoinPerShare).div(1e25);
        emit Deposit(msg.sender, _pid, _amount);
    }

    function withdraw(uint256 _pid, uint256 _amount) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        require(user.amount >= _amount, "withdraw: not good");
        updatePool(_pid);
        uint256 pendingAmount = user.amount.mul(pool.accCoinPerShare).div(1e25).sub(user.rewardDebt);
        if (pendingAmount > 0) {
            safeCoinTransfer(msg.sender, pendingAmount, pool.coinAmount);
        }
        if (_amount > 0) {
            user.amount = user.amount.sub(_amount);
            if (address(pool.lpToken) == coin) {
                pool.coinAmount = pool.coinAmount.sub(_amount);
            }
            pool.lpToken.safeTransfer(address(msg.sender), _amount);
        }
        user.rewardDebt = user.amount.mul(pool.accCoinPerShare).div(1e25);
        emit Withdraw(msg.sender, _pid, _amount);
    }

    function emergencyWithdraw(uint256 _pid) public {
        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        uint256 amount = user.amount;
        user.amount = 0;
        user.rewardDebt = 0;
        if (address(pool.lpToken) == coin) {
            pool.coinAmount = pool.coinAmount.sub(amount);
        }
        pool.lpToken.safeTransfer(address(msg.sender), amount);
        emit EmergencyWithdraw(msg.sender, _pid, amount);
    }

    function safeCoinTransfer(
        address _to,
        uint256 _amount,
        uint256 _poolCoinAmount
    ) internal {
        uint256 coinBalance = IERC20(coin).balanceOf(address(this));
        coinBalance = coinBalance.sub(_poolCoinAmount);
        if (_amount > coinBalance) {
            IERC20(coin).transfer(_to, coinBalance);
        } else {
            IERC20(coin).transfer(_to, _amount);
        }
    }

    // Prevent accidental recharge of tokens
    function extract(
        address _token,
        address _to,
        uint256 _amount
    ) external onlyOwner {
        require(_token != address(0) && _to != address(0), "Is zero address");
        TransferHelper.safeTransfer(_token, _to, _amount);
    }
}
