pragma solidity ^0.8.0;

import "GreenEnergyToken.sol";

contract EnergyMarket {
    GreenEnergyToken public token;

    mapping(address => uint256) public energyBalances;

    struct Listing {
        address seller;
        uint256 energyAmount;
        uint256 price;
    }

    Listing[] public listings;

    constructor(GreenEnergyToken _token) {
        token = _token;
    }

    function produceEnergy(uint256 amount) public {
        energyBalances[msg.sender] += amount;
    }

    function listEnergy(uint256 energyAmount, uint256 price) public {
        require(price > 0, "Price must be greater than 0");
        require(energyBalances[msg.sender] >= energyAmount, "Not enough energy to list");
        energyBalances[msg.sender] -= energyAmount;
        listings.push(Listing(msg.sender, energyAmount, price));
    }

    function buyEnergy(uint256 listingId) public {
        require(listingId < listings.length, "Listing does not exist");
        Listing storage listing = listings[listingId];
        require(listing.seller != address(0), "Listing has already been sold");
        require(token.balanceOf(msg.sender) >= listing.price, "Not enough tokens");

        // Transfer the tokens
        token.transferFrom(msg.sender, listing.seller, listing.price);

        // Transfer the energy
        energyBalances[msg.sender] += listing.energyAmount;

        // Remove the listing
        delete listings[listingId];
    }

}
