import "ERC20.sol";
import "Ownable.sol";

contract GreenEnergyToken is ERC20, Ownable {
    string private logoUrl;

    constructor(uint256 initialSupply) ERC20("Green Energy Token", "GET") {
        require(initialSupply > 0, "Initial supply must be greater than 0");
        _mint(msg.sender, initialSupply);
    }

    function setLogo(string memory _logoUrl) public onlyOwner {
        logoUrl = _logoUrl;
        emit LogoUpdated(_logoUrl);
    }

    function getLogo() public view returns (string memory) {
        return logoUrl;
    }

    receive() external payable {
        revert();
    }

    fallback() external payable {
        revert();
    }

    event LogoUpdated(string logoUrl);
}
