pragma solidity ^0.8.0;

import "GreenEnergyToken.sol";

contract Voting {
    GreenEnergyToken public token;

    uint256 public constant MAX_ACTIVE_PROPOSALS = 5;
    uint256 public constant VOTING_PERIOD = 1 weeks;

    struct Proposal {
        string description;
        uint256 voteCount;
        bool open;
        uint256 endTime;
        mapping(address => uint256) votes;
        mapping(address => bool) votedToClose;
        uint256 closeVoteCount;
    }

    Proposal[] public proposals;

    event ProposalCreated(uint256 proposalId, string description, uint256 endTime);
    event VoteCast(uint256 proposalId, address voter, uint256 voteCount);
    event VoteToCloseCast(uint256 proposalId, address voter, uint256 voteCount);
    event ProposalClosed(uint256 proposalId);

    constructor(GreenEnergyToken _token) {
        token = _token;
    }

    function activeProposals() public view returns (uint256) {
        uint256 count = 0;
        for (uint256 i = 0; i < proposals.length; i++) {
            if (proposals[i].open) {
                count++;
            }
        }
        return count;
    }

    function createProposal(string memory description) public {
        require(activeProposals() < MAX_ACTIVE_PROPOSALS, "Too many active proposals");
        require(bytes(description).length > 0, "Description cannot be empty");

        Proposal storage newProposal = proposals.push();
        newProposal.description = description;
        newProposal.voteCount = 0;
        newProposal.open = true;
        newProposal.endTime = block.timestamp + VOTING_PERIOD;
        newProposal.closeVoteCount = 0;

        emit ProposalCreated(proposals.length - 1, description, newProposal.endTime);
    }


    function vote(uint256 proposalId, uint256 amount) public {
        require(proposalId < proposals.length, "Proposal does not exist");
        Proposal storage proposal = proposals[proposalId];
        require(proposal.open, "Proposal is closed");
        require(block.timestamp < proposal.endTime, "Voting period has ended");
        require(token.balanceOf(msg.sender) >= amount, "Not enough tokens to vote");
        require(proposal.votes[msg.sender] == 0, "Already voted on this proposal");

        token.transferFrom(msg.sender, address(this), amount);

        proposal.voteCount += amount;
        proposal.votes[msg.sender] = amount;

        emit VoteCast(proposalId, msg.sender, amount);
    }

    function voteToClose(uint256 proposalId, uint256 amount) public {
        require(proposalId < proposals.length, "Proposal does not exist");
        Proposal storage proposal = proposals[proposalId];
        require(proposal.open, "Proposal is already closed");
        require(block.timestamp < proposal.endTime, "Voting period has ended");
        require(!proposal.votedToClose[msg.sender], "Already voted to close this proposal");
        require(token.balanceOf(msg.sender) >= amount, "Not enough tokens to vote");

        token.transferFrom(msg.sender, address(this), amount);

        proposal.closeVoteCount += amount;
        proposal.votedToClose[msg.sender] = true;

        emit VoteToCloseCast(proposalId, msg.sender, amount);

        // Close the proposal if the majority of voters voted to close it
        if (proposal.closeVoteCount * 2 > proposal.voteCount) {
            proposal.open = false;
            emit ProposalClosed(proposalId);
        }
    }
}
