interface IUniswapV2Factory {
    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    function feeTo() external view returns (address);
    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB) external view returns (address pair);
    function allPairs(uint) external view returns (address pair);
    function allPairsLength() external view returns (uint);

    function createPair(address tokenA, address tokenB) external returns (address pair);

    function setFeeTo(address) external;
    function setFeeToSetter(address) external;
}


interface IUniswapV2Pair {
    event Approval(address indexed owner, address indexed spender, uint value);
    event Transfer(address indexed from, address indexed to, uint value);

    function name() external pure returns (string memory);
    function symbol() external pure returns (string memory);
    function decimals() external pure returns (uint8);
    function totalSupply() external view returns (uint);
    function balanceOf(address owner) external view returns (uint);
    function allowance(address owner, address spender) external view returns (uint);

    function approve(address spender, uint value) external returns (bool);
    function transfer(address to, uint value) external returns (bool);
    function transferFrom(address from, address to, uint value) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);
    function PERMIT_TYPEHASH() external pure returns (bytes32);
    function nonces(address owner) external view returns (uint);

    function permit(address owner, address spender, uint value, uint deadline, uint8 v, bytes32 r, bytes32 s) external;

    event Mint(address indexed sender, uint amount0, uint amount1);
    event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
    event Swap(
        address indexed sender,
        uint amount0In,
        uint amount1In,
        uint amount0Out,
        uint amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint);
    function factory() external view returns (address);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function price0CumulativeLast() external view returns (uint);
    function price1CumulativeLast() external view returns (uint);
    function kLast() external view returns (uint);

    function mint(address to) external returns (uint liquidity);
    function burn(address to) external returns (uint amount0, uint amount1);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
    function skim(address to) external;
    function sync() external;

    function initialize(address, address) external;
}

interface IUniswapV2Router01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountToken, uint amountETH);
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountToken, uint amountETH);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

interface IUniswapV2Router02 is IUniswapV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountETH);
    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

interface IStaking{
    function notifyRewardAmount(uint256 reward) external;    
}

contract SAFEST is ERC20, ERC20Burnable, AccessControl {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    uint256 public constant total = 10 * 1000000000 ether;

    uint256 public _liqFee = 4;
    uint256 public _stakingFee = 2;
    uint256 public _devFee = 3;

    address public _devWallet = 0xe643165BF39213a5E4fb80b7cDeAf7df525a1eb3;

    uint256 public holders = 1;

    uint256 public _maxTxAmount = 3000000000 ether;

    mapping (address => bool) private _feeWhitelist;

    uint256 private minBuyLiq = 1000000 ether;
    bool public liqEnabled = false;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    bool public inSwapAndLiquify;

    uint256 public totalLiq = 0;
    uint256 public totalStaking = 0;

    address public stakingContact;

    uint256 public minStakingTransfer = 1000000 ether;


    bool public uniswapEnabled = true;

    modifier onlyAdmin {
        require(hasRole(ADMIN_ROLE, msg.sender), "!admin");
        _;
    }

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor() ERC20("SafuFide Token", "SAFEST") {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setupRole(ADMIN_ROLE, msg.sender);

        _mint(msg.sender, total);

        _feeWhitelist[msg.sender] = true;
        _feeWhitelist[_devWallet] = true;

        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E); //mainnet
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory())
            .createPair(address(this), uniswapV2Router.WETH());
    }

    receive() external payable {}

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _internalTransfer(msg.sender, recipient, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _internalTransfer(sender, recipient, amount);

        uint256 currentAllowance = allowance(sender, msg.sender);
        require(currentAllowance >= amount, "ERC20: transfer amount exceeds allowance");
        _approve(sender, msg.sender, currentAllowance - amount);

        return true;
    }

    function _internalTransfer(address sender, address recipient, 
        uint256 amount) private returns(bool){

        require(recipient != uniswapV2Pair || uniswapEnabled, 
            "public sale not started");

        if( !hasRole(ADMIN_ROLE, sender) && !hasRole(ADMIN_ROLE, recipient))
            require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");

        if(_feeWhitelist[sender] || _feeWhitelist[recipient]){
            _transfer(sender, recipient, amount);
            return true;
        }

        uint256 liqFeeAmount = amount * _liqFee / 100;
        uint256 stakingFeeAmount = amount * _stakingFee / 100;
        uint256 devFeeAmount = amount * _devFee / 100;

        if(amount > 0 && balanceOf(recipient) == 0){
            holders += 1;
        }

        _transfer(sender, recipient, amount - liqFeeAmount - 
            stakingFeeAmount - devFeeAmount
        );

        if(balanceOf(sender) == 0 && holders > 0){
            holders -= 1;
        }

        if(liqFeeAmount > 0){
            _transfer(sender, address(this), liqFeeAmount);
            totalLiq += liqFeeAmount;
        }
        if(stakingFeeAmount > 0){
            _transfer(sender, address(this), stakingFeeAmount);
            totalStaking += stakingFeeAmount;
        }
        if(devFeeAmount > 0){
            _transfer(sender, _devWallet, devFeeAmount);
        }

        bool overMinTokenBalance = totalLiq >= minBuyLiq;

        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            sender != uniswapV2Pair &&
            liqEnabled
        ) {
            swapAndLiquify(minBuyLiq);
            totalLiq -= minBuyLiq;
        }

        if(totalStaking > minStakingTransfer && stakingContact != address(0)){
            transfer(stakingContact, totalStaking);
            totalStaking = 0;
            IStaking(stakingContact).notifyRewardAmount(totalStaking);
        }

        return true;
    }

    function swapAndLiquify(uint256 _totalLiq) private lockTheSwap {
        // split the contract balance into halves
        uint256 half = _totalLiq / 2;
        uint256 otherHalf = _totalLiq / 2;

        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(half);

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance- initialBalance;

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function setLiqFee(uint256 newLiqFee) public onlyAdmin {
        _liqFee = newLiqFee;
    }

    function setStakingFee(uint256 _fee) public onlyAdmin {
        _stakingFee = _fee;
    }

    function setDevFee(uint256 newDevFee) public onlyAdmin {
        _devFee = newDevFee;
    }

    function setDevWallet(address newDevWallet) public onlyAdmin {
        _devWallet = newDevWallet;
    }

    function updateWhiteList(address _address, bool activation) public onlyAdmin {
        _feeWhitelist[_address] = activation;
    }


    function updateMinBuyLiq(uint256 min) public onlyAdmin {
        minBuyLiq = min;
    }

    function updateLiqEnabled(bool enabled) public onlyAdmin {
        liqEnabled = enabled;
    }

    function setStakingContact(address addr) public onlyAdmin {
        stakingContact = addr;
    }

    function setMinStakingTransfer(uint256 _minStakingTransfer) public onlyAdmin {
        minStakingTransfer = _minStakingTransfer;
    }

    function updateUniswapV2Router(address newAddress) public onlyAdmin {
        uniswapV2Router = IUniswapV2Router02(newAddress);
    }

    function withdrawToken(address _tokenAddr, address _to, uint _amount) public onlyAdmin{
        require(_to != address(0), "0x0");
        if(_tokenAddr == address(0)){
            payable(_to).transfer(_amount);    
        }else{
            IERC20(_tokenAddr).transfer(_to, _amount);    
        }
    }

    function publicSale() public onlyAdmin{
        uniswapEnabled = true;
    }

    function setMaxTxAmount(uint256 amount) public onlyAdmin{
        _maxTxAmount = amount;
    }
}
