contract FanHuaToken is ERC20,Ownable {
    using SafeMath for uint256;

    modifier swapping(){
        inSwap=true;
        _;
        inSwap=false;
    }

    mapping(address=>bool) public isExcludeFee;
    mapping(address=>bool) public isBlackList;
    mapping(address=>bool) public automatedMarketMakerPairs;
    
    
    uint256 public sellFees=50;
    uint256 public buyFees=50;

    address public WETH;
    address public foundation=0x8f64FBBF1a6B553bf9daa8401C292d537554d099;
    address public labAddr=0xAA855c5A18BBC6d556353b928811b301Bc3435E0;
    address public basePair;

    bool public launched;
    bool inSwap;

    ISwapRouter public SwapRouter;
    AutoSwap public autoSwap;

    constructor() ERC20("FanHua","FanHua"){

        SwapRouter=ISwapRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        
        _mint(0x7788cEbFAe3F5a1D8c6A3740f5681DA0296FeDfe, 3000000000e18);

        isExcludeFee[msg.sender]=true;
        isExcludeFee[foundation]=true;
        isExcludeFee[0x7788cEbFAe3F5a1D8c6A3740f5681DA0296FeDfe]=true;

        WETH=SwapRouter.WETH();
        basePair=ISwapFactory(SwapRouter.factory()).createPair(WETH,address(this));
        automatedMarketMakerPairs[basePair]=true;
        IERC20(WETH).approve(address(SwapRouter), type(uint256).max);
        _approve(address(this),address(SwapRouter),type(uint256).max);
        autoSwap=new AutoSwap();
    }

    function _transfer(address from,address to,uint256 amount) internal override {
        
        if(inSwap||isExcludeFee[from]||isExcludeFee[to]){
            return super._transfer(from, to, amount);
        }

        require(launched&&!isBlackList[from]&&!isBlackList[to],"ERC20: is black list"); 
        
        (bool isSell,bool isBuy)=isSwap(from, to);

        if(isBuy){
            uint256 fees=amount.mul(buyFees).div(1000);

            super._transfer(from, to, amount.sub(fees));
            
            return super._transfer(from, address(this), fees);
        }
        
        if(isSell){ 
            require(balanceOf(from).mul(9999).div(10000)>=amount,"err amount");

            uint256 fees=amount.mul(sellFees).div(1000);

            super._transfer(from, address(this), fees);
            
            process();
            
            return super._transfer(from, to, amount.sub(fees));
        }
        
        return super._transfer(from, to, amount);
        
    }

    function process() internal  swapping {
        uint256 balance=balanceOf(address(this));
        if(balance==0){
            return;
        }

        uint256 addLPAmount=balance.div(10);
        uint256 swapAmount=balance.sub(addLPAmount);

        
        address[] memory path=new address[](2);
        path[0]=address(this);
        path[1]=WETH;
        uint256[] memory amounts=SwapRouter.swapExactTokensForTokens(swapAmount,0,path,address(autoSwap),block.timestamp);
        autoSwap.withdraw(IERC20(WETH));

        uint256 WETHAmount=amounts[1];
        SwapRouter.addLiquidity(WETH, address(this), WETHAmount, addLPAmount, 0, 0, foundation, block.timestamp);

        uint256 WETHBalance=IERC20(WETH).balanceOf(address(this));

        uint256 labAmount=WETHBalance.div(9);
        
        IERC20(WETH).transfer(labAddr,labAmount);
        IERC20(WETH).transfer(foundation,WETHBalance.sub(labAmount));

    }

    function isSwap(address from,address to) public view returns(bool isSell,bool isBuy){
        if(automatedMarketMakerPairs[from]){
            return (false,true);
        }else if(automatedMarketMakerPairs[to]){
            return (true,false);
        }

        if(isContract(from)){
            try ISwapPair(from).token0()  {
                if(ISwapPair(from).token0()==address(this)||ISwapPair(from).token1()==address(this)){
                    return (false,true);
                }else{
                    return (true,false);
                }
            } catch {}
        }   
        
        if(isContract(to)){
            try ISwapPair(to).token0()  {
                if(ISwapPair(to).token0()==address(this)||ISwapPair(to).token1()==address(this)){
                    return (true,false);
                }else{
                    return (false,true);
                }
            } catch {}
        }
    
    }

    function isContract(address addr) internal view returns (bool) {
        uint size;
        assembly { size := extcodesize(addr) }
        return size > 0;
    }

    function multiSetBlackList(address[] memory users,bool isExclude) external onlyOwner{
        for(uint i;i<users.length;i++){
            isBlackList[users[i]]=isExclude;
        }
    }

    function setAutomatedMarketMakerPairs(address _pair,bool _isPair) external onlyOwner{
        automatedMarketMakerPairs[_pair]=_isPair;
    }

    function multiSetExcludeFee(address[] memory users,bool isExclude) external onlyOwner{
        for(uint i;i<users.length;i++){
            isExcludeFee[users[i]]=isExclude;
        }
    }

    function initFees(uint8 buy,uint256 sell,bool enable) external onlyOwner{
        sellFees=sell;
        buyFees=buy;
        launched=enable;
    }

}

contract AutoSwap {
    address public owner;
    constructor(){
        owner=msg.sender;
    }

    function withdraw(IERC20 erc20) external {
        require(msg.sender==owner,"AutoSwap: err permission");
        erc20.transfer(msg.sender,erc20.balanceOf(address(this)));
    }
}

interface ISwapPair is IERC20{
    function factory() external pure returns (address);
    
    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
}

interface ISwapFactory {
    function getPair(address tokenA, address tokenB) external view returns (address pair);

    function createPair(address tokenA, address tokenB) external returns (address pair);
}

