contract BWJRewards is Ownable {
    IERC20 public rewardsToken;
    uint public minReward;
    using SafeMath for uint;

    mapping(address => uint) public rewards;


    address public op;

    address public feeAddress;
    uint256 public feePercent= 0 ; //percent


    struct bulkRewards {
        address user;
        uint256 amount;
    }

   


       struct checkPay {

        string[] hash;

    }
     mapping(string => checkPay) hashPay;

    constructor(address _rewardsToken) {
        rewardsToken = IERC20(_rewardsToken);
       
    }
function setOp(address account)  public onlyOwner  {
        op =  account;
    }

    function setFeeAddress(address account)  public onlyOwner  {
        feeAddress =  account;
    }
      function setFeePercent(uint256 _fee)  public onlyOwner  {
        feePercent =  _fee;
    }
    function addReward(address account,uint256 amount)  public onlyOwner  {
        rewards[account] = rewards[account] + amount;
    }
//Bulk
     function addBulkRewards(bulkRewards[] memory _data)  public onlyOwner  {

         for(uint i = 0; i<_data.length; i++){
            rewards[_data[i].user] = rewards[_data[i].user] + _data[i].amount;
        }
    }


    //
  function setReward(address account,uint256 amount)  public onlyOwner  {
        rewards[account] =  amount;
    }

     function setRewardMin(uint256 amount)  public onlyOwner  {
        minReward =  amount;
    }
    function claimReward() public{
       
        uint256 reward = rewards[msg.sender];
         //Check for min reward
           require(reward >= minReward , "Min Amount wrong");  
        //Set fee
           uint256 tFee       = reward.mul(feePercent).div(100);
           uint256 amount =reward.sub(tFee);
      
        //
        rewards[msg.sender] = 0;
        rewardsToken.transfer(msg.sender,amount*10**9 );
    }

        
    function userReward(address _owner) public view returns (uint256) {
        return rewards[_owner];
    }

 function userRewardWithFee(address _user) public view returns (uint256) {
     uint256 tFee       = rewards[_user].mul(feePercent).div(100);
       return  rewards[_user].sub(tFee);
    }
  function addRewardOp(address account,uint256 amount)  public   {
         require(msg.sender == op , "Wrong Op"); 
         
        rewards[account] = rewards[account] + amount;
    }

    //Bulk for op
     function addBulkRewardsOp(bulkRewards[] memory _data)  public   {
         require(msg.sender == op , "Wrong Op"); 

         for(uint i = 0; i<_data.length; i++){
            rewards[_data[i].user] = rewards[_data[i].user] + _data[i].amount;
        }
    }


    //

    function setRewardToken(address _token)  public onlyOwner  {
        rewardsToken =  IERC20(_token);
    }
    function withdraw() public onlyOwner {
        (bool os, ) = payable(owner()).call{value: address(this).balance}("");
        require(os);
    }
    function withdrawTokens(IERC20 token) public onlyOwner {
        uint256 balance = token.balanceOf(address(this));
        token.transfer(msg.sender, balance);
    }


//Deposit for Levelup and etc
 modifier checkAllowance(uint amount) {
        require(rewardsToken.allowance(msg.sender, address(this)) >= amount, "Error Allowance");
        _;
    }

   function costUpgrade(uint _amount,string calldata _data) public  {

        require(rewardsToken.allowance(msg.sender, address(this)) >= _amount, "Error Allowance");
        rewardsToken.transferFrom(msg.sender, address(this), _amount);

    }

   

}
