    "contracts/TA.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.6;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "./interfaces/IUniswapV2Factory.sol";
import "./interfaces/IPancakeRouter02.sol";
import "./interfaces/IUserRegistry.sol";

import "./MiningToken.sol";
import "./RewardTokenContract.sol";

interface INotifyRewardAmount {
    function notifyRewardAmount(uint256 amount) external;
}

contract TA is IERC20, IERC20Metadata, Ownable, MiningToken {
    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;

    mapping(address => mapping(address => uint256)) private _allowances;

    string private _name = "TA Sports";
    string private _symbol = "TA";

    mapping(address => bool) private _shareExclude;

    mapping(address => bool) private _isExcludedFromFee;

    IPancakeRouter02 public  uniswapV2Router;
    address public uniswapV2Pair;

    address public daoAddress;
    address public devAddress;
    address public rewardAddress;
    address public nftAddress;

    IUserRegistry userRegistry;

    RewardTokenContract rtc;

    constructor(
        address routerAddress,
        address usdtAddress,
        address daoAddress_,
        address devAddress_,
        address rewardAddress_,
        IUserRegistry userRegistry_
    ) {
        daoAddress = daoAddress_;
        devAddress = devAddress_;
        rewardAddress = rewardAddress_;

        uniswapV2Router = IPancakeRouter02(routerAddress);
        uniswapV2Pair = IUniswapV2Factory(uniswapV2Router.factory()).createPair(address(this), usdtAddress);

        rtc = new RewardTokenContract(address(this), usdtAddress, routerAddress);
        userRegistry = userRegistry_;

        _isExcludedFromFee[msg.sender] = true;
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[BURN_ADDRESS] = true;
        _isExcludedFromFee[address(rtc)] = true;
        _shareExclude[address(this)] = true;
        _shareExclude[uniswapV2Pair] = true;
        _shareExclude[BURN_ADDRESS] = true;
        _shareExclude[address(rtc)] = true;
        _mintExclude[address(this)] = true;
        _mintExclude[uniswapV2Pair] = true;
        _mintExclude[BURN_ADDRESS] = true;
        _mintExclude[address(rtc)] = true;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return 18;
    }

    function totalSupply() public view virtual override(MiningToken, IERC20) returns (uint256) {
        return MiningToken.totalSupply();
    }

    function balanceOf(address account) public view virtual override(MiningToken, IERC20) returns (uint256) {
        return MiningToken.balanceOf(account);
    }

    function transfer(address to, uint256 amount) public virtual override returns (bool) {
        address owner = msg.sender;
        _transfer(owner, to, amount);
        return true;
    }

    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function setExcludedFromFee(address account, bool state) external onlyOwner {
        _isExcludedFromFee[account] = state;
    }

    function setShareExclude(address account, bool state) external onlyOwner {
        _shareExclude[account] = state;
    }

    function setMintExclude(address account, bool state) external onlyOwner {
        _mintExclude[account] = state;
    }

    function setDaoAddress(address account) external onlyOwner {
        daoAddress = account;
    }

    function setDevAddress(address account) external onlyOwner {
        devAddress = account;
    }

    function setNftAddress(address account) external onlyOwner {
        nftAddress = account;
    }

    function setUserRegistry(IUserRegistry addr) external onlyOwner {
        userRegistry = addr;
    }

    function setRtc(RewardTokenContract addr) external onlyOwner {
        rtc = addr;
    }

    function transferRtcOwner(address account) external onlyOwner {
        rtc.transferOwnership(account);
    }

    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        address owner = msg.sender;
        _approve(owner, spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = msg.sender;
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        address owner = msg.sender;
        _approve(owner, spender, allowance(owner, spender) + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        address owner = msg.sender;
        uint256 currentAllowance = allowance(owner, spender);
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero");
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function mint(address account, uint256 amount) external onlyOwner {
        _mint(account, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        updateMining();

        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            _tokenTransfer(from, to, amount);
        } else {
            rtc.checkSharing();
            if (from != uniswapV2Pair) {
                rtc.checkSwap();
            }
            if(from == uniswapV2Pair){
                _tokenTransferBuy(from, to, amount);
            } else if (to == uniswapV2Pair) {
                _tokenTransferSell(from, to, amount);
            } else {
                _tokenTransferFees(from, to, amount);
            }
        }

        updateShare(from);
        updateShare(to);
    }

    function _tokenTransferBuy(
        address from,
        address to,
        uint256 amount
    ) private {
        // 2% : 自动加LP，
        // 1% : 千一分红
        // 2% : 作为NFT持有分红，
        // 1% : 作为教练裁判卡分红，
        // 6% : 市场奖励：直推：1.25% 间推1% 3--5代0.25% 6--10代0.6%

        uint256 fee1 = amount * 3 / 100;
        uint256 fee2 = amount * 2 / 100;
        uint256 fee3 = amount * 1 / 100;
        uint256 fee4 = amount * 6 / 100;
        _tokenTransfer(from, to, amount - fee1 - fee2 - fee3);

        _tokenTransfer(from, address(rtc), fee1);
        _tokenTransfer(from, rewardAddress, fee2);
        if (nftAddress != address(0)) {
            _tokenTransfer(from, nftAddress, fee3);
            INotifyRewardAmount(nftAddress).notifyRewardAmount(fee3);
        } else {
            _tokenTransfer(from, BURN_ADDRESS, fee3);
        }
        _tokenTransferRecommender(to, fee4);
    }

    function _tokenTransferRecommender(address account, uint256 amount) private {
        uint8 steps = 10;
        address _recommender = userRegistry.recommender(account);

        uint256 leftAmount = amount;
        while (steps > 0 && _recommender != address(0)) {
            uint256 a;
            if (steps == 10) {
                a = amount * 125 / 600;
            } else if (steps == 9) {
                a = amount * 100 / 600;
            } else if (steps > 5) {
                a = amount * 25 / 600;
            } else {
                a = amount * 60 / 600;
            }
            a = a > leftAmount ? leftAmount : a;
            if (_recommender != address(0)) {
                _tokenTransfer(account, _recommender, a);
                leftAmount -= a;
            }
            _recommender = userRegistry.recommender(_recommender);
            steps--;
        }

        if (leftAmount > 0) {
            _tokenTransfer(account, daoAddress, leftAmount);
        }
    }

    function _tokenTransferSell(
        address from,
        address to,
        uint256 amount
    ) private {
        // 4% 自动加LP，
        // 1% 千一分红
        // 2% 奖励给持有NFT分红，
        // 1% 裁判教练卡分红，
        // 2% 技术开发升级维护，
        // 2% 自动销毁，
        uint256 fee1 = amount * 5 / 100;
        uint256 fee2 = amount * 2 / 100;
        uint256 fee3 = amount * 1 / 100;
        uint256 fee4 = amount * 2 / 100;
        uint256 fee5 = amount * 2 / 100;
        _tokenTransfer(from, to, amount - fee1 - fee2 - fee3 - fee4 - fee5);

        _tokenTransfer(from, address(rtc), fee1);
        _tokenTransfer(from, rewardAddress, fee2);
        if (nftAddress != address(0)) {
            _tokenTransfer(from, nftAddress, fee3);
            INotifyRewardAmount(nftAddress).notifyRewardAmount(fee3);
        } else {
            _tokenTransfer(from, BURN_ADDRESS, fee3);
        }
        _tokenTransfer(from, devAddress, fee4);
        _tokenTransfer(from, BURN_ADDRESS, fee5);
    }

    function _tokenTransferFees(
        address from,
        address to,
        uint256 amount
    ) private {
        uint256 fee;
        if (from.code.length + to.code.length == 0) {
            fee = amount * 3 / 100;
            _tokenTransfer(from, BURN_ADDRESS, fee);
        }
        _tokenTransfer(from, to, amount - fee);
    }

    function _tokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        super._tokenTransfer(from, to, amount);
        emit Transfer(from, to, amount);
    }

    function _mint(address account, uint256 amount) internal override {
        require(account != address(0), "ERC20: mint to the zero address");
        require(!miningStarted, "already start mining");

        super._mint(account, amount);

        emit Transfer(address(0), account, amount);

        miningStarted = true;
        start();
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(currentAllowance >= amount, "ERC20: insufficient allowance");
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function updateShare(address shareholder) private {
        if (_shareExclude[shareholder]) return;
        if (shareholder.code.length > 0) return;

        if(rtc.shared(shareholder)){
            if(balanceOf(shareholder) < totalSupply() / 1000) rtc.removeShareholder(shareholder);
            return;
        }

        if(balanceOf(shareholder) < totalSupply() / 1000) return;

        rtc.addShareholder(shareholder);
    }
}
"
